    // Time varying ring color
    vec3 ringcolor = vec3(abs(sin(iTime/12.+0.9))+0.1, abs(cos(iTime/2.+0.5)), abs(fract(iTime/3.)-0.5)+0.2);//vec3(0.42, 0., 0.);
    //a for loop
    for(float i = 0.0 ; i<num ; i++){
        //circle shifed based on random offet based on index of for loop
        x = rnd(vec2(i, i+4904.));
        y = rnd(vec2(i, i+234.));
        //radius changed based on for loop index

        r = rnd(vec2(i, i+10.))/2.;
    	uv = uv*rot(iTime/32.);
        //shift and separationg for each circle
        vec2 st=uv+(vec2(x, y)*th-th/2.);

        tx1 = vec3(texture(iChannel1, st/2.)).x+0.3;
    	//shadow circle
        col = mix(col, vec3(0.0), S(st, r));
       //rim lighting circle
