const int ITER = 20;
const float PI = 3.14159265;

mat3 m = mat3( 0.00,  0.80,  0.60,
              -0.80,  0.36, -0.48,
              -0.60, -0.48,  0.64 );

mat2 m2 = mat2( 0.80,  0.60, -0.60,  0.9 );


vec2 r2(vec2 v, float a) 
{
	mat2 m = mat2 (
		cos(a), -sin(a),
		sin(a), cos(a)
		);
	return m*v;
}

float ss(float mi,float ma,float a)
{ // sineSignal (min,max,alpha)
	return (sin(a)+1.)*(ma-mi)*0.5+mi;
}


