//---  Noise
//   \


float noise( in vec2 x )
{
	vec2 p = floor(x);
	vec2 f = fract(x);
	vec2 uv = p.xy + f.xy*f.xy*(3.0-2.0*f.xy);
	return texture( iChannel0, (uv+118.4)/256.0, -100.0 ).x;
}

float noise( in vec3 x )
{
	vec3 p = floor(x);
	vec3 f = fract(x);
	f = f*f*(3.0-2.0*f);

	vec2 uv = (p.xy+vec2(37.0,17.0)*p.z) + f.xy;
	vec2 rg = textureLod( iChannel0, (uv+ 0.5)/256.0, 0.0 ).yx;
	return mix( rg.x, rg.y, f.z );
}

float fbm( vec2 p )
{
	p*=3.;
	float f = 0.0;
    f += 0.25000*noise( p ); p = m2*p*0.02;
    f += 0.12500*noise( p ); p = m2*p*0.13;
    f += 0.06250*noise( p ); p = m2*p*0.01;
    f += 0.03125*noise( p ); p = m2*p*0.04;
    f += 0.01500*noise( p );
    return f/0.38375;
}

float fbm( vec3 p )
{
    float f;
    f = 0.5000*noise( p ); p = m*p*2.02;
    f += 0.2500*noise( p ); p = m*p*2.03;
    f += 0.1250*noise( p ); p = m*p*2.01;
    f += 0.0625*noise( p );
    return f;
}

//    /
// ---  Field
//    \

float distanceField(in vec3 p) 
{
  	float s = 8.; // spacing
  	float d = cos(p.x*0.05);
  	float rs = 0.25 * iTime+ d; // rotating speed
  	vec2 pos; // yz

  	float a = rs+d;  
  	pos = s*vec2( sin(a) , cos(a));

  	float r = 2.5;
  	float res;

  	// a simple cylinder would go like this :  length(p.yz+pos)-r  
  	// here is 4 cylinders with displacements :

  	float ssf =  ss(0.,1.,p.y*2.) * sin(p.x+iTime);
  	float w = fbm(p)*2.;

  	res = 
		( length(p.yz+pos+w*0.3*abs(p.x/10.)+sin(p.x/2.+iTime))-r ) - ssf;

  	a = rs+PI*1.0+d;
  	pos = s*vec2(sin(a),cos(a));
  	float res2 =
	  	( length(p.yz+pos+w*0.3*abs(p.x/10.)+sin(p.x/2.+iTime))-r ) - ssf;

	a = rs+PI*0.50+d;
  	pos = s*vec2(sin(a),cos(a));
  	float res3 =
		( length(p.yz+pos+w*0.3*abs(p.x/10.)+sin(p.x/2.+iTime))-r ) - ssf;

  	a = rs+PI*1.50+d;
  	pos = s*vec2(sin(a),cos(a));
  	float res4 =
		( length(p.yz+pos+w*0.3*abs(p.x/10.)+sin(p.x/2.+iTime))-r ) - ssf;

  	res = min(res,res4);
  	res = min(res,res3);
  	res = min(res,res2);
  	return res;
}

vec3 getColor(in vec3 p)
{
	vec3 res = vec3(1.,0.2,0.) * 2.;
  	float m = mod(sin(p.x),2.);
  	float n = fbm(p);
  	float g = fbm(vec3(p.x*iTime*2000.,1.,1.))*2.;
  	res = res*n*m*(g*2.+1.);
  	return res;
}

