// ---  Main
//	  \

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
  	vec3 bgColor = vec3 (0.1,0.0,0.0);

  	vec2 vPos=-1.0+2.0*fragCoord.xy/iResolution.xy;
  	vec3 finalColor = vec3 (0.0);  

  	//Camera animation
  	vec3 vuv=vec3(0,1,0);//Change camere up vector here
  	float s0 = ss(-15.,+15.,iTime*0.1);

  	vec3 prp=vec3(s0,0.,ss(3.,13.,iTime*0.13)); //Change camera path position here
  	vec3 vrp=vec3(sin(iTime*0.002)*2.,cos(iTime)*2.,0); //Change camere view here

  	//Camera setup
  	vec3 vpn=normalize(vrp-prp);
  	vec3 u=normalize(cross(vuv,vpn));
  	vec3 v=cross(vpn,u);
  	vec3 vcv=(prp+vpn);
  	vec3 scrCoord=vcv+vPos.x*u*iResolution.x/iResolution.y+vPos.y*v;
  	vec3 scp=normalize(scrCoord-prp);

  	//Raymarching
  	const vec3 e=vec3(0.1,0,0);
  	const float maxd=30.0; //Max depth

  	float s= 0.1;
  	vec3 c,p,n;

  	float f=1.0;
  	for(int i=0;i<  ITER ; i++)
    {
    	if (abs(s)<.01||f>maxd) break;
    	f+=s;
    	p=prp+scp*f;
    	s=distanceField(p);
  	}

  	if (f<maxd)
    {
    	c=getColor(p);	// cylinder color
    	n=normalize(
      	vec3(s-distanceField(p-e.xyy),
        	s-distanceField(p-e.yxy),
        	s-distanceField(p-e.yyx)));
    	float b=dot(n,normalize(prp-p));

    	vec3 finalColor = vec3( b*c+pow(b,8.0))*(1.0-f*.02) ;  //simple phong LightPosition=CameraPosition

		finalColor*=0.44;
    	fragColor = vec4( finalColor, 1.);
  	} else 
    { // background color
    	bgColor = 
	  	vec3(0.7,0.2,0.1);
	  	float n = fbm(p);
      	bgColor = vec3(n)*bgColor*f/60.;
	  	fragColor=vec4(bgColor,1.);
  	}
}

