               ));
           //)));
}

vec3 sceneNormal(vec3 p) {
    return normalize(vec3(
        scene(p + NDELTAX) - scene(p - NDELTAX),
        scene(p + NDELTAY) - scene(p - NDELTAY),
        scene(p + NDELTAZ) - scene(p - NDELTAZ)
	));
}


