vec4 map(vec3 p)
{
    float d = MAX_DIST;
    float qq = 4., pp = 3.;
    vec3 col;
    for(float j = -a/2.; j < a/2.; j++)
        for(float k = -a; k < a; k++)
        {            
            vec3 point = vec3(qq*(j), cos(iTime/2.)*sin(j*cos(iTime)-k*sin(iTime)), (pp*(k+a)));
        #ifdef rotate_space
            point.z -= a*pp;
            point.xz *= rot(iTime * 5.);
            point.z += a*pp;
        #endif
            //vec3 point = vec3(qq*(j-b), 1.+.8*cos(iTime/2.)*sin(j*cos(iTime)-k*sin(iTime)), (pp*k-b));
            float newD = sphere(p, point , .25+sin(iTime*2.+j+k)*.17);
            if(newD < d)
            {
                d = newD;
                col = vec3(k, j, j*k);
            }
        }

    return vec4(col+2.5, d);//min(p.y + 1., d);
}

vec3 deriv(vec3 p)
{
    return normalize(vec3(
        map(p + vec3(EPS, 0, 0)).w - map(p - vec3(EPS, 0, 0)).w,
        map(p + vec3(0, EPS, 0)).w - map(p - vec3(0, EPS, 0)).w,
        map(p + vec3(0, 0, EPS)).w - map(p - vec3(0, 0, EPS)).w
        ));
}

vec4 march(vec3 ro, vec3 rd)
{

    vec4 d=vec4(0.);
    float dv;
    for(int i = 0; i < 250; i++)
    {
        vec3 newRay = ro + dv * rd;
        d = map(newRay);
        if(d.w < EPS || dv > MAX_DIST)
            break;
        dv += d.w * .7;
    }

    return vec4(d.xyz, dv);
}

