float points(vec2 uv, int count, float delta)
{
    float r = 0.;

    vec2 puv = vec2(length(uv), atan(uv.y, uv.x));

    puv.x = SIN(puv.x * 3.);

    for(int i = 0; i < count; i++)
    {

        vec2 pos = rot(vec2(1.,0.), TAU*float(i)/float(count) );

        float dist =  .8;// + .6*sin(iTime);
        // uv distortion proportional to its length and offset
        float uvDist;
        uvDist = iTime*.5;
        uvDist += (sin(puv.x*10.))*pow( puv.x, 2.);
        //deuvDist += puv.y;
        vec2 uv0;
        uv0 = rot(uv, uvDist);

        uv0 = puv.x*vec2(cos(puv.y+uvDist), sin(puv.y+uvDist));

        // brutal distortion
        uv0 *= mix(vec2(1.), (1. - dist*pos), SIN(iTime));
        //delta *= 1.*sin(4.*puv.y);
        r += 1.-smoothstep(.0, .42, length(uv0 - delta*pos));
    }
    return r;
}

