const vec2 z = vec2(4);
const float complexity =4.;
const float density = .4; // 0-1

const float PI = atan(1.)*4.;

vec4 hash42(vec2 p)
{
    p+=1e2;
	vec4 p4 = fract(vec4(p.xyxy) * vec4(.1031, .1030, .0973, .1099));
    p4 += dot(p4, p4.wzxy+33.33);
    return fract((p4.xxyz+p4.yzzw)*p4.zywx);
}

