// the initial mandelbrot shape is preserved
//
// the mapping of each input frequency results in slight variations
// 
// example coordinates are entered uncomment them to use them, but make sure commenting all afterwards as well
// 
// 

const float PI=3.14159265359;
const int maxIter=1215;
const float maxIterFloat=float(maxIter);

const int startIteration=20;
const int endIteration=120;

// cost intensive rotate, use it for rotating view for the downside bulbs
vec2 rotate(vec2 v, float a) {
	float s = sin(a);
	float c = cos(a);
	mat2 m = mat2(c, -s, s, c);
	return m*v;
} 

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 col = vec3(0.0);

    // default
    vec2 click=vec2(.25,.15);
    // right above tip (uncomment only this to zoom this area)
    // click=vec2(.25,.15);
    // left bulb (uncomment only this to zoom this area)
    // click=vec2(-1.35,.0);
    // top small bulb (uncomment only this to zoom this area)
    click=vec2(-0.1,0.8);
    // top small left bulb (uncomment only this to zoom this area)
    //  click=vec2(-0.5,0.6);
    // top small right bulb (uncomment only this to zoom this area)
    // click=vec2(0.3,0.5);
    // bottom small right bulb (uncomment only this to zoom this area)
    // click=vec2(0.25,-0.55);
    // bottom small left bulb (uncomment only this to zoom this area)
    // click=vec2(-0.5,-0.6);


    vec2 mouse=(-iMouse.xy/iResolution.xy)*2.0f-1.0f;

        vec2 p = (-iResolution.xy + 2.0*fragCoord.xy)/iResolution.y; 
        p=rotate(p,0.);
        float time = iTime; 
    	time=1.;
        float zoo = 0.82 + 0.28*cos(.07*time);
        zoo=iMouse.x<0.0001f?0.15:iMouse.y/iResolution.y;   
        vec2 c =  click+p.xy*zoo;// '+ xy*zoo;

        const float B = 256.0;
        float l = 0.0;
	    vec2 z  = vec2(0.0);

        for( int i=0; i<maxIter; i++ )
        {



            // z = z*z + c		
    		z = vec2( z.x*z.x - z.y*z.y, 2.0*z.x*z.y ) + c;


            if(i>startIteration && i<endIteration){
               /*
				lets sample from the input fourier values (todo: get sampling rate/subdivision right)

				*/  
                float iPos= float(i-startIteration)/float(endIteration-startIteration); 
				col.x=sin(iPos*PI*100.+iTime )*.1;
             	col.y=cos(iPos*PI*10.+iTime )*.1;

                /*

					since sample rate is 1-dimensional, but we have a 2dimensional complex value,
					lets use a timed arrrow rotating as direction ...
				*/
                float value=texture(iChannel0,vec2(float(i)/float(maxIter))).r;


                // this just offsets from audio per iteration - rather linear
                 z.x+= value*value*.15  ;
                 z.y-=  value*value*.15   ;

                // use this to make rotational direction 
                 //z.x+= col.x*value*value*.15  ;
                 //z.y-= col.x*value*value*.15   ;
            }
                if( dot(z,z)>(B*B) ) break;

    		l += 1.0;
        }

    	// ------------------------------------------------------
        // smooth interation count
    	//float sl = l - log(log(length(z))/log(B))/log(2.0);


        col += 0.5 + 0.5*cos( 3.0 + l*0.15 + vec3(0.0,0.6,1.0));


    fragColor = vec4( col, 1.0 );
}

vec3 eye = vec3(0.0, 0.0, 10.0);
vec3 front = vec3(0.0, 0.0, -1.0);
vec3 up = vec3(0.0, 1.0, 0.0);
float fov = radians(45.0);

float minDist = 1e-3;
int maxSteps = 0xFF;


float distEstimate(vec3 p)
{
    vec2 t = vec2(0.5, 0.10);
    vec2 q = vec2(length(p.xz)-t.x,p.y);
  	return length(q)-t.y;
}

vec3 getRay(vec2 uv, float aspect)
{
    uv.y = (1.0 - uv.y);
    vec3 right = cross(up, front);
    vec2 coeffs = tan(-fov/2.0 + uv * fov);
    coeffs.y *= aspect;

    return normalize(front + right*coeffs.x + up*coeffs.y);
}

vec3 rayMarch(vec2 uv, float aspect)
{
    vec3 dir = getRay(uv, aspect);

    int steps = 0;
    float total = 0.0;
    float glowDist = 1e9;
    while (steps++ < maxSteps)
    {
        vec3 pos = eye + total * dir;
        float dist = distEstimate(pos);
        glowDist = min(glowDist, dist);
        total += dist;
        if (dist < minDist) break;
    }
    if (distEstimate(eye + total * dir) < minDist)
    {

        float col = (1.0 - float(steps)/float(maxSteps));
        return vec3(col, 0.0, 0.0);
    }
    else
    {
        return vec3(max((0.05 - glowDist)*20.0, 0.0));
    }
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    eye.y = sin(iTime/5.0);
    eye.x = cos(iTime/5.0);

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    float aspect = iResolution.y / iResolution.x;    

    // Output to screen
    fragColor = vec4(rayMarch(uv, aspect), 1.0);
}

#define MAX_MARCH 640
#define MAX_DST 100.
#define MIN_DST .01
#define AMBIENT_DST .1

#define OBJ_PLANE .5
#define OBJ_SPHERE .51

#define OBJECTS 8

mat4 obj[OBJECTS];

// globals
vec3 p;
int o;
float deltaDst;

vec3 getNor(int o, vec3 p) {
    if(obj[o][3][3] < OBJ_SPHERE) {
        return obj[o][2].xyz;
    }else{
    	return normalize(p - obj[o][0].xyz);
    }
}

float getDistance(vec3 p) {
    float min = 255.;
    float res;
    for(int c = 0; c < OBJECTS; c++) {

        if(obj[c][3][3] < OBJ_SPHERE) {
            res = abs(p.y - obj[c][0][1]); // plane
        }else{
            res = length(p - obj[c][0].xyz) - obj[c][0].w; // sphere
        }

        if(min > res) {
            deltaDst = min - res;
            min = res;
            o = c;
        }

    }
    return min;
}

float rayMarch(vec3 start, vec3 dir) {
    float ret = 0.;
    int i;
    float dst;

    for(i = 0; i < MAX_MARCH; i++) {
        p = start + dir * ret;
        dst = getDistance(p);
        ret += dst;
        if(ret > MAX_DST || dst < MIN_DST) {
           break; 
        }
    }
    return ret;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    // Normalized pixel coordinates (from -1 to 1)
    vec2 uv = (fragCoord - .5 * iResolution.xy) / iResolution.y;

    // light
    vec3 light = vec3(sin(iTime) * 10., 6., cos(iTime) * 10. + 10.);
    vec3 light2 = vec3(sin(iTime + 3.14) * 10., 6., cos(iTime + 3.14) * 10. + 10.);

    // camera
    vec3 cam = vec3(0., 1., 0.);
    vec3 dir = normalize(vec3(uv.x, uv.y, 1.));

    // objects
    obj[0][0] = vec4(0., .2, 0., 0.); // infinite plane at zero Y
    obj[0][1] = vec4(.5, .5, .5, 1.);
    obj[0][2] = vec4(0., 1., 0., 0.);
    obj[0][3][3] = OBJ_PLANE;

    obj[1][0] = vec4(0., 1., 6., 1.); // red sph
    obj[1][1] = vec4(1., 0., 0., 1.);
    obj[1][3][3] = OBJ_SPHERE;

    obj[2][0] = vec4(1.5, 0.8, 6., 0.8); // blue sph
    obj[2][1] = vec4(0., 0., 1., 1.);
    obj[2][3][3] = OBJ_SPHERE;

    obj[3][0] = vec4(-1.5, 0.8, 6., 0.8); // green sph
    obj[3][1] = vec4(0., 1., 0., 1.);
    obj[3][3][3] = OBJ_SPHERE;

    obj[4][0] = vec4(0., .4, 2., .4); // yellow sph
    obj[4][1] = vec4(1., 1., 0., 1.);
    obj[4][3][3] = OBJ_SPHERE;

    obj[5][0] = vec4(1.5, 0.6, 3., 0.6); // magenta sph
    obj[5][1] = vec4(1., 0., 1., 1.);
    obj[5][3][3] = OBJ_SPHERE;

    obj[6][0] = vec4(-1.5, 0.6, 3., 0.6); // cyan sph
    obj[6][1] = vec4(0., 1., 1., 1.);
    obj[6][3][3] = OBJ_SPHERE;

    obj[7][0] = vec4(0., 7., 0., 0.); // infinite plane at 7 Y
    obj[7][1] = vec4(.5, 1., .5, 1.);
    obj[7][2] = vec4(0., -1., 0., 0.);
    obj[7][3][3] = OBJ_PLANE;

    for(int i = 0; i < OBJECTS; i++) {
        //obj[i][0].x *= cos(iTime);
        //obj[i][0].z *= sin(iTime);
    }

    float dst = rayMarch(cam, dir);

    dir = normalize(light - p);
    vec3 nor = getNor(o, p);

    vec3 dir2 = normalize(light2 - p);

    float colMul = clamp(dot(dir, nor), 0.3, 0.5);
    float colMul2 = clamp(dot(dir2, nor), 0.3, 0.5);
    float ambient = 1.;

    if(deltaDst < AMBIENT_DST) {
        ambient = deltaDst / AMBIENT_DST;
    }

    vec4 c = obj[o][1];
    vec3 collisionP = p;
    int collisionO = o;

    float shadow = rayMarch(p + (nor * MIN_DST * 2.), dir);

    if(shadow < length(light - p)) {
        colMul *= .9;
    }


    shadow = rayMarch(p + (nor * MIN_DST * 2.), dir2);

    if(shadow < length(light2 - p)) {
        colMul *= .9;
    }

    nor = normalize(reflect(collisionP - cam, nor));
    if(rayMarch(collisionP + (nor * MIN_DST * 2.), nor) < 10.) {
    	c = obj[o][1] + (obj[collisionO][1] * .5);
    }

    vec4 c2 = c;
    c2.rgb *= colMul2;
    c.rgb *= colMul;

    fragColor = (c + c2) * ambient;
}

const float PI = 3.14159265;
//use for differential
const float EPS = 0.001;

//common function
mat2 genRot(float val){
	return mat2(cos(val), -sin(val),
               sin(val), cos(val));
}

float rand (float x){
    x = fract(sin(x*416.31434));
    x = fract(sin(x*234.41523));
    x = fract(sin(x*235.51424));
	return x;
}

float smin( float a, float b, float k )
{
    float res = exp2( -k*a ) + exp2( -k*b );
    return -log2( res )/k;
}

vec3 pmod(vec3 p,float c){
    float tmp = PI * 2. / c;
	float l = length(p.xy);
    float theta = atan(p.y/p.x);
    theta = mod(theta,PI * 2. / c);
    return vec3(l * cos(theta), l * sin(theta),p.z);

}


//Common SDF

float sphere(vec3 p,vec3 o,float r){
	return length(p - o) - r;
}

float cylinder(vec2 p,vec2 o,float r){
	return length(p - o) - r;
}

float cube(vec3 p,vec3 o,vec3 s){
    float x = abs(p.x - o.x) - s.x/2.;
    float y = abs(p.y - o.y) - s.y/2.;
    float z = abs(p.z - o.z) - s.z/2.;
    return max(x,max(y,z));
}

float line(vec3 p,vec3 q1,vec3 q2,float r){
	float t = clamp(
    	dot(q2 - p,q2 -q1)/dot(q2-q1,q2-q1),
    	0.,
        1.
    );
    vec3 q = t * q1 + (1. - t) * q2;
    return length(q - p) - r;
}


//Gathering SDF
float menger(vec3 p,vec3 o,float l){
    p = p - o;
    float result = max(abs(p.x),max(abs(p.y),abs(p.z)))- l/2.0;
    for(int i = 0; i < 4; i++){
        p *= 3.;
    	float a = max(abs(p.x),abs(p.y)) - l/2.;
        float b = max(abs(p.y),abs(p.z)) - l/2.;
    	float c = max(abs(p.z),abs(p.x)) - l/2.;
        float hole = min(a,min(b,c));
        result = max(result,-hole);
        p = fract(p/l) * l;
        p -= l;


    }
	return result;
}
float map(vec3 p){
	float h = -3. + cos(p.x/2.) * 0.5 + cos(p.z/2.) * 0.5;
    h += sin(p.x /8.) * 3.;
    h -= sin(p.z /8.) * 3.;
    float ground = p.y - h;
    vec3 q = p;
    float towerHeight = q.y - floor(h) - 10.;
    q.x = fract(q.x /8. + 0.5) * 8. - 4.;
    q.z = fract(q.z /6. + 0.5) * 6. - 3.;
    q.y = fract(q.y /3.0 + 0.5) * 3.0 - 1.5;
    float me = menger(q,vec3(0.),3.0);
    me = max(me,towerHeight);
    return min(ground,me);
}

//Getting Normal

vec3 getNormal(vec3 p) {
    return normalize(vec3(
        map(p + vec3(EPS, 0.0, 0.0)) - map(p + vec3(-EPS,  0.0,  0.0)),
        map(p + vec3(0.0, EPS, 0.0)) - map(p + vec3( 0.0, -EPS,  0.0)),
        map(p + vec3(0.0, 0.0, EPS)) - map(p + vec3( 0.0,  0.0, -EPS))
    ));
}

//Setting CameraPos
vec3 Camera(float t){
    float time = iTime * 4.0;
	return vec3(4.,1.-sin(-5./8. + time/8.) * 3.,-5.0 + time);
}

//Setting Ray
vec3 Ray(vec2 uv, float z){
	vec3 ray = normalize(vec3(uv,z));
    ray.xy *= genRot(PI / 8.);
    ray.xz *= genRot(PI / 8.);
    return ray;
}

//Tracing Ray

vec4 trace (vec3 o, vec3 r){
	float t = 0.0;
    vec3 p = vec3(0.0,0.0,0.0);

    for(int i = 0; i < 256; ++i){
        p = o + r * t;
        float d = map(p);
        t += d * 0.25;
    }
    return vec4(getNormal(p),t);
}

//Making color
vec3 getColor(vec3 o,vec3 r,vec4 data){
    float t = data.w;
    float fog = 1.0 / (1.0 + t * t * 0.005);
    float a = dot(data.xyz,r);
    vec3 p = o + r * t;
    vec3 fc = min(fract(p.x),fract(p.z))<0.03 || abs(p.x - 4.) < 1. || fract(p.z / 6. - 0.4) < 0.2 ? vec3(0.95) : vec3(0.05);
    fc = mix(fc,vec3(1.),1. + a*1.5);
    fc = mix(vec3(0.95),fc,fog);
    fc = t < 1000. ? fc : vec3(0.95);
    //fc = vec3(fog);
    return fc;
}

//Drawing

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //set canvas
    vec2 uv = fragCoord.xy /iResolution.xy;
	uv = uv * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;

    //set camera and ray
    vec3 r = Ray(uv,1.2);
    vec3 o = Camera(iTime);

    //trace ray
    vec4 data = trace(o,r);
    vec3 fc = getColor(o,r,data) ;
	//fc = vec3(fog);
    // Output to screen
    fragColor = vec4(fc,1.0);
}

