vec3 palette(float x) {
  float wave = sin(3. * time) * 0.5 + 0.5;
  vec3 p = vec3(1, 1, 1);
  vec3 q = vec3(.3, .6, 1);
  vec3 r = vec3(.3, .6, .3);
