  return p + q * sin(2. * PI * (x * r + s));
}

mat2 rot2d(float a) {
  float c = cos(a), s = sin(a);

  return mat2(c, s, -s, c);
}

vec3 kifs(vec3 p, float s, float tf) {
  float t = tf * time;

  for (float i = 0.; i < 6.; i++) {
    p.xy *= rot2d(t + i);
    p.xz *= rot2d(t * 0.6 - i);
    p = abs(p);
    p -= s;
    s *= 0.8 + .1 * sin(2.8 * time);
  }

  return p;
}

float map(vec3 p) {
  float d = 10000.;


  p.xy *= rot2d(sin(.3 * time) * p.z / 40.);
  vec3 rep = vec3(20);
  p = mod(p, rep) - 0.5 * rep;

  vec3 pc = kifs(p, 1., .3);
  vec3 pe = kifs(p, 1.05, .1);

  d = min(d, length(pc) - 1.);
  d = max(d, -(length(pe) - .8));

  return d;
}

