const float gridSize = 3.0;
const float penSize = 15.0;

vec4 ComputeWaveGradientRGB(float t, vec4 bias, vec4 scale, vec4 freq, vec4 phase)
{
	vec4 rgb = bias + scale * cos(6.28 * (freq * t + phase));
	return vec4(clamp(rgb.xyz,0.0,1.0), 1.0);
}


vec4 ComputeGridPattern(
	vec2 p,
	float pen, 
	float scale,
	float maxRes
	)
{
	p *= scale;
	pen *= scale / maxRes;
	vec2 cell = floor(p);
	vec2 cellCoords = p - cell;

	vec2 d = abs(cellCoords) - vec2(1, 1);
	float edgeDistance = length(max(d, vec2(0, 0))) + min(max(d.x, d.y), 0.0);
	float annularDistance = abs(edgeDistance) - pen / 2.0;

	float intensity = 1.0 - step(0.0, annularDistance);
	return vec4(intensity, annularDistance, cellCoords.x, cellCoords.y);

}




void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv.x *= float(iResolution.x) / float(iResolution.y);

    float maxRes = max(float(iResolution.x), float(iResolution.y));

    float tm = iTime * 0.5;


    vec4 pattern = ComputeGridPattern(uv + (iTime * 0.01), penSize, gridSize + (4.0 * (sin(iTime * .30)+1.0)), maxRes);

    vec4 bias = vec4(0.350,0.906,0.689,1.0);
    vec4 scale = vec4(0.772,0.114,0.263,1.0);
    vec4 freq = vec4(0.077,0.368,1.016,1.0);
    vec4 phase = vec4(3.859,3.252,5.857,1.0);


    vec4 baseColor = ComputeWaveGradientRGB(sin(pattern.x + tm),bias,scale, freq, phase);
	vec4 accent0 = ComputeWaveGradientRGB(cos(pattern.y + tm),bias,scale, freq, phase);
    vec4 accent1 = ComputeWaveGradientRGB(fract(pattern.z*tm * tan(tm)),bias,scale, freq, phase);
    vec4 accent2 = ComputeWaveGradientRGB(fract(pattern.w*tm* sin(tm)),bias,scale, freq, phase);


    vec4 finalColor = (baseColor * accent0 * accent1 * accent2);
    fragColor = finalColor;
}

