vec3 rotz(in vec3 p, float a) {
	return vec3(cos(a) * p.x + sin(a) * p.y,
                cos(a) * p.y - sin(a) * p.x,
                p.z);
}

vec3 roteuler(in vec3 p, in vec3 euler) {
 	return roty(rotx(rotz(p, euler.z), euler.y), euler.z);   
}

vec4 minp(in vec4 a, in vec4 b) {
	return a.w < b.w ? a : b;   
}

bool checkerboard(in vec2 p) {
	float x = mod(p.x, 2.0);
    if (x < 0.0) x += 2.0;
    float y = mod(p.y, 2.0);
    if (y < 0.0) y += 2.0;
    return (x < 1.0) == (y < 1.0);
}

vec4 map(in vec3 p) { 
    vec4 sphere = vec4(0, 0, 0, 1000);
    for (int i = 0; i < 10; i++) {
        float a = float(i) * 2.0*pi / 10.0;
        float a2 = a + iTime * 0.4;
        vec3 center = vec3(sin(a2), 0.0, cos(a2)) * 5.0 + vec3(0, 2.0 + sin(float(i)), -10);
        vec3 col = vec3(sin(a), sin(a + 2.2), sin(a + 4.4)) * 0.75 + vec3(0.5);
     	sphere = minp(sphere, vec4(col, mapsphere(p, center, 1.0)));
    }

    const vec3 boxCol = vec3(0.6);    
    vec3 ccenter = vec3(0, 4.0 + sin(iTime) * 1.0,-10);
    vec3 boxp = ccenter + roteuler(p - ccenter, vec3(iTime * 0.2, iTime * 0.7, iTime * 1.1) * 0.3);
    vec4 box = vec4(boxCol, mapbox(boxp, ccenter, vec3(3, 2, 0.75)));
    box.w = max(box.w, 1.0 - length(boxp.xy - ccenter.xy));
    box.w = max(box.w, 0.5 - length(boxp.yz - ccenter.yz));
    box.w = max(box.w, 0.5 - length(boxp.xz - ccenter.xz));

    const vec3 planeCol1 = vec3(0, 0.5, 0.75);
    const vec3 planeCol2 = vec3(0.7, 0.8, 1);
    vec4 plane = vec4(
        checkerboard(p.xz * 0.5) ? planeCol1 : planeCol2, 
        p.y - -2.0);

    return minp(sphere, minp(box, plane));
}

vec3 getNormal(in vec3 p, float t) {

	// Calculate pixel size at point distance. 
    // This will be the distance of the normal samples from the original point.
    float s = 0.1 / t;

    // Sample relative distance along each axis
    float d = map(p).w;			// Need original distance to compare it to
    vec3 r = vec3(
        map(p + vec3(s,0,0)).w - d,
        map(p + vec3(0,s,0)).w - d,
        map(p + vec3(0,0,s)).w - d);

    return normalize(r);
}

vec3 getScreenRay(in vec2 s) {
    s -= iResolution.xy / 2.0;
    return vec3(s / (screenDist * iResolution.x), -1);
}

vec4 march(in vec3 from, in vec3 delta) {
    float t = tmin;    
    for (int i = 0; i < stepCount && t < tmax; i++) {
     	vec3 p = from + delta * t;
        vec4 d = map(p);
        if (abs(d.w) <= 0.0005 * t)
            return vec4(d.xyz, t);
        t += d.w;
    }

    return vec4(vec3(0),-1);    
}

float shadow(in vec3 from, in vec3 delta, float k) {
    float res = 1.0;
    float t = tmin;    
    for (int i = 0; i < stepCount && t < tmax; i++) {
     	vec3 p = from + delta * t;
        float d = map(p).w;
        if (d <= 0.0)
            return 0.0;
        res = min(res, k*d/t);
        t += d;
    }

    return res;
}

// Crude lighting equation
float lighting(in vec3 e, in vec3 n, float diffuse, float specular, float shiny) {

    // Diffuse term
    float d = dot(n, -light) * diffuse;

    // Specular term
    vec3 h = -(normalize(e) + light) / 2.0;
    float s = pow(dot(n, h), shiny) * specular;

    return max(d + s, 0.0);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord )
{
    const vec3 fogCol = vec3(0.0, 0.0, 0.2);
    const float r = 0.6;

    float ang = iTime * 0.03;
    vec3 from = vec3(0, 3, -10) + roty(vec3(0, 0, 12), ang);
    vec3 dir = normalize(roty(rotx(getScreenRay(fragCoord), 0.1), ang));

    vec3 sumCol = vec3(0);
    float sumf = 1.0;
    for (int bounce = 0; bounce < 4; bounce++) {    
        vec4 d = march(from, dir);

        if (d.w < 0.0)
            break;

		// Position and normal
        vec3 p = from + dir * d.w;
        vec3 n = getNormal(p, d.w);

        // Lighting
        float l = lighting(dir, n, 1.5, 2.0, 10.0);
        if (l > 0.0) {
            // Shadow ray
            float s = shadow(p, -light, 16.0);
            l *= s;
        }

        vec3 col = d.xyz * min(l + 0.2, 1.0);
        col *= r;

        // Fog
        float fog = clamp(d.w * d.w / 1200.0, 0.0, 1.0);
        col = mix(col, fogCol, fog);

        // Add to sum
        sumCol += col*sumf;

        // Calculate reflection vector
        from = p;
        dir = normalize(reflect(dir, n));

        sumf *= (1.0 - r) * (1.0 - fog);
    }

    sumCol += fogCol * sumf;

    fragColor = vec4(sumCol, 1.0);
}

#define HASHSCALE1 vec3(.1031)

vec3 hash(vec3 p3)
{
	p3 = fract(p3 * HASHSCALE1);
	p3 += dot(p3, p3.yxz+19.19);
	return fract((p3.xxy + p3.yxx)*p3.zyx);
}

vec3 noise( in vec3 x )
{
	vec3 p = floor(x);
	vec3 f = fract(x);
	f = f*f*(3.0-2.0*f);

	return mix(	mix(mix( hash(p+vec3(0,0,0)), 
						hash(p+vec3(1,0,0)),f.x),
					mix( hash(p+vec3(0,1,0)), 
						hash(p+vec3(1,1,0)),f.x),f.y),
				mix(mix( hash(p+vec3(0,0,1)), 
						hash(p+vec3(1,0,1)),f.x),
					mix( hash(p+vec3(0,1,1)), 
						hash(p+vec3(1,1,1)),f.x),f.y),f.z);
}

const mat3 m3 = mat3( 0.00,  0.80,  0.60,
					-0.80,  0.36, -0.48,
					-0.60, -0.48,  0.64 );
vec3 fbm(in vec3 q)
{
	vec3 f  = 0.5000*noise( q ); q = m3*q*2.01;
	f += 0.2500*noise( q ); q = m3*q*2.02;
	f += 0.1250*noise( q ); q = m3*q*2.03;
	f += 0.0625*noise( q ); q = m3*q*2.04;
#if 1
	f += 0.03125*noise( q ); q = m3*q*2.05; 
	f += 0.015625*noise( q ); q = m3*q*2.06; 
	f += 0.0078125*noise( q ); q = m3*q*2.07; 
	f += 0.00390625*noise( q ); q = m3*q*2.08;  
#endif
	return vec3(f);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col = fbm(float(iTime) + 1000.0 + vec3(fragCoord.x, fragCoord.y, (fragCoord.x + fragCoord.y) * 0.5) * 0.01);

    // Output to screen
    fragColor = vec4(col,1.0);
}

#define PI	3.14159265359
#define PI2	PI * 2.0
#define rot(a) mat2(cos(a),sin(a),-sin(a),cos(a))

float smin(float d1, float d2, float k)
{
    float h = clamp(0.5 + 0.5 * (d2 - d1) / k, 0.0, 1.0);
    return mix(d2, d1, h) - k * h * (1.0 - h);
}

vec2 polarFold(vec2 p,float n)
{
    float h=floor(log2(n));
    float a =PI2*exp2(h)/n;
    for(int i=0;i<int(h)+2;i++)
    {
        vec2 v = vec2(-cos(a),sin(a));  
        p-=2.0*min(0.0,dot(p,v))*v;
        a*=0.5;
    }
    return p;
}

vec2 polarSmoothFold(vec2 p,float n)
{
    float h=floor(log2(n));
    float a =PI2*exp2(h)/n;
    for(int i=0; i<int(h)+2; i++)
    {
        vec2 v = vec2(-cos(a),sin(a));  
 		p-=2.0*smin(0.0,dot(p,v),0.05)*v;
        a*=0.5;
    }
    return p;
}

float map(vec3 p)
{
    p.xy *= rot(iTime*0.5);
    p.yz *= rot(iTime*0.3);

    p.xy=polarSmoothFold(p.xy,16.);
    p.y -= 1.2;
    return length(p)-0.3;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (2.0 * fragCoord.xy - iResolution.xy) / iResolution.y;

    vec2 q = polarFold(uv,6.);
    q.y-=iTime*0.8;
    q.y=mod(q.y,0.4)-0.05;
    vec3 col = vec3(0.2,0.5,0.1);
    col = mix(col, vec3(0,0.15,0),step(0.0, q.x * q.y)); 
    col = mix(col, vec3(1,0.5,0)*0.7*dot(uv,uv), smoothstep(0.02, 0.0, abs(q.y)));
    col = mix(col, vec3(0,0.6,1)*0.8*length(uv), smoothstep(0.02, 0.0, abs(q.x)));

 	vec3 ro = vec3(0,0,3);
    vec3 rd = normalize(vec3(uv,-2));
    float d, t = 0.0;
    for(float i = 1.0;i > 0.0;i -= 1.0/30.0)
    {
     	t += d = map(ro+t*rd);
        if(d < 0.001)
        {
            col += i*i;
            break;
        }
    }
	fragColor = vec4(col,1.0);
}

vec2 Center = vec2(0.5,0.5);

float Rad = 1.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec4 FinalColor = vec4( 0.5, 0.5, 1.0, 1.0 );

    float Distance = sqrt((uv.x - Center.x)*(uv.x - Center.x) +
                            (uv.y - Center.y)*(uv.y - Center.y)); 

    FinalColor.xy = (uv - Center)/ Distance *2.0 *3.14159265 * Rad ;

    FinalColor.xy = cos(FinalColor.xy + iTime);

    FinalColor.xy = FinalColor.xy*0.5 + 0.5;


    // Output to screen
    fragColor = FinalColor;
}

