// these seem popular nowdays let's try it with a twist --jz

// how many curly fries do we need?
#define MAX_ITER 12
 void mainImage(out vec4 fragColor,in vec2 fragCoord)
//void main( void )
 {

	//vec2 p = vv_FragNormCoord;
	vec2 p=(2.4*fragCoord.xy-iResolution.xy)/iResolution.xy;
	vec2 i = p;

	float c = 0.0;

	float d = length(i);
	float r = atan(i.x, i.y);
	for (int n = 0; n < MAX_ITER; n++) {
		i = i + vec2( 
				1.0/float((n+1) * MAX_ITER/(n+1)) * d * sin(r * i.y * (pow(float(n), 2.0 - abs(sin(iTime * 0.022))) * float(MAX_ITER)/float(n+1)) + iTime * 1.49),
				1.0/float((n+1) * MAX_ITER/(n+1)) * d * cos(r * i.x * (pow(float(n), 2.0 - abs(cos(iTime * 0.021))) * float(MAX_ITER)/float(n+1)) + iTime * 1.51)
			);

		d = length(i);
	        r = atan(i.x, i.y);
	}

	c = (1.50-d);
	c = pow(c, .52);
	fragColor = vec4( vec3( c*abs(sin(i.x + 1.0)), c*abs(sin(i.y + 4.0)), c*abs(sin(i.x+i.y + 4.0))) * vec3(.6501,1.15,1.5), 1.0 );
}

#define AREA 60

float r1D(float r)
{
    return fract(sin(r*12.43)*519803.43);
}

float rand(vec2 p)
{
  return fract(sin(dot(p,vec2(12.43,98.21)))*519803.43);
}

float map(vec2 v, float s, vec2 z)
{
    if (z.x >= 0. && z.y >= 0.)
    {
        v*=z;
        v*=s;
		return rand(floor(v));
    }
    if (z.x < 0. && z.y >= 0.)
    {
        v*=z;
        v*=s;
		return rand(vec2(ceil(v.x-1.), floor(v.y)));
    }
    if (z.x < 0. && z.y < 0.)
    {
        v*=z;
        v*=s;
		return rand(ceil(v-1.));
    }
    if (z.x >= 0. && z.y < 0.)
    {
        v*=z;
        v*=s;
		return rand(vec2(floor(v.x), ceil(v.y-1.)));
    }
}

float grid2D(vec3 ro, vec3 rd, vec2 st)
{
    vec3 p;
    vec3 u=ro;

    int j = 1;
    for (int i=0;i<AREA;i+=0)
    {
        if (j > AREA)
            break;
        float h=0.;
        if (rd.x != 0.)
        {
            p.x=floor(u.x)+1.;
            h=(p.x-u.x)/rd.x;
            p.y=h*rd.y+u.y;
        }
        else	p.y=floor(u.y)+1.;
        if (p.y>=floor(u.y)+1.)
        {
            p.y=floor(u.y)+1.;
            h=(p.y-u.y)/rd.y;
            p.x=h*rd.x+u.x;
        }
        else{j++;}

        float g=(p.x-u.x)/rd.x;
        p.z=g*rd.z+u.z;
        float t=map(u.xy,1.,sign(st))*abs(sin(iTime)*rand(floor(u.xy))+1.);
        if (u.z<t)
        {
            return length(u-ro);
            break;
        }
        if (p.z<t)
        {
            float z = p.z-t;
            h=z/rd.z;
            float x = h*rd.x;
            h=x/rd.x;
            float y = h*rd.y;
            u = p-vec3(x,y,z);
            return length(u-ro);
        }
        u=p;
    }
    return 0.;
}

vec3 normal(vec3 p, vec2 st)
{
    float d = length(p);
    vec2 e = vec2(.01, 0.);
   	vec3 n = vec3(map(p.xy-e.xy,1.,sign(st))-map(p.xy+e.xy,1.,sign(st)),
                  map(p.xy-e.yx,1.,sign(st))-map(p.xy+e.yx,1.,sign(st)),
                  e.x);
    return normalize(n);
}

#define B vec3(.6,.1,1.)
#define G vec3(.6,1.,.1)

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy*2.-1.;
    uv.x*=iResolution.x/iResolution.y;

    vec2 st=uv;
    vec3 ro=vec3(0.,0.,10.);
   	vec3 rd=normalize(vec3(abs(st),-1.));

    vec3 col=vec3(0.);

    float d = grid2D(ro, rd, st);
    vec3 p = ro+rd*d;
    vec3 n = normal(p,st);
    vec3 l = vec3(cos(iTime),sin(iTime),.25)*10.;
    vec3 ld = normalize(l-p);
    float diff = max(dot(ld,n),0.);
    col += diff*p.z+(p.z+.1)*.1;
    fragColor = vec4(sqrt(col*mix(B,G,p.z*1.5)), 0.);
}

float noise(vec3 p)
{
    p = floor(p);
    p = fract(p*vec3(283.343, 251.691, 634.127));
    p += dot(p, p+23.453);
    return fract(p.x*p.y);
}
float noiseblend(vec3 p)
{
    vec2 off = vec2(1.,0.);
    return mix(	mix(	mix(noise(p), noise(p+off.xyy),fract(p.x)),
           				mix(noise(p+off.yxy), noise(p+off.xxy),fract(p.x)),fract(p.y)),
               	mix(	mix(noise(p+off.yyx), noise(p+off.xyx),fract(p.x)),
           				mix(noise(p+off.yxx), noise(p+off.xxx),fract(p.x)),fract(p.y))
               ,fract(p.z));

}
float turb(vec3 p)
{
    p *=4.;
    vec3 dp = vec3(p.xy,p.z+iTime*.25);
    float inc = 0.75;
    float div = 1.75;
    vec3 octs = dp*2.13;
    float n = noiseblend(dp);
    for(float i=0.; i<5.; i++)
    {
        float ns = noiseblend(octs);
        n += inc*ns;

        octs *=2.+(vec3(ns,noiseblend(octs+vec3(n,0.,0.1)),0.));
        inc *=.5*n;
        div +=inc;
    }
    float v = n/div;
    v *= 1.-max(0.,1.2-length(vec3(.5,0.,6.)-p));
    return v;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from -1 to 1)
    vec2 uv = (2.*fragCoord-iResolution.xy)/iResolution.y;
    vec2 mouse = 1.+(2.*iMouse.xy-iResolution.xy)/iResolution.y;

    uv *=1.+.2*length(uv);
    float uvlen = 1.-length(uv);
    float tt = .5*iTime+(0.3-.3*uvlen*uvlen);
    vec2 rot = vec2(sin(tt),cos(tt));
    uv = vec2(uv.x*rot.x+uv.y*rot.y, uv.x*rot.y-uv.y*rot.x);
    mouse = vec2(mouse.x*rot.x+mouse.y*rot.y, mouse.x*rot.y-mouse.y*rot.x);
    vec3 ro = vec3(mouse,-1.);
    vec3 rd = normalize(vec3(uv,5.)-ro);



    vec3 col = vec3(0);
    rd.z+=tt*.01;
    float nv = turb(rd);
    for(float i=0.; i<1.; i+=.2)
    {
        nv *=.5;
        nv = turb(vec3(rd.xy,rd.z+i));
    	col += (1.5-i)*vec3(nv,nv*nv*(3.-2.*nv), nv*nv);
    }
    col /=5.;


    fragColor = vec4(col,1.0);
}

float PI = 3.14159265;
vec2 path(float t){
	float x = sin(t) + cos(t / 2.) + sin(t / 4.);
    float y = sin(t) + sin(t/2.) + cos(t/4.);
    return vec2(x,y)/4.;
}
mat2 genRot(float val){
    return mat2(cos(val),-sin(val),sin(val),cos(val));
}
vec3 modCell(vec3 p,vec3 c){
	p.x = (fract(p.x / c.x + 0.5) - 0.5) * c.x;
    p.y = (fract(p.y / c.y + 0.5) - 0.5) * c.y;
    p.z = (fract(p.z / c.z + 0.5) - 0.5) * c.z;
    return p;
}

vec2 pmod(vec2 p,float c){
    p.xy *= genRot(PI/c);
	float at = atan(p.y/p.x);
    float r = length(p);
    at = mod(at,PI * 2. / c);
    vec2 re = vec2(cos(at) * r,sin(at) * r);
    re.xy *= genRot(-PI/c);
    return re;
}
float roomsize = 7.5;
vec2 map(vec3 p){
    p.xy += path(p.z);
    //p = modCell(p,vec3(roomsize));
    p = modCell(p,vec3(vec2(roomsize),2.0));
    p.xy *= genRot(iTime);
    p.xy = pmod(p.xy,12.);
	vec2 sp = vec2(length(p - vec3(3.0,0.,0.)) - 0.5,1.0);
    vec3 q = p;
    q.xz -= vec2(3.0,0.);
    q.xy *= genRot(iTime);
    float cyl = length(q.xz) - 0.25;
    cyl = max(cyl,-length(p.xy) + 1.);
    cyl = max(cyl,length(p.xy) - 3.00);
    cyl = min(cyl,length(p.xy - vec2(3.,0.)) - 0.1);
    return vec2(cyl,0.0);
}
vec2 trace(vec3 r,vec3 o){
    float t = 0.0;
    float id = -1.0;
    for(int i = 0; i < 128; i++){
    	vec3 p = o + r * t;
        vec2 d = map(p);
        t += d.x * 0.5;
        id = d.y;
    }
    return vec2(t,id);
}

vec3 ray (vec2 uv,float z){
 vec3 r = normalize(vec3(uv,z));
    r.xz *= genRot(-PI/6.);
    r.yz *= genRot(-PI / 8.);
    r.xy *= genRot(iTime /1.);
    return r;
}
vec3 cam (float t){
	vec3 c = vec3(roomsize/2.,roomsize/2.,-6.5 + iTime * 8.0);
    return c;
}

vec3 getColor(vec3 r, vec3 o, vec2 data){
    float t = data.x;
    vec3 p = o + r * t;
    vec3 col1 = vec3(sin(p.x) * 0.5 + 0.75,sin(p.y) * 0.5 + 0.75,sin(p.z) * 0.5 + 0.75);
    vec3 col2 = vec3(cos(p.x) * 0.5 + 0.75,cos(p.y) * 0.5 + 0.75,cos(p.z) * 0.5 + 0.75);
	float fog = 1./(1. + t * t * 0.01);
    float a = sin(p.z - iTime * 4.) * 0.5 + 0.5;
    col1 = mix(col1,col2,a);
    col1 = mix(col1,vec3(sin(iTime) * 0.5 + 0.5),1. - fog);
    return col1;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord.xy * 2. - iResolution.xy)/iResolution.y;
    vec3 o = cam(iTime);
    vec3 r = ray(uv,1.5);
    vec2 data = trace(r,o);

    // Time varying pixel color
    vec3 col =getColor(r,o,data);

    // Output to screen
    fragColor = vec4(col,1.0);
}

#define PI 3.1415926
#define ALPHA(d) 1. - clamp(d*200., 0., 1.)

float hash(float p) { vec3 p3 = fract(vec3(p) * 0.1031); p3 += dot(p3, p3.yzx + 19.19); return fract((p3.x + p3.y) * p3.z); }

vec2 rotate(vec2 v, float a) { return cos(a)*v + sin(a)*vec2(v.y,-v.x); }

float fade(float t) { return t*t*t*(t*(6.*t-15.)+10.); }

float grad(float hash, float p) { return (int(1e4*hash) & 1) == 0 ? p : -p; }

float perlinNoise1D(float p) {
	float pi = floor(p), pf = p - pi, w = fade(pf);
    return mix(grad(hash(pi), pf), grad(hash(pi + 1.0), pf - 1.0), w) * 2.0;
}

float fbm(float pos, int octaves, float persistence)  {
    float total = 0., frequency = 1., amplitude = 1., maxValue = 0.;
    for(int i = 0; i < octaves; ++i) {
        total += perlinNoise1D(pos * frequency) * amplitude;
        maxValue += amplitude;
        amplitude *= persistence;
        frequency *= 2.;
    }
    return total / maxValue;
}

//2d distance functions from http://www.iquilezles.org/www/articles/distfunctions2d/distfunctions2d.htm
float sdTriangle(vec2 p, vec2 p0, vec2 p1, vec2 p2 ) {
    vec2 e0 = p1-p0, e1 = p2-p1, e2 = p0-p2;
    vec2 v0 = p -p0, v1 = p -p1, v2 = p -p2;
    vec2 pq0 = v0 - e0*clamp( dot(v0,e0)/dot(e0,e0), 0.0, 1.0 );
    vec2 pq1 = v1 - e1*clamp( dot(v1,e1)/dot(e1,e1), 0.0, 1.0 );
    vec2 pq2 = v2 - e2*clamp( dot(v2,e2)/dot(e2,e2), 0.0, 1.0 );
    float s = sign( e0.x*e2.y - e0.y*e2.x );
    vec2 d = min(min(vec2(dot(pq0,pq0), s*(v0.x*e0.y-v0.y*e0.x)),
                     vec2(dot(pq1,pq1), s*(v1.x*e1.y-v1.y*e1.x))),
                     vec2(dot(pq2,pq2), s*(v2.x*e2.y-v2.y*e2.x)));
    return -sqrt(d.x)*sign(d.y);
}

float sdCircle(vec2 p, float r) { return length(p) - r; }

float sdBox(vec2 p, vec2 b) { vec2 d = abs(p)-b; return length(max(d,vec2(0))) + min(max(d.x,d.y),0.0); }

vec4 mixLayer(vec4 layer1, vec4 layer2) { return mix(layer1, layer2, layer2.a); }

vec4 sky(vec2 uv) { 
    vec3 col = vec3(.3,.5,.8)-uv.y*.3;
    col = mix(col, vec3(1.-uv.y,.6,.0), pow(1. - clamp(uv.y-.2,0.,1.), 2.));
    return vec4(col, 1.); 
}

vec4 sun(vec2 uv) {
    vec2 d = uv - vec2(1.1, .8);
    float t = pow(1./(1.+length(d)), 4.);
	vec3 col =  3. * vec3(.8, .7, .5) * t; //sun disk
    float a = atan(d.y/d.x)+iTime;
    col = mix(vec3(1.,1.,.5)*t*step(mod(a, PI/8.),PI/16.), col, t); //sun rays
    return vec4(col, t);
}

vec4 hill(vec2 pos, vec2 size, vec3 col, bool snow) {
	float d = (pos.y - size.y*cos(pos.x*1./size.x));
    if (abs(pos.x) > .5*PI*size.x || pos.y < 0.) return vec4(0.);
    if (snow && pos.y > .7*size.y + .1*fbm(pos.x*8., 1, .5)) col = vec3(.95);
    return vec4(col, ALPHA(d));
}

vec4 windmill(vec2 pos, float scale) {
    pos/=scale;
	float d = sdBox(pos, vec2(.003, .15));
    pos -= vec2(0.,.15);
    d = min(d, sdCircle(pos, .01));
#define WING d = min(d, sdTriangle(pos, vec2(0.), vec2(.1,-.09), vec2(.02,-.04)));
    pos = rotate(pos, iTime); WING;
    pos = rotate(pos, PI*2./3.); WING;
    pos = rotate(pos, PI*2./3.); WING;
    return vec4(vec3(1.), ALPHA(d));
}

vec4 grid(vec2 pos, vec3 v[4], vec3 col) {
    for (int i = 0; i < 4; ++i) {
        v[i].yz = rotate(v[i].yz, PI/4.);
        v[i].xz = rotate(v[i].xz, .5*sin(iTime));
        v[i]*=clamp(v[i].z+1., .2, 2.);
    }
    float d = sdTriangle(pos, v[0].xy, v[1].xy, v[2].xy);
    d = min(d, sdTriangle(pos, v[0].xy, v[2].xy, v[3].xy));
	return vec4(col, ALPHA(d));
}

vec4 solarPanel(vec2 pos, float scale) {
 	pos/=scale;
	vec4 col = vec4(vec3(.8), ALPHA(sdBox(pos, vec2(.003, .1))));
    pos -= vec2(0.,.1);
    float w = .24, h = .12, dx = w / 4., dy = h / 2., k=1.;
    for (float x = -w/2.; x < w/2.; x += dx) {
        for (float y = -h/2.; y < h/2.; y += dy, k=-k) {
            vec3 gridCol = k > 0. ? vec3(0.,.2,.6) : vec3(0.,.3,.5);
        	col = mixLayer(col, grid(pos, vec3[](vec3(x,y,0.),vec3(x+dx,y,0.),vec3(x+dx,y+dy,0.),vec3(x,y+dy,0.)), gridCol));
        }
	}
    return col;
}

vec4 opU(vec4 c1, vec4 c2) {return c1.w < c2.w ? c1 : c2; } 

vec4 tree(vec2 pos, float scale) {
 	pos/=scale;
    vec4 col = vec4(vec3(.5), ALPHA(sdBox(pos, vec2(.008, .08))));
    pos.x += .1*sin(2.*iTime) * pos.y;
    col = mixLayer(col, vec4(vec3(.2, .32, .1), ALPHA(sdTriangle(pos, vec2(-.1, -.03), vec2(.1, -.03), vec2(0., .05)))));
    col = mixLayer(col, vec4(vec3(.2, .36, .1), ALPHA(sdTriangle(pos, vec2(-.07, .02), vec2(.07, .02), vec2(0., .08)))));
    col = mixLayer(col, vec4(vec3(.2, .34, .1), ALPHA(sdTriangle(pos, vec2(-.045, .06), vec2(.045, .06), vec2(0., .14)))));
    return col;
}

vec4 mountain(vec2 uv) {
	vec4 col = mixLayer(vec4(0.), windmill(uv-vec2(.5, .2), .3));
    col = mixLayer(col, hill(uv-vec2(-1.5, -.4), vec2(.2, .7), vec3(.2, .32, .1), true));
    col = mixLayer(col, hill(uv-vec2(1.5, -.4), vec2(.3, 1.2), vec3(.62, .67, .68), true));
    col = mixLayer(col, hill(uv-vec2(-.5, -.4), vec2(.5, .55), vec3(.0, .7, .2), false));
    col = mixLayer(col, hill(uv-vec2(.5, -.4), vec2(.5, .6), vec3(.0, .7, .2), false));
    col = mixLayer(col, hill(uv-vec2(-1., -.4), vec2(.4, .35), vec3(.2, .85, .4), false));
	col = mixLayer(col, windmill(uv-vec2(-.5, .1), .45));
	col = mixLayer(col, windmill(uv-vec2(1., -.05), .6));
    col = mixLayer(col, hill(uv-vec2(1., -.4), vec2(.4, .3), vec3(.2, .85, .4), false));
    col = mixLayer(col, hill(uv-vec2(.0, -.4), vec2(.5, .5), vec3(.2, .85, .4), false));
    col = mixLayer(col, windmill(uv-vec2(.2, .1), .8));
    col = mixLayer(col, windmill(uv-vec2(-.3, -.2), 1.));
	col = mixLayer(col, windmill(uv-vec2(-1., .05), .7));
   	col = mixLayer(col, solarPanel(uv-vec2(.3, -.2), 1.));
    col = mixLayer(col, solarPanel(uv-vec2(-.8, -.1), .8));
    col = mixLayer(col, tree(uv-vec2(1.2, -.2), 1.));
    col = mixLayer(col, tree(uv-vec2(-1.1, -.1), .9));
    col = mixLayer(col, tree(uv-vec2(-.1, -.15), .9));
    col = mixLayer(col, tree(uv-vec2(0., .12), .6));
    col = mixLayer(col, tree(uv-vec2(.1, .0), .6));
    col = mixLayer(col, tree(uv-vec2(-.1, .1), .6));    
    return col;  
}

vec4 building(vec2 uv) {
    float d = uv.y - (.8*hash(floor(uv.x*8.))+.2);
    vec3 col = vec3(.15, .35, .5);
    return vec4(col, ALPHA(d));   
}

vec4 wave(vec2 uv, float height, float amplitude) {
    float noise = amplitude * (fbm(uv.x*4., 4, .1) + fbm(uv.x*4. + iTime, 4, .1));
    float d = uv.y - (noise + height);
    return vec4(vec3(.0, .15, .4), 1.-clamp(d*30., 0., 1.));
}

vec4 water(vec2 uv) {
	vec4 col;
    float height = -.4, amplitude = .005;
    for (int i = 0; i < 5; ++i, uv.x+=10., height-=.13, amplitude+=.003)
    	col = mixLayer(col, wave(uv, height, amplitude));
    return col;
}

vec4 sailingBoat(vec2 uv) {
    vec4 col = vec4(vec3(1.), ALPHA(sdBox(uv-vec2(0.,.13), vec2(.001, .1))));
    vec2 p[4] = vec2[](vec2(-.1, .0), vec2(.1, .0), vec2(.15, .07), vec2(-.13,.05));
    col = mixLayer(col, vec4(vec3(1.,.8,.0), ALPHA(sdTriangle(uv, p[0], p[1], p[3]))));
    col = mixLayer(col, vec4(vec3(1.,.8,.0), ALPHA(sdTriangle(uv, p[1], p[2], p[3]))));
    col = mixLayer(col, vec4(vec3(1.), ALPHA(sdTriangle(uv, vec2(.01,.23), vec2(.01,.08), vec2(.1,.08)))));
    col = mixLayer(col, vec4(vec3(1.), ALPHA(sdTriangle(uv, vec2(-.01,.21), vec2(-.01,.08), vec2(-.08,.08)))));
    return col;
}

vec4 render(vec2 uv) {
	vec4 col = sky(uv);
    col = mixLayer(col, sun(uv));
    col = mixLayer(col, building(uv));
    col = mixLayer(col, mountain(uv));
    col = mixLayer(col, water(uv));
    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = (2.*fragCoord-iResolution.xy) / iResolution.y;
    if (uv.y >= -.4) 
        fragColor = render(uv);
    else //reflections
        fragColor = mix(render(uv), render(vec2(uv.x, max(-.4, -uv.y*2.-1.2 + .04*fbm(uv.x*4.+iTime,4,.1)))), .1);
        float w = iResolution.x / iResolution.y + .2;

    uv -= vec2(2.*w*fract(.05*iTime+.5)-w,-.7);
    uv = rotate(uv, sin(5.*iTime) * .02);
    fragColor = mixLayer(fragColor, sailingBoat(uv));
    //reflection of the sailing boat
    fragColor = mixLayer(fragColor, .3*sailingBoat(vec2(uv.x, -uv.y*2.-.05 + .04*fbm(uv.x*4.+iTime,4,.1))));
}

/**
 * https://thebookofshaders.com/12/
 */
vec2 random2(vec2 p) {
	return fract(sin(vec2(dot(p,vec2(127.1,311.7)),dot(p,vec2(269.5,183.3))))*43758.5453);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    // Some variables
    vec2 uv = fragCoord/iResolution.xy;
    uv.y *= iResolution.y / iResolution.x;
    float gridLength = 3. + sin(iTime * .2) * 1. + 1.;
    vec2 gridCoords = floor(uv * gridLength);
    vec2 gridFrag = fract(uv * gridLength);

    // Calc min distance to random point by grid
    float minDist = 1.;
    for(int x=-1; x<=1; x++) {
        for(int y=-1; y<=1; y++) {
        	vec2 neighborGridCoords = vec2(float(x), float(y));
            vec2 randomPoint = random2(neighborGridCoords + gridCoords);
            randomPoint = sin(iTime * randomPoint) * 0.5 + .5;
            minDist = min(minDist, length(neighborGridCoords + randomPoint - gridFrag));
        }
    }

    // Apply Post Effect
    vec3 color = vec3(minDist);
    color += step(.5, sin((20. * (uv.y * gridLength * 2.) + 10.) * color)) * (.2 + (sin(iTime * 2.) * .1 + .1));
    color.r += uv.x * .1 + (sin(iTime) * .5 + .5) * .2;
    color.g += uv.x * .1;
    color.b += (1.-uv.y) * .1;

    // Apply Post Effect (Part 2)
    color += step(.5, color) * uv.x * .1;

    // Apply Post Effect (Part 3);
    color.r -= uv.x * .06;
    color.g -= (1.0-uv.x) * .03;
    color.b += (2.0-uv.y) * .1;

    // Draw
    fragColor = vec4(color,1.);
}

