void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
  	vec3 bgColor = vec3 (0.1,0.0,0.0);

  	vec2 vPos=-1.0+2.0*fragCoord.xy/iResolution.xy;
  	vec3 finalColor = vec3 (0.0);  

  	//Camera animation
  	vec3 vuv=vec3(0,0,1);//Change camere up vector here

    vec3 prp=vec3(
        	ss(-5.4,15.6,iTime/2.),
        	ss(-2.,2.,iTime),
        	4. ); //Change camera path position here

        	//2.5 , 2., 2.5 );

    vec3 vrp=vec3(
        2.5,
        2.5,
        //ss(0.,8.,iTime) ); //Change camere view here
        2.5);

  	//Camera setup
  	vec3 vpn=normalize(vrp-prp);
  	vec3 u=normalize(cross(vuv,vpn)*rotate3(vec3(ss(-0.3,0.3,iTime*0.63),0.,0.) ));
  	vec3 v=cross(vpn,u);
  	vec3 vcv=(prp+vpn);
  	vec3 scrCoord=vcv+vPos.x*u*iResolution.x/iResolution.y+vPos.y*v;
  	vec3 scp=normalize(scrCoord-prp);

  	//Raymarching
  	const vec3 e=vec3(0.2,0,0);
  	const float maxd=50.0; //Max depth

  	float s= 0.1;
  	vec3 c,p,n;

  	float f=1.0;
  	for(int i=0;i<  ITER ; i++)
    {
    	if (abs(s)<.01||f>maxd) break;
    	f+=s;
    	p=prp+scp*f;
    	s=distanceField(p);
  	}

  	if (f<maxd)
    {

    	n=normalize(
      	vec3(s-distanceField(p-e.xyy),
        	s-distanceField(p-e.yxy),
        	s-distanceField(p-e.yyx)));

        float texScale = 0.5;
        n = doBumpMap(iChannel1, p*texScale, n,  0.40);

        float b=dot(n,normalize(prp-p));

        c=tex3D(iChannel1, p*texScale, n)*4.;

        vec3 lightPos = vec3( 2.5,2.5,sin(iTime*1.1)*4. );
        vec3 lightDir = normalize(lightPos-p);
        float lightDistance = distance(lightPos,p);

        float b2=dot(lightDir,n);

        vec3 c1 = vec3( b*c+pow(b,8.) ) ;
        vec3 c2 = vec3( c*(b2 + pow(b2, 8.) + 0.35)*1.5/(1.+lightDistance*.1+lightDistance*lightDistance*.005));  // Shane suggestion


        finalColor = mix(c1,c2,.6);
		finalColor*=0.44;

    	// vignetting from : https://www.shadertoy.com/view/4lSXDm
    	vec2 uv = fragCoord.xy / iResolution.xy;
    	vec2 coord = (uv - 0.5) * (iResolution.x/iResolution.y) * 2.0;
    	float Falloff = 0.35;
        float rf = sqrt(dot(coord, coord)) * Falloff;
	    float rf2_1 = rf * rf + 1.0;
	    float e = 1.0 / (rf2_1 * rf2_1);

	    vec4 src = vec4(finalColor,1.);
		fragColor = vec4(src.rgb * e, 1.0);    
    	//fragColor = vec4( finalColor, 1.);
  	} else 
    { // background color
    	bgColor = 
	  		vec3(0.1,0.1,0.1)*0.4;
	  	fragColor=vec4(bgColor,1.);
  	}
}

/**

Sine Wave 2D

c.Kleinhuis evoke 2019 - talk 'hybrid fractals'

Controls:

- use mouse x/yo change frequencies of 2 waves

here a second dimension is added to create a place on the plane

2 hybrid sine waves form the position of each dot

this is the way how common demo programming sprite/endless bobs/dotflag
effects created their output and serves as example how hybridisation has
been always used in computer/demo scene programming

*/
#define NUM_POINTS 1000000.0f
#define POINT_SIZE 0.005f

vec2 increment1=vec2(1.7f,1.1f);
vec2 increment2=vec2(1.7f,1.2f);
vec2 increment3=vec2(1.4f,1.1f);
vec2 amplitude=vec2(1,1); 

float sineDist(vec2 p){

  float time=iTime*100.0f;
vec2 frequency1=iMouse.xy/iResolution.xy;
vec2 frequency2=iMouse.zw/iResolution.xy;
vec2 frequency3=vec2(1.,2.);
    float mini=NUM_POINTS;
    for(float i=0.;i<800.f;i++){
           // current point
           vec2 curr=vec2(         
               // x
               sin(radians(time*frequency1.x+i*frequency1.x*increment1.x))*amplitude.x/6.0+amplitude.x/6.0+ 
               sin(radians(time*frequency2.x+i*frequency2.x*increment2.x))*amplitude.x/6.0+amplitude.x/6.0+                  
               sin(radians(time*frequency3.x+i*frequency3.x*increment3.x))*amplitude.x/6.0+amplitude.x/6.0,                   
               // y
               sin(radians(time*frequency1.y+i*frequency1.y*increment1.y))*amplitude.y/6.0+amplitude.y/6.0+
               sin(radians(time*frequency2.y+i*frequency2.y*increment2.y))*amplitude.y/6.0+amplitude.x/6.0+ 
               sin(radians(time*frequency3.y+i*frequency3.y*increment3.y))*amplitude.y/6.0+amplitude.y/6.0
        );

        if(length(p-curr)<POINT_SIZE){
        	mini=min(mini,length(p-curr));
        }

    }

    return 1.-mini;


}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col =       vec3(0.,0.,0.);

    col.r=sineDist(uv);
    // Output to screen
    fragColor = vec4(col,1.0);


}

float PI = 3.14159265;
mat2 genRot(float v){
	return mat2(cos(v),-sin(v),sin(v),cos(v));
}
vec3 pmod(vec3 p,float c){
    float tmp = PI * 2. / c;
	float l = length(p.xy);
    float theta = atan(p.y/p.x);
    theta = mod(theta,PI * 2. / c);
    return vec3(l * cos(theta), l * sin(theta),p.z);

}
vec3 cellMod(vec3 p,vec3 c){
    p.z = mod(p.z + c.z / 2.,c.z) - c.z/2.;
	p.x = mod(p.x + c.x / 2.,c.x) - c.x/2.;
    p.y = mod(p.y + c.y / 2.,c.y) - c.y/2.;
    return p;
}

float cube(vec3 p, vec3 o){
	return max(
    	abs(p.x - o.x),
        max(abs(p.y - o.y),
            abs(p.z - o.z))
    );
}

float map(vec3 p){
    p.xy *= genRot(iTime);
	p = cellMod(p,vec3(2.0,2.0,0.5));
    p.xy *= genRot(PI / 12.);
	p = pmod(p,12.);
    p.xy *= genRot(-PI / 12.);

    float cu = cube(p,vec3(1.,0.,0.)) - 0.2;
    float sp = length(p - vec3(1.,0.,0.)) - 0.1;
    sp = min(sp,length(p.xz - vec2(1.,0.)) - 0.05);
    return mix(cu,sp,smoothstep(0.4,0.6,abs(fract(iTime/2.) - 0.5) * 2.0));

}

const float EPS = 0.001;
vec3 getNormal(vec3 p) {
    return normalize(vec3(
        map(p + vec3(EPS, 0.0, 0.0)) - map(p + vec3(-EPS,  0.0,  0.0)),
        map(p + vec3(0.0, EPS, 0.0)) - map(p + vec3( 0.0, -EPS,  0.0)),
        map(p + vec3(0.0, 0.0, EPS)) - map(p + vec3( 0.0,  0.0, -EPS))
    ));
}

vec4 trace (vec3 o, vec3 r){
	float t = 0.0;
    vec3 p = vec3(0.0,0.0,0.0);

    for(int i = 0; i < 96; ++i){
        p = o + r * t;
        float d = map(p);
        t += d * 0.5;
    }
    return vec4(getNormal(p),t);
}


void mainImage( out vec4 fragColor, in vec2 U )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec3 R = iResolution,
    r = normalize(vec3((2.*U - R.xy )/  R.y,1.2)),
    o = vec3(0,0,-1.5 + iTime * 2.);
    r.yz *= genRot(iTime * PI / 8.);
    vec4 data = trace(o,r);
    vec3 n = vec3(data.xyz);
    float t = data.w;
    float fog = 1.0 / (1.0 + t * t * 0.05);
    float tmp = sqrt(dot(n,r));
vec3 fc = t > 10000.0 ? vec3(0.8) : mix((vec3(data.z) + 1.0)/1.5
                                            ,vec3(0.0), - pow(dot(n,r),1.0));
	fc = mix(fc,vec3(1.),1. - fog);
    //fc = vec3(fog);
    // Output to screen
    fragColor = vec4(fc,1.0);
}

#define MAX_DIST 250.
#define EPS 0.01

// Uncomment to switch to dark color pallete
//#define DARK_MODE

vec3 spCenter;

float sphere(vec3 p, vec3 center, float radius)
{
    return length(center - p) - radius;
}

mat2x2 rot(float ang)
{
    ang *= 3.141592654 / 180.;
 	return mat2x2(cos(ang), sin(ang), -sin(ang), cos(ang));   
}

float terrain(vec3 p)
{
    float d = 0.;
    d += 3.*sin(iTime+p.z/10.)*cos(iTime+p.x/10.);
    d += (sin(iTime+p.z/5642.38453)*cos(iTime+p.x/345.153));
    d += 2.*sin(p.x*p.z/2500.+iTime);
    return d;
}

vec4 map(vec3 p)
{
    float h = terrain(p);
 	h *= terrain(p+cos(iTime+p.z*p.x/50.))/2.;
    float d = p.y + h;
    vec3 col = vec3(0);
    float ringSize1 = 20. + iMouse.y/20. + 7.*sin(p.x/7.+iTime);
    float ringSize2 = 20. + 10.*sin(p.z/25.+1.325*iTime);

    float ringSize3 = 55. + iMouse.y/20. + 10.*sin(p.x/50.+iTime);
    float ringSize4 = 20. + 10.*cos(p.x/15.+2.325*iTime);

    float spRotRad = 20.;
    spCenter = vec3(spRotRad * cos(iTime), 10., 50. + spRotRad * sin(iTime));

    float sp = sphere(p, spCenter, 3./* + sin(length(p))*/);

    if(sp < d)
    {
        d = sp;
        col = vec3(.7, .5, .5);
    }else
    {
        if(length(p) > ringSize1 && length(p) < ringSize1 + ringSize2)
        {
            col = normalize(vec3(p.x, p.z, .5));

            col.zy = (rot(iTime*15.) * col.xy);
            #ifndef DARK_MODE
            col.zy = abs(col.zy);
            #endif
            col.zx = (rot(iTime*30.) * col.yx);
            #ifndef DARK_MODE
            col.zx = abs(col.zx);
            #endif
            col += .2;
        }
        if (length(p) > ringSize3 && length(p) < ringSize3 + ringSize4)
        {
            col += normalize(vec3(p.x, .5, p.z));

                col.xy = (rot(iTime*15.) * col.xy);
                #ifndef DARK_MODE
                col.zy = abs(col.zx);
                #endif
                col.zy = (rot(iTime*30.) * col.yx);
                #ifndef DARK_MODE
                col.zx = abs(col.zx);
                #endif
            col += .2;
        }
    }


    return vec4(col, d);
}

vec3 deriv(vec3 p)
{
    return normalize(vec3(
        map(p + vec3(EPS, 0, 0)).w - map(p - vec3(EPS, 0, 0)).w,
        map(p + vec3(0, EPS, 0)).w - map(p - vec3(0, EPS, 0)).w,
        map(p + vec3(0, 0, EPS)).w - map(p - vec3(0, 0, EPS)).w
        ));
}

vec4 march(vec3 ro, vec3 rd)
{

    vec4 d=vec4(0.);
    float dv;
    for(int i = 0; i < 250; i++)
    {
        vec3 newRay = ro + dv * rd;
        d = map(newRay);
        if(d.w < EPS || dv > MAX_DIST)
            break;
        dv += d.w * .3;
    }

    return vec4(d.xyz, dv);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy - .5;
    uv.x *= iResolution.x / iResolution.y;

    vec3 ro = vec3(0, 30, -1);

    vec3 lookat = vec3(0, 0, 45);

    vec3 fw = normalize(lookat - ro);
    vec3 ri = normalize(vec3(fw.z, 0, -fw.x));
    vec3 up  = cross(fw, ri);

    vec3 rd = mat3x3(ri, up, fw) * vec3(uv, 1);

    vec4 d = march(ro, rd);

    vec3 light = vec3(1);
    light.xz *= rot(iMouse.x/2. + 200.);
    vec3 col = vec3(0);
    if(d.w < MAX_DIST)
    {
        vec3 hitPoint = ro + rd * d.w;
        vec3 n = deriv(hitPoint);
        vec3 basecolor = d.xyz*vec3(.7, 0.3, .5)*1.2;

        float ds = march(hitPoint + n *.1, light).w;
        float l = .3;
        if(ds>=MAX_DIST)
            l = max(.3, dot(n, 1.3*normalize(light)));
        col = basecolor * l;
    }
    // Output to screen

    fragColor = vec4(col,1.0);
}

const float z = 1.;
const float complexity = 13.;
const float speed = .5;// scene changing speed
const float density = .8; // 0-1

const float PI = atan(1.)*4.;

vec4 hash42(vec2 p)
{
	vec4 p4 = fract(vec4(p.xyxy) * vec4(.1031, .1030, .0973, .1099));
    p4 += dot(p4, p4.wzxy+33.33);
    return fract((p4.xxyz+p4.yzzw)*p4.zywx);
}

mat2 rot2D(float r){
    return mat2(cos(r), sin(r), -sin(r), cos(r));
}
#define q(x,p) (floor((x)/(p))*(p))


void mainImage( out vec4 o, vec2 C)
{
    vec2 R = iResolution.xy;
    vec2 uv = C/R.xy;
    vec2 N = uv;
    uv.x *= R.x/R.y;
    uv *= z;
    float t = iTime+1e4;
    uv += floor(t*speed)*z*1.618;// scene switcher
    o = vec4(1);

    for (float i = 1.;i <= complexity; ++ i) {
        vec4 h = hash42(floor(uv+i));
        vec2 p = fract(uv+i)-.5;
        p *= rot2D(h.x*PI*2.);
        uv += p * h.z;
        h = hash42(floor(uv));
        h += length(fract(p+q(iTime,.1)*.1)); // animate
        h = fract(h);
        if (i < 2. || h.w > density) {
            o *= h;
        }
    }
    o=pow(o,o-o+.5);
  	o=step(.5,o) * mod(C.y,3.)/2.;
}

#define PI 3.14159265359

mat2 rot(float t) {
    return mat2(cos(t), sin(t), -sin(t), cos(t));
}

float heart(vec2 p) {
    p.y -= 1.6;
    float t = atan(p.y, p.x);
    float s = sin(t);
    float r = 2.0-2.0*s+s*(sqrt(abs(cos(t)))/(s+1.4));
    return r+2.0;
