#define MAX_MARCH 640
#define MAX_DST 100.
#define MIN_DST .001
#define AMBIENT_DST .1

#define OBJ_PLANE .5
#define OBJ_SPHERE .51

#define OBJECTS 8

mat4 obj[OBJECTS];

// globals
vec3 p;
int o;
float deltaDst;

vec3 getNor(int o, vec3 p) {
    if(obj[o][3][3] < OBJ_SPHERE) {
        return obj[o][2].xyz;
    }else{
    	return normalize(p - obj[o][0].xyz);
    }
}

float getDistance(vec3 p) {
    float min = 255.;
    float res;
    for(int c = 0; c < OBJECTS; c++) {

        if(obj[c][3][3] < OBJ_SPHERE) {
            res = abs(p.y - obj[c][0][1]); // plane
        }else{
            res = length(p - obj[c][0].xyz) - obj[c][0].w; // sphere
        }

        if(min > res) {
            deltaDst = min - res;
            min = res;
            o = c;
        }

    }
    return min;
}

float rayMarch(vec3 start, vec3 dir) {
    float ret = 0.;
    int i;
    float dst;

    for(i = 0; i < MAX_MARCH; i++) {
        p = start + dir * ret;
        dst = getDistance(p);
        ret += dst;
        if(ret > MAX_DST || dst < MIN_DST) {
           break; 
        }
    }
    return ret;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    // Normalized pixel coordinates (from -1 to 1)
    vec2 uv = (fragCoord - .5 * iResolution.xy) / iResolution.y;

    // light
    vec3 light = vec3(sin(iTime) * 10., 6., cos(iTime) * 10. + 10.);

    // camera
    vec3 cam = vec3(0., 1., 0.);
    vec3 dir = normalize(vec3(uv.x, uv.y, 1.));

    // objects
    obj[0][0] = vec4(0., .2, 0., 0.); // infinite plane at zero Y
    obj[0][1] = vec4(.5, .5, .5, 1.);
    obj[0][2] = vec4(0., 1., 0., 0.);
    obj[0][3][3] = OBJ_PLANE;

    obj[1][0] = vec4(0., 1., 6., 1.); // red sph
    obj[1][1] = vec4(1., 0., 0., 1.);
    obj[1][3][3] = OBJ_SPHERE;

    obj[2][0] = vec4(1.5, 0.8, 6., 0.8); // blue sph
    obj[2][1] = vec4(0., 0., 1., 1.);
    obj[2][3][3] = OBJ_SPHERE;

    obj[3][0] = vec4(-1.5, 0.8, 6., 0.8); // green sph
    obj[3][1] = vec4(0., 1., 0., 1.);
    obj[3][3][3] = OBJ_SPHERE;

    obj[4][0] = vec4(0., .4, 2., .4); // yellow sph
    obj[4][1] = vec4(1., 1., 0., 1.);
    obj[4][3][3] = OBJ_SPHERE;

    obj[5][0] = vec4(1.5, 0.6, 3., 0.6); // magenta sph
    obj[5][1] = vec4(1., 0., 1., 1.);
    obj[5][3][3] = OBJ_SPHERE;

    obj[6][0] = vec4(-1.5, 0.6, 3., 0.6); // cyan sph
    obj[6][1] = vec4(0., 1., 1., 1.);
    obj[6][3][3] = OBJ_SPHERE;

    obj[7][0] = vec4(0., 7., 0., 0.); // infinite plane at 7 Y
    obj[7][1] = vec4(.5, 1., .5, 1.);
    obj[7][2] = vec4(0., -1., 0., 0.);
    obj[7][3][3] = OBJ_PLANE;

    for(int i = 0; i < OBJECTS; i++) {
        //obj[i][0].x *= cos(iTime);
        //obj[i][0].z *= sin(iTime);
    }

    float dst = rayMarch(cam, dir);

    dir = normalize(light - p);
    vec3 nor = getNor(o, p);

    float colMul = dot(dir, nor);
    float ambient = 1.;

    if(deltaDst < AMBIENT_DST) {
        ambient = deltaDst / AMBIENT_DST;
    }

    vec4 c = obj[o][1];
    vec3 collisionP = p;
    int collisionO = o;

    float shadow = rayMarch(p + (nor * MIN_DST * 2.), dir);

    if(shadow < length(light - p)) {
        colMul *= .2;
    }

    nor = normalize(reflect(collisionP - cam, nor));
    if(rayMarch(collisionP + (nor * MIN_DST * 2.), nor) < 10.) {
    	c = obj[o][1] + (obj[collisionO][1] * .5);
    }

    c.rgb *= colMul;

    fragColor = c * ambient;
}

// trippy

mat2 rot(float a)
{
	float c = cos(a);
	float s = sin(a);
	return mat2(c, s, -s, c);
}
vec3 fx(float time, vec2 uv)
{
    //float time = iTime;
	vec2 f = vec2(0.3);
	vec3 c = vec3(1.2,1.0,1.0);
	float light = 0.1;

	for (float x = 1.1; x < 10.0; x += 1.0)
	{
		uv *= rot(x*200.0+sin(time*0.08));

		f = vec2(cos(cos(time*0.6+x + uv.x * x) - uv.y * dot(vec2(x + uv.y), vec2(sin(x), cos(x)))));
		light += (0.04 / distance(uv, f)) - (0.01 * distance(vec2((cos(time*0.3 + uv.y))), vec2(uv)));

		c.y += sin(x+time+abs(uv.y))*0.3;
		if (c.y<0.8)
			c.y = 0.8;
		light-=x*0.001 + c.y*0.001;

	}
	c *= light;
	c.x += (sin(time*2.4)*0.1);
    return c;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord.xy - 0.5 * iResolution.xy) / iResolution.y;
    vec3 ccc = fx(iTime*0.76,uv);
    uv.y = dot(uv,uv);
    uv.x = dot(uv,uv*2.0)*0.25;
    fragColor = vec4(ccc*fx(iTime,uv),1.0);
}

mat2 rot(float t){
	return mat2(cos(t), sin(t), -sin(t), cos(t));
}

float dist(in vec3 pos){
    vec3 p=pos;
    vec3 a=0.14*vec3(cos(iTime));
    float c=0.2*pow(0.01, smoothstep(0.6, -0.6, cos(iTime*0.7)));

    for(int k=0;k<10;k++){
        p=sqrt(p*p+c)-0.5;
		p.xy*=rot(a.z);
        p.yz*=rot(a.x);
        p.zx*=rot(a.y);
    }
    return length(p)-0.45;
}

void getColor(out vec4 fragColor, in vec3 pos, in vec3 ray){
    vec3 bg=vec3(0.0, 0.0, 0.05);
    vec3 solid=vec3(1.0, 0.5, 0.55);
	fragColor=vec4(bg, 1.0);
    float t=0.;
    float dprev=1.0;
    for(int i=0;i<200;i++){
		float d=dist(pos);
        pos += d*ray;
        t += d;
        if(t>1e3)return;
        if(d<1e-3){
			float u=1.0-d/dprev;
            u+=0.5;
            fragColor=vec4(solid*u, 1.0);
            return;
        }
        dprev=d;
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord * 2.0 - iResolution.xy)/iResolution.y;

    vec3 pos = vec3(40.0*cos(iTime*1.1), 15.0, 40.0*sin(iTime*1.1));
    vec3 eye = -normalize(pos);
    vec3 up = vec3(0.0, 1.0, 0.0);
    up-=dot(up, eye)*eye;
    up=normalize(up);
    vec3 right = cross(eye, up);
    float angle=0.3;
    vec3 ray = eye + (uv.x*right+uv.y*up) * angle;
    ray = normalize(ray);

    getColor(fragColor, pos, ray);
}

