    //ammount
    float anim_effect_1 = ((sin (iTime + 0.3))*0.005);
    float radius = length(uv) + anim_effect_1;
    //contrast
    radius = pow(radius, barrelContrast);

    uv.x = radius * cos(theta);
    uv.y = radius * sin(theta);
    color.r = 0.5 * (uv.y +1.0);
    color.b = radius;
    return 0.5 * (uv + 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord) {

    //animation
    barrelContrast += (((sin(iTime) + cos(cos(iTime)))/2.0)-0.5);
    //screen coord
    vec2 normalizedUV = (fragCoord.xy/(iResolution.xy/2.0)) -1.0;
    vec2 distortedUV = BarrelDistortion(normalizedUV)* iResolution.xy;
    MakeGrid(distortedUV.xy);


    fragColor = vec4( color, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    float scale = 5.0; // How much of the xy plane you see; window goes from '-scale' to 'scale' on the x and y axes
    float speed = 1.0; // Determines the speed of the animation
    float rings = 3.0; // Roughly Determines the number of rings on screen
    float radius = 4.0;// Determines the radius on which the foci orbit

    float time = iTime*speed;// adjust time based on speed

    vec2 uv = scale*2.0*fragCoord/iResolution.xy - scale; // scale coordinates

    // Focus F1 and Focus F2 orbit on a circle at different speeds in different directions
    vec2 F1 = vec2 (radius*cos(-7.0*time*0.029),radius*sin(-7.0*time*0.029)); // Determine foci position on a circle
    vec2 F2 = vec2 (radius*cos(5.0*time*0.029),radius*sin(5.0*time*0.029));
    // F1 moves at speed -7, F2 moves at speed 5

    float D1 = length(F1 - uv); // Get distances to foci
    float D2 = length(F2 - uv);

    // Q is set to the sum of the sines of the distance of point uv to the foci
    // This creates the ring effect
    float Q = sin(rings*D1) + sin(rings*D2); // Compute locus

    // This sets the boundary between black and red, using 'Q<sin(time)' causes black and red to pulse in and out,
    //   using 'Q<1' generates rings, 'Q<0' generates a checkerboard. Setting Q< values between 2 and -2 generates  
    //   interesting patterns.
    if (Q < sin(time)) {
        // Q*0.25+o.5 generates the light borders in the red regions
        fragColor = vec4(1.0,Q*0.25+0.5,Q*0.25+0.5,1.0);
    }
    else{
        fragColor = vec4(0,0,0,0); // Black regions
    }
}

#ifdef GL_ES
precision mediump float;
#endif

const float h=0.5;
const float j=0.5;
const float F=5.0;
const float G=5.0;

void mainImage(out vec4 fragColor,in vec2 fragCoord)

{
	float f = F;
	float g = G;

	vec2 res = iResolution.xy;
	vec2 mou = vec2(0., 0.);

	mou.x = sin((iTime*0.2) * .3) * sin((iTime*0.2) * .15) * 2. + sin((iTime*0.2) * .3);
	mou.y = (1.0-sin((iTime*0.2) * .3 * 2.) )* sin((iTime*0.2) * .15) + cos((iTime*0.2) * .3);
	mou *= res;

	vec2 z = ( (-res+2.0 * fragCoord.xy) / res.y);
	vec2 p = ( (-res+2.0 + mou) / res.y) * j ;

	for( int i = 0; i < 30; i++) {

		float d = dot(z, z) + 0.0 * dot(p*0.1,z*0.1);
		z = (vec2( z.x, -z.y ) / d) + p * (h)/(j); 
		z.x = 1.0- abs(z.x);
		f = max( d-f, tan(dot(z-p,z-p) ));
		g = min( g*d, sin(dot(z+p,z+p))+1.0);
	}

	f = abs(-log(f) / 3.5);
	g = abs(+log(g) / 3.5 );

	vec3 col =  vec3(g*j, g*f, f*j);
	fragColor = vec4( min( col, 1.0), 1.0);
}

vec3 tunnel(vec3 p){
  vec3 off=vec3(0);
  off.x += sin(p.z*0.1)*1.7;
  off.y += sin(p.z*0.05)*2.3;
  return off;
}

mat2 rot(float a) {
  float ca=cos(a);
  float sa=sin(a);
  return mat2(ca,sa,-sa,ca);
}

float sph(vec3 p, float r) {
    return length(p)-r;
}


float box(vec3 p, vec3 b) {
    vec3 d = abs(p) - b;
  	return length(max(d,0.0)) + min(max(d.x,max(d.y,d.z)),0.0) - 0.05; 
}

float N(float t) {
 	return fract(2375.*sin(7523.*t));   
}

float NP(float t) {
 	return N(t)*6.28;
}

float map(vec3 p) {

    vec3 r = vec3(9.0);
    vec3 p1 = mod(p, r) - 0.5*r;
    vec3 p2 = p-p1;

    float t1 = 0.5*iTime;

    //p1.xz *= rot(2.3*p2.x + t1);
    p1.xy *= rot(3.7*p2.y + t1);
    //p1.xz *= rot(5.1*p2.z + t1);
    p1.xz *= rot(NP(p2.x) + t1);
    //p1.yx *= rot(NP(p2.y) + t1);
    p1.xz *= rot(NP(p2.z) + t1);
    float b = box(p1, vec3(1));

    float s = sph(p1 - vec3(0,0,0), 1.2);  

    return max(s, b);
}

vec3 norm(vec3 p) {
	vec2 o=vec2(0.002, 0.0);
    return normalize(vec3(
        map(p+o.xyy)-map(p-o.xyy),
        map(p+o.yxy)-map(p-o.yxy),
        map(p+o.yyx)-map(p-o.yyx)
    ));
}

vec3 march(vec3 s, vec3 r) {

    float d=0.0;
    int i=0;
    vec3 p=s;

    for (; i<100; ++i) {             
        d = map(p);   
        if (abs(d)<0.001) {
            break;
        }       
        p+=d*r;
    }        

    vec3 col = vec3(0);
    float li = 2.0;

    vec3 n = norm(p);
    vec3 l = normalize(vec3(1,1,-1));
    float lb = max(0.0,dot(n,l));
    col += lb * vec3(0.8,0.9,0.6) * li;

    vec3 h = normalize(l + -r);
    float sa = max(dot(h, n), 0.0);
    float sp = pow(sa, 5.0);
    if (lb>0.0) {
        col += sp * vec3(0.5,0.5,0.5);
    }

    float fog = 1.0-float(i)/100.0;
	col = mix(vec3(0.05, 0.05, 0.05), col, fog);

    return col;

}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = vec2(fragCoord.x / iResolution.x, fragCoord.y / iResolution.y);
  	uv -= 0.5;
  	uv /= vec2(iResolution.y / iResolution.x, 1);

    vec3 ro = vec3(14, 0, iTime*20.0);
    ro += 1.2*tunnel(ro);
    vec3 rd = normalize(vec3(uv, 0.3+(sin(0.53*iTime + 60.0)*0.3)));


    vec3 col = march(ro, rd);

    fragColor = vec4(col,1.0);
}

