#define AA  1 

float PrCylDf (vec3 p, float r, float h);
vec2 PixToHex (vec2 p);
vec2 HexToPix (vec2 h);
float Minv3 (vec3 p);
float SmoothMin (float a, float b, float r);
vec2 Rot2D (vec2 q, float a);
vec3 HsvToRgb (vec3 c);
vec2 Hashv2v2 (vec2 p);
float Fbm2 (vec2 p);
vec3 VaryNf (vec3 p, vec3 n, float f);

mat3 flMat;
vec3 ltDir, flPos;
vec2 gId;
float tCur, dstFar, hgSize, wavHt, bRad, nArm, armDir, armFrq, cCol;
int idObj;
bool isOcc;
const float pi = 3.14159, sqrt3 = 1.7320508;

#define DMIN(id) if (d < dMin) { dMin = d;  idObj = id; }

float ObjDf (vec3 p)
{
  vec3 q;
  float dMin, d, bLen, h, r;
  dMin = dstFar;
  p.xz -= HexToPix (gId * hgSize);
  q = p;
  q.xz = Rot2D (q.xz, 2. * pi * (floor (6. * atan (q.z, - q.x) / (2. * pi) + 0.5)) / 6.);
  d = max (abs (q.x) - 0.85 * hgSize, q.y - wavHt + 0.05 * dot (q.xz, q.xz));
  DMIN (1);
  if (isOcc) {
    bLen = 0.5;
    p.y -= wavHt - 0.03;
    q = p;
    r = length (q.xz);
    q.xz = Rot2D (q.xz, armDir * (0.1 * pi * r * (1. + 0.1 * sin (2. * pi * armFrq * r))
       - 0.03 * pi * q.y * (1. + sin (2. * pi * 1.5 * r))));
    q.xz = Rot2D (q.xz, 2. * pi * (floor (nArm * atan (q.z, - q.x) / (2. * pi) + 0.5) / nArm));
    q.x += bLen;
    h = 0.25 * (1. - q.x / bLen);
    h = 0.3 * (1. - 3.2 * h * h);
    q.y -= h + 0.05;
    d = length (max (abs (q) - vec3 (bLen, h, 0.001 * (1. + 12. * q.x) * (4. - 20. * q.y)), 0.)) - 0.05;
    q = p;
    q.y -= 0.35;
    d = SmoothMin (d, PrCylDf (q.xzy, 0.01, 0.35), 0.03);
    q = p;
    q.y -= -1.25;
    d = 0.8 * SmoothMin (d, - SmoothMin (min (bRad - length (q), q.y - 1.25), 2.2 * bLen -
       r, 0.05), 0.05);
    DMIN (2);
  }
  return dMin;
}

void SetGrObjConf ()
{
  vec2 p, u, fRand;
  float emFrac;
  p = HexToPix (gId * hgSize);
  u = mod (0.1 * vec2 (p.x + p.y, p.x - p.y) * (1. + 0.3 * sin (0.2 * 2. * pi * p)) +
    0.1 * tCur, 1.) - 0.5;
  wavHt = 0.6 * dot (exp (-100. * u * u), vec2 (1.));
  emFrac = 0.05;
  fRand = Hashv2v2 (gId * vec2 (37.3, 43.1) + 27.1);
  isOcc = (fRand.y >= emFrac);
  if (isOcc) {
    nArm = 5. + floor (12. * fRand.x);
    armDir = 2. * floor (mod (32. * fRand.x, 2.)) - 1.;
    fRand.y = (fRand.y - emFrac) / (1. - emFrac);
    armFrq = 2. + 6. * fRand.y;
    cCol = fRand.x + fRand.y;
  }
}

float ObjRay (vec3 ro, vec3 rd)
{
  vec3 vri, vf, hv, p;
  vec2 edN[3], pM, gIdP;
  float dHit, d, s, eps;
  eps = 0.0005;
  edN[0] = vec2 (1., 0.);
  edN[1] = 0.5 * vec2 (1., sqrt3);
  edN[2] = 0.5 * vec2 (1., - sqrt3);
  for (int k = 0; k < 3; k ++) edN[k] *= sign (dot (edN[k], rd.xz));
  vri = hgSize / vec3 (dot (rd.xz, edN[0]), dot (rd.xz, edN[1]), dot (rd.xz, edN[2]));
  vf = 0.5 * sqrt3 - vec3 (dot (ro.xz, edN[0]), dot (ro.xz, edN[1]),
     dot (ro.xz, edN[2])) / hgSize;
  pM = HexToPix (PixToHex (ro.xz / hgSize));
  gIdP = vec2 (-99.);
  dHit = 0.;
  for (int j = 0; j < 160; j ++) {
    hv = (vf + vec3 (dot (pM, edN[0]), dot (pM, edN[1]), dot (pM, edN[2]))) * vri;
    s = Minv3 (hv);
    p = ro + dHit * rd;
    gId = PixToHex (p.xz / hgSize);
    if (gId.x != gIdP.x || gId.y != gIdP.y) {
      gIdP = gId;
      SetGrObjConf ();
    }
    d = ObjDf (p);
    if (dHit + d < s) {
      dHit += d;
    } else {
      dHit = s + eps;
      pM += sqrt3 * ((s == hv.x) ? edN[0] : ((s == hv.y) ? edN[1] : edN[2]));
    }
    if (d < eps || dHit > dstFar) break;
  }
  if (d >= eps) dHit = dstFar;
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.001, -0.001);
  v = vec4 (- ObjDf (p + e.xxx), ObjDf (p + e.xyy), ObjDf (p + e.yxy), ObjDf (p + e.yyx));
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

float ObjSShadow (vec3 ro, vec3 rd)
{
  vec3 p;
  vec2 gIdP;
  float sh, d, h;
  sh = 1.;
  d = 0.01;
  gIdP = vec2 (-99.);
  for (int j = 0; j < 30; j ++) {
    p = ro + d * rd;
    gId = PixToHex (p.xz / hgSize);
    if (gId.x != gIdP.x || gId.y != gIdP.y) {
      gIdP = gId;
      SetGrObjConf ();
    }
    h = ObjDf (p);
    sh = min (sh, smoothstep (0., 0.05 * d, h));
    d += 0.05;
    if (sh < 0.05) break;
  }
  return 0.5 + 0.5 * sh;
}

float ObjAO (vec3 ro, vec3 rd)
{
  vec3 p;
  vec2 gIdP;
  float ao, d;
  ao = 0.;
  for (int j = 0; j < 4; j ++) {
    d = 0.01 + float (j) / 20.;
    p = ro + d * rd;
    gId = PixToHex (p.xz / hgSize);
    if (gId.x != gIdP.x || gId.y != gIdP.y) {
      gIdP = gId;
      SetGrObjConf ();
    }
    ao += max (0., d - 3. * ObjDf (p));
  }
  return 0.5 + 0.5 * clamp (1. - 2. * ao, 0., 1.);
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 col4;
  vec3 col, vn, vno, q;
  float dstObj, sh, ao, s;
  bRad = 1.75;
  dstObj = ObjRay (ro, rd);
  if (dstObj < dstFar) {
    ro += dstObj * rd;
    vn = ObjNf (ro);
    vno = vn;
    if (idObj == 1) {
      q = ro;
      q.xz -= HexToPix (gId * hgSize);
      if (vn.y > 0.01) {
        col4 = vec4 (mix (vec3 (0.97, 1., 0.97), vec3 (0.9, 0.9, 0.95),
           smoothstep (0.4, 0.6, Fbm2 (ro.xz))), 0.3);
      } else {
        q.xz = Rot2D (q.xz, 2. * pi * (floor (6. * atan (q.z, - q.x) / (2. * pi) + 0.5)) / 6.);
        col4 = vec4 (0.95, 0.95, 1., 0.3) * (0.8 + 0.2 * cos (9. * 2. * pi * q.z / hgSize));
      }
    } else if (idObj == 2) {
      q = ro;
      q.y -= wavHt;
      s = length (q - vec3 (HexToPix (gId * hgSize), -1.25).xzy) - bRad + 0.01;
      col4 = mix (mix (vec4 (1., 1., 1., 0.2), vec4 (0.3, 0.1, 0., 0.1),
         step (0.7, Fbm2 (16. * ro.xz))), vec4 (HsvToRgb (vec3 (floor (8. * mod (cCol, 1.)) / 8.,
         0.5, 1.)), 0.2), smoothstep (0., 0.02, s));
      s = step (0., s);
      vn = VaryNf ((8. + 56. * s) * q, vn, 4. - 3. * s);
    }
    ao = ObjAO (ro, vno);
    sh = ObjSShadow (ro, ltDir);
    col = col4.rgb * (0.2 + 0.8 * sh * max (dot (vn, ltDir), 0.)) +
       col4.a * step (0.95, sh) * pow (max (dot (normalize (ltDir - rd), vn), 0.), 32.);
    if (sh > 0.5) col *= ao;
  } else col = vec3 (0.6, 0.6, 0.6);
  return clamp (col, 0., 1.);
}

vec3 TrackPath (float t)
{
  return t * vec3 (0.1, 0., sqrt (0.99)) + vec3 (2. * cos (0.1 * t), 0., 0.);
}

void VuPM (float t)
{
  vec3 fpF, fpB, vel, acc, va, ort, cr, sr;
  float dt;
  dt = 1.;
  flPos = TrackPath (t);
  fpF = TrackPath (t + dt);
  fpB = TrackPath (t - dt);
  vel = (fpF - fpB) / (2. * dt);
  vel.y = 0.;
  acc = (fpF - 2. * flPos + fpB) / (dt * dt);
  acc.y = 0.;
  va = cross (acc, vel) / length (vel);
  ort = vec3 (0.2, atan (vel.z, vel.x) - 0.5 * pi, 5. * length (va) * sign (va.y));
  cr = cos (ort);
  sr = sin (ort);
  flMat = mat3 (cr.z, - sr.z, 0., sr.z, cr.z, 0., 0., 0., 1.) *
     mat3 (1., 0., 0., 0., cr.x, - sr.x, 0., sr.x, cr.x) *
     mat3 (cr.y, 0., - sr.y, 0., 1., 0., sr.y, 0., cr.y);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 dateCur;
  vec3 ro, rd, col;
  vec2 canvas, uv, ori, ca, sa;
  float el, az, zmFac, sr;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  dateCur = iDate;
  tCur = mod (tCur, 2400.) + 30. * floor (dateCur.w / 7200.) + 11.1;
  hgSize = 1.6;
  VuPM (1. * tCur);
  az = 0.;
  el = -0.1 * pi;
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  vuMat = mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
          mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
  flPos.y += 10.;
  ro = flPos;
  zmFac = 6. + 3. * sin (0.02 * 2. * pi * tCur);
  dstFar = 100.;
  ltDir = normalize (vec3 (1., 1., -1.));
#if ! AA
  const float naa = 1.;
#else
  const float naa = 3.;
#endif  
  col = vec3 (0.);
  sr = 2. * mod (dot (mod (floor (0.5 * (uv + 1.) * canvas), 2.), vec2 (1.)), 2.) - 1.;
  for (float a = 0.; a < naa; a ++) {
    rd = normalize (vec3 (uv + step (1.5, naa) * Rot2D (vec2 (0.5 / canvas.y, 0.),
       sr * (0.667 * a + 0.5) * pi), zmFac));
    rd = vuMat * rd;
    rd = rd * flMat;
    col += (1. / naa) * ShowScene (ro, rd);
  }
  fragColor = vec4 (col, 1.);
}

float PrCylDf (vec3 p, float r, float h)
{
  return max (length (p.xy) - r, abs (p.z) - h);
}

vec2 PixToHex (vec2 p)
{
  vec3 c, r, dr;
  c.xz = vec2 ((1./sqrt3) * p.x - (1./3.) * p.y, (2./3.) * p.y);
  c.y = - c.x - c.z;
  r = floor (c + 0.5);
  dr = abs (r - c);
  r -= step (dr.yzx, dr) * step (dr.zxy, dr) * dot (r, vec3 (1.));
  return r.xz;
}

vec2 HexToPix (vec2 h)
{
  return vec2 (sqrt3 * (h.x + 0.5 * h.y), (3./2.) * h.y);
}

float Minv3 (vec3 p)
{
  return min (p.x, min (p.y, p.z));
}

float SmoothMin (float a, float b, float r)
{
  float h;
  h = clamp (0.5 + 0.5 * (b - a) / r, 0., 1.);
  return mix (b, a, h) - r * h * (1. - h);
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

vec3 HsvToRgb (vec3 c)
{
  return c.z * mix (vec3 (1.), clamp (abs (fract (c.xxx + vec3 (1., 2./3., 1./3.)) * 6. - 3.) - 1., 0., 1.), c.y);
}

const float cHashM = 43758.54;

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (vec2 (dot (p, cHashVA2), dot (p + vec2 (1., 0.), cHashVA2))) * cHashM);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);  
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float Fbm2 (vec2 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    f += a * Noisefv2 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbmn (vec3 p, vec3 n)
{
  vec3 s;
  float a;
  s = vec3 (0.);
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    s += a * vec3 (Noisefv2 (p.yz), Noisefv2 (p.zx), Noisefv2 (p.xy));
    a *= 0.5;
    p *= 2.;
  }
  return dot (s, abs (n));
}

vec3 VaryNf (vec3 p, vec3 n, float f)
{
  vec3 g;
  vec2 e;
  e = vec2 (0.1, 0.);
  g = vec3 (Fbmn (p + e.xyy, n), Fbmn (p + e.yxy, n), Fbmn (p + e.yyx, n)) - Fbmn (p, n);
  return normalize (n + f * (g - n * dot (n, g)));
}

const int MAX_MARCHING_STEPS = 64;
const float EPSILON = 0.0015;
const float NEAR_CLIP = 0.0;
const float FAR_CLIP = 100.00;
const float PI = 3.14159265359;

vec3 toGamma( vec3 col ){
	return pow( col, vec3(1.0/2.2));
}

float clampeddot(vec3 a, vec3 b){
    return max(0.,dot(a, b));
}

vec3 lightDirection = vec3(1.0, 1.0, 1.0);

vec2 rotate(vec2 pos, float angle){
    float c = cos(angle);
    float s = sin(angle);
    return mat2(c, s, -s, c) * pos;
} 

float sdBox( vec3 p, vec3 b ){
    vec3 d = abs(p) - b;
    return min(max(d.x,max(d.y,d.z)),0.0) + length(max(d,0.0));
}

float sphere(vec3 pos, float radius){
    return length(pos) - radius;
}

vec3 opRep( vec3 p, vec3 c ){
    return mod(p,c)-0.5*c;
}

float map(vec3 pos){
    pos.xy = rotate(pos.xy, sin(iTime) * PI/2. * 0.03);
    float offset = 0.5;
    //float sph = sphere(pos, 1.5);
    // just a sphere
    //return sph;

    // domain repetition, more edges, antialias more visible
    pos = opRep(pos, vec3(offset, offset, offset));

    // test boxes
    return sdBox(pos, vec3(0.1, 0.1, 0.1));

    // test spheres
    //return sphere(pos, .2);
}

vec3 computeNormal(vec3 pos){
    vec2 eps = vec2(0.01, 0.);
    return normalize(vec3(
        map(pos + eps.xyy) - map(pos - eps.xyy),
        map(pos + eps.yxy) - map(pos - eps.yxy),
        map(pos + eps.yyx) - map(pos - eps.yyx)
    ));
}

float diffuse(vec3 normal){
    return clampeddot(normal, lightDirection);
}

float specular(vec3 normal, vec3 dir){
    vec3 h = normalize(normal - dir);
    float specularityCoef = 40.;
    return pow(clampeddot(h, normal), specularityCoef);
}

mat3 setCamera( in vec3 ro, in vec3 ta, float cr ){
    vec3 cw = normalize(ta-ro);
    vec3 cp = vec3(sin(cr), cos(cr),0.0);
    vec3 cu = normalize( cross(cw,cp) );
    vec3 cv = normalize( cross(cu,cw) );
    return mat3( cu, cv, cw );
}

vec3 getColor(vec3 eye, vec3 dir,float dist){
    float ambient = 0.2;
    vec3 red = vec3(1.0, 0.353, 0.208);
    vec3 collision = (eye += (dist*0.995) * dir );
    vec3 normal = computeNormal(collision);
    float diffLight = diffuse(normal) * ambient + ambient;
    float specLight = specular(normal, dir);

    return (diffLight + specLight ) * red;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = 2.0 * fragCoord / iResolution.xy - 1.0;
    uv.x *= iResolution.x / iResolution.y;

    vec3 eye = vec3(3.5, 3.0, -1.0);
    vec3 ta = vec3( -0.5, -0.9, 0.5 );
    mat3 camera = setCamera( eye, ta, 1.0 );
    float fov = 2.5;
    vec3 dir = camera * normalize(vec3(uv, fov));

    // bg color
    vec3 finalColor = vec3(0.086, 0.19, 0.8);
    // t is the position of the center of the sphere along the ray.
    float t = NEAR_CLIP;
    float dist = EPSILON;
    float zoom = 1.5;

    // this is the radius of the sphere that models the cone.
    // formula from https://www.scratchapixel.com/lessons/advanced-rendering/rendering-distance-fields/basic-sphere-tracer
    float vfov = 0.9; //vfov is the vertical camera field-of-view (in radians)
    float radius = (2.0*tan(vfov/2.0)) /(iResolution.y*zoom);

    // it can also be simplyfied using a constant, like in via https://www.shadertoy.com/view/MsBGWm
    // float radius = .7071/(u_resolution.y*zoom);

    // color accumulation variable. This is used to accumulate the colors sampled for all
    // the intersection between the cone and the scene, and to accumulate the coverage
    // on the alfa channel
    vec4 acc = vec4(0, 0, 0, 1);

    for (int i = 0; i < MAX_MARCHING_STEPS; i++) {
        float rad = t*radius;

        // move the center of the sphere along the ray
        dist = map(eye + t * dir);


        // if the distance from the scene is smaller than the radius,
        // it means that there is an intersection between the cone and the scene.
        // we accumulate and blend the colors sampled for those intersections into the acc variable.
        if (dist < rad) {
            // how much of the surface of the projected pixel is covering the scene?
            // this is what the coverage value stands for.

            // This is the easiest coverage calculation that I have found:
            // float alpha = smoothstep(rad, -rad, dist);
            // https://www.shadertoy.com/view/4scBW8
            float alpha = smoothstep(rad, -rad, dist);

            // Another one is to get an estimation of the coverage of the surface by using it's distance
            // over the projected aperture of the cone. 
            // coverage = dist / rad;
            // of course the blending algorithm have to be adapted. see https://www.shadertoy.com/view/MsBGWm

            // then sample the color at the given distance.
            // I know, I could save the allocation of this variable, but, for the sake of clarity ...
            vec3 sampleCol = getColor(eye, dir, t);

            // the new color is calculated multiplying
            // the previous alpha for the new color times the new alpha
            acc.rgb += acc.a * (alpha*sampleCol.rgb);
            // here we save the new alpha
            acc.a *= (1.0 - alpha);

            // when acc.a is really small, it means that we hit the object, brake the loop
            if (acc.a < EPSILON ) break;   
        }


        // move the center of the sphere forward along the ray. The smaller
        // the steps the higher the precision and the slower the computation.
        t += max(abs(dist*0.85), 0.001); //https://www.shadertoy.com/view/4dlyWB
        // t += max( dist, rad*.5 ); // https://www.shadertoy.com/view/MsBGWm

        // brake if we are going too far away
        if (t > FAR_CLIP) break;
    }

    finalColor = mix(acc.rgb, finalColor, acc.a);
    fragColor = vec4(clamp(toGamma(finalColor),0.0,1.0),1.0);
}

vec2 complexMult(vec2 a, vec2 b) {
	return vec2(a.x*b.x - a.y*b.y, a.x*b.y + a.y*b.x);
}

vec3 complexMult3(vec3 a, vec3 b) {
	return vec3(a.x*b.x - a.y*b.y - a.z*b.z, a.x*b.y + a.y*b.x, a.x*b.z + a.z*b.x);
}

float testMandelbrotDist(vec3 coord3d) {
    // turn this up to reduce visual noise
    const int iterations = 92;
	vec3 testPoint = vec3(0,0,0);
    vec3 distTest = vec3(0,0,0);
	for (int i = 0; i < iterations; i++){
        distTest = 2.0*complexMult3(testPoint, distTest) + vec3(1,0,0);
		testPoint = complexMult3(testPoint,testPoint) + coord3d;
        float ndot = dot(testPoint,testPoint);
		if (ndot > 1024.0) {
            return 0.5 * sqrt(ndot/dot(distTest,distTest))*log(ndot);
		}
	}
	return -0.1;
}

mat3 rotmat(vec3 axis, float angle) {
	float s = sin(angle);
	float c = cos(angle);
	float oc = 1.0 - c;
	return mat3(oc * axis.x * axis.x + c, oc * axis.x * axis.y - axis.z * s,  oc * axis.z * axis.x + axis.y * s, 
	oc * axis.x * axis.y + axis.z * s,  oc * axis.y * axis.y + c,           oc * axis.y * axis.z - axis.x * s, 
	oc * axis.z * axis.x - axis.y * s,  oc * axis.y * axis.z + axis.x * s,  oc * axis.z * axis.z + c);
}

vec3 getRay(vec2 uv) {
    uv = (0.1 + 0.6*abs(sin(iTime*0.1)))*(uv * 2.0 - 1.0) * vec2(1.0, iResolution.y / iResolution.x);
	vec3 proj = normalize(vec3(uv.x, uv.y, 1.0));
    vec2 coord = vec2(6.0 * iMouse.x/ iResolution.x, 3.0 * (0.5+ iMouse.y/iResolution.y));
    if (iResolution.x < 500.0) {
        coord = vec2(-4.33,-3.0);
    }
	return rotmat(
        vec3(0.0, 1.0, 0.0),
        coord.x
    ) * rotmat(
        vec3(1.0, 0.0, 0.0), 
        coord.y 
    ) * proj;
}
const float minDist = 0.5;
const float maxDist = 4.5;
float rayMarchMandelbrot(vec3 eye, vec3 ray) {
    float iterDval = 0.01;
    float distance = minDist;
    const int limit = int(maxDist/0.01);
    for (int i = 0; i < limit; i++) {
     	float testval =  testMandelbrotDist(eye + ray*distance);
        if (iterDval < 0.001 || abs(testval) < 0.000001) {
         	return distance;   
        }
        if (testval > 0.0) {
         	distance = distance + iterDval;   
        } else {
            iterDval = iterDval * 0.5;
            distance = distance - iterDval;
        }
    }
    return distance;
}


vec3 estimateNormal(vec3 p) {
    const float es = 0.02;
    return normalize(vec3(
        testMandelbrotDist(vec3(p.x + es, p.y, p.z)) - testMandelbrotDist(vec3(p.x - es, p.y, p.z)),
        testMandelbrotDist(vec3(p.x, p.y + es, p.z)) - testMandelbrotDist(vec3(p.x, p.y - es, p.z)),
        testMandelbrotDist(vec3(p.x, p.y, p.z  + es)) - testMandelbrotDist(vec3(p.x, p.y, p.z - es))
    ));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 fragment = fragCoord/iResolution.xy;
    vec3 ray = getRay(fragment);
	vec3 camPos = getRay(vec2(0.5,0.5)) * -3.0;
    float dist = rayMarchMandelbrot(camPos, ray);
    if (dist > minDist && dist < maxDist){
        vec3 lightpos = vec3(-.5,-1,.5);
        vec3 lightCol = vec3(.98,.4,.5);
        vec3 normal = estimateNormal(camPos + ray * dist);
        float intensity = dot(lightpos, normal);        
    	fragColor = vec4(lightCol * intensity, 1.0);
    } else {
        fragColor = vec4(vec3(0.3*(dist)), 1.0);
    }
}

const float xscale = .25;
const float yscale = .2;
const float xspeed = 1.0;
const float timeMult = .17;
const float sluggishness = 10.;
const float camShakiness = 0.05;
const float camZoom = 0.1;

//---------------------------------------------

const float PI = atan(1.)*4.;
const float PI2 = PI*2.;

vec3 hash32(vec2 p) {
	vec3 p3 = fract(vec3(p.xyx) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yxz+19.19);
    return fract((p3.xxy+p3.yzz)*p3.zyx);
}

float dtoa(float d, float amount){
    return 1. / clamp(d*amount, 1., amount);
}
float sdSquare(vec2 p, vec2 pos, vec2 origin, float a, float s) {
	vec2 d = abs(((p-pos) * mat2(cos(a), sin(a), -sin(a), cos(a))) - origin) - s;
	return min(max(d.x, d.y), 0.0) + length(max(d, 0.0));
}

// returns { x:orig, y:height, z:position 0-1 within this cell }
vec3 cell(float x) {
    float pos = fract(x/xscale);
    float orig = (x/xscale-pos)*xscale;
    float h = hash32(vec2(orig)).r;
    return vec3(orig, h * yscale, pos);
}

void scene(out vec4 o, vec2 N, vec2 uv, float t, float xpos, float xcam) {
    vec3 sqCellPrev = cell(xpos-xscale);

    // ground
    vec3 bigCellThis = cell(uv.x);
    vec3 bigCellNext = cell(uv.x + xscale);
    float bigHeightThis = mix(bigCellThis.y, bigCellNext.y, bigCellThis.z);// terrain height
    float sd = uv.y - bigHeightThis;

    // walking square; interpolate between positions
    float sdsq = 1e7;
    vec3 sqCellThis = cell(xpos);
    vec3 sqCellNext = cell(xpos+xscale);
    float aThis = atan(sqCellPrev.y - sqCellThis.y, sqCellPrev.x - sqCellThis.x);
    float aNext = atan(sqCellThis.y - sqCellNext.y, sqCellThis.x - sqCellNext.x) - PI*.5;
    if(aNext > aThis+PI) aNext -= PI2;
    if(aNext < aThis-PI) aNext += PI2;

    float szThis = distance(sqCellPrev.xy, sqCellThis.xy);
    float szNext = distance(sqCellThis.xy, sqCellNext.xy);

    float asq = mix(aThis, aNext, pow(sqCellNext.z, sluggishness));
    float sz = mix(szThis, szNext, pow(sqCellNext.z, sluggishness));
    sdsq = sdSquare(uv,
                                  vec2(sqCellThis.x, sqCellThis.y),
                                  vec2(-sz*.5,sz*.5), asq+PI, sz*.5);

    // parallax bg
    o=vec4(0);
    vec2 uvtemp =uv;
    for (float i = 1.; i <= 9.; ++i) {
        uvtemp.x -= xpos;
        uvtemp*=vec2(2.,1.8);
        //uvtemp *= 2.;
        uvtemp.y -= .3;
        uvtemp.x += xpos + 1e3;
        vec3 cellThis = cell(uvtemp.x);
        vec3 cellNext = cell(uvtemp.x + xscale);
        float heightThis = mix(cellThis.y, cellNext.y, cellThis.z);
        float sd = uvtemp.y - heightThis;
        o.rgb = max(o.rgb, dtoa(sd,1e4)*.2/i);

        float amt = 25.+heightThis*500.;
        //amt = 1000./((uvtemp.y)*100.);
      	o.rgb = max(o.rgb, dtoa(abs(sd)+.01,amt)*.4/i);
    }
    o +=vec4(.8-uv.y*1.1);
    o.g *= o.r;
    o.r *=.6;
    o.b += N.y;

    // square
    float alphasq = dtoa(sdsq, 1000.);
    o.rgb = mix(o.rgb, vec3(.9,.1,.1), alphasq);

    // ground
    float alphagr = dtoa(sd, 300.);
    o.rgb = mix(o.rgb, vec3(1), alphagr);

    // snow
    float amt = 25.+bigHeightThis*500.;
    alphagr = dtoa(abs(sd+.01), amt);
    o.rgb = mix(o.rgb, vec3(.8,.9,1), alphagr);
}

void mainImage( out vec4 o, vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy-.5;
    vec2 N = uv;
    uv.x *= iResolution.x / iResolution.y;
    float t = iTime*timeMult;
    float xpos = t*xspeed;
    float xcam = sin(t)*sin(t*9.)*camShakiness;// shaky cam
    uv *=.7+sin(t*2.)*camZoom;//zoomy cam
    uv.x += xpos + xcam;
	uv.y += .3;

    // calc scene twice & motion blur
    vec3 sqCellPrev = cell(xpos-xscale);
    float bounce = abs(sin(sqCellPrev.z*26.))*pow(1.-sqCellPrev.z, 7.) * .03;
    uv.y += bounce;
    scene(o, N, uv, t, xpos, xcam);
    uv.y -= bounce;
    vec4 o2;
    scene(o2, N, uv, t, xpos, xcam);
    o = mix(o2, o, .5);

    o.b *= .9;
    o.rgb += (hash32(fragCoord+iTime)-.5)*.08;
    o *= 1.1-dot(N,N);
    o=clamp(o,0.,1.);
    o.a = 1.;
}

