#define PI					3.14

#define saturate(x) 		clamp(x, 0.0, 1.0)
#define dot2(x) 			dot(x, x)

#define MENGER_ITERATIONS	2
#define SOFT_SHADOW_STEPS 	16

#define INTERSECT_STEPS		500
#define INTERSECT_MIN_DIST	0.0001
#define INTERSECT_MAX_DIST	100.0


vec2 rotate2D(vec2 p, float angle)
{
    float sRot = sin(angle);
    float cRot = cos(angle);
    return p * cRot + p.yx * sRot * vec2(-1.0, 1.0);
}

float sdPlane(vec3 p, float height)
{
   	return p.y - height;
}

//https://www.shadertoy.com/view/ltfSWn
float mandelbulb(vec3 p)
{
    vec3 w = p;
    float m = dot2(w);
    float dz = 1.0;
    for(int i = 0; i < 3; ++i)
    {
		dz = 8.0 * pow(sqrt(m), 7.0) * dz + 1.0;
        float r = length(w);
        float b = 8.0 * acos(w.y / r);
        float a = 8.0 * atan(w.x, w.z);
        w = p + pow(r, 8.0) * vec3(sin(b) * sin(a), cos(b), sin(b) * cos(a));

        m = dot(w,w);
		if (m > 255.0)
		{
            break;
		}
    }	
    return 0.1 * log(m) * sqrt(m) / dz;
}

vec4 opU(vec4 d1, vec4 d2)
{
     return (d1.x < d2.x) ? d1 : d2;
}

vec4 map(in vec3 p)
{	
    float ground = sdPlane(p, 1.8);

    p.xz = mod(p.xz + 5.0, 2.0) -1.0;
    p.y = mod(p.y + 1.0, 2.0) - 1.0;

    float d = mandelbulb(p - vec3(0.0, 0.5, 0.0));
    vec4 res = vec4(d, 1.0, 0.0, 2.0);

    //......................................................................
    //  https://www.iquilezles.org/www/articles/menger/menger.htm
    //......................................................................
    float s = 1.0;
    for(int i = 0; i < MENGER_ITERATIONS; ++i)
    { 
        vec3 a = mod(p * s, 2.0) - 1.0;
        s *= 3.0;
        vec3 r = abs(1.0 - 3.0 * abs(a));
        float da = max(r.x, r.y);
        float db = max(r.y, r.z);
        float dc = max(r.z, r.x);
        float c = (min(da, min(db, dc)) - 3.0) / s;

        if(c > d)
        {
            d = c;
            res = vec4(d, min(res.y, 0.2 * da * db * dc), 0.0, 1.0);
        }
    }
    //......................................................................

    return opU(res, vec4(ground, 1.0, 0.0, 1.0));
}

vec4 intersect(in vec3 ro, in vec3 rd)
{
    float t = 0.0;
    vec4 res = vec4(-1.0);
    vec4 h = vec4(1.0);
    for (int i = 0; i < INTERSECT_STEPS; i++ )
    {
		if(h.x < INTERSECT_MIN_DIST || t > INTERSECT_MAX_DIST) 
		{
	    	break;
		}

        h = map(ro + rd * t);
        res = vec4(t, h.yzw);
        t += h.x;
    }

    if (t > INTERSECT_MAX_DIST) 
    {
		res = vec4(-1.0);
    }

    return res;
}

float softshadow(in vec3 ro, in vec3 rd)
{
    float res = 1.0;
    float t = 0.0;
    for (int i = 0; i < SOFT_SHADOW_STEPS; ++i)
    {
		vec3 pos = ro + rd * t;
        float h = map(pos).x;
        res = min(res, float(SOFT_SHADOW_STEPS) * h / t);
        if(res < 0.0001)
		{
	    	break;
		}
        t += clamp(h, 0.01, 0.2);
    }
    return saturate(res);
}

vec3 calcNormal(in vec3 pos)
{
    vec3 eps = vec3(0.0001, 0.0, 0.0);
    vec3 n;
    n.x = map(pos + eps.xyy).x - map(pos - eps.xyy).x;
    n.y = map(pos + eps.yxy).x - map(pos - eps.yxy).x;
    n.z = map(pos + eps.yyx).x - map(pos - eps.yyx).x;
    return normalize(n);
}

float softshadow(in vec3 ro, in vec3 rd, in float k)
{
    float res = 1.0;
    float t = 0.0;
    for (int i = 0; i < SOFT_SHADOW_STEPS; ++i)
    {
		vec3 pos = ro + rd * t;
        float h = map(pos).y;
        res = min(res, k * h / t);
        if(res < 0.0001)
		{
	    	break;
    	}
        t += clamp(h, 0.01, 0.2);
    }
    return saturate(res);
}

vec3 GetMaterialColor(in vec3 p, float materialID)
{
    if (materialID < 1.5)
    {
         vec2 uv = p.xz;
         vec3 texRGB = texture(iChannel0, uv, 0.0).rgb;
         return texRGB * 0.2 + vec3(0.0, 0.015, 0.0);
    }
    else if (materialID < 2.5)
    {
         vec2 uv = p.xy;
         vec3 texRGB = texture(iChannel1, uv, 0.0).rgb;
         return texRGB * 0.5;
    }
    return vec3(0.0);
}

vec3 render(in vec3 ro, in vec3 rd)
{
    vec3 color = vec3(0.0);
    vec4 res = intersect(ro,rd);
    if(res.x > 0.0)
    {
        const vec3 light1 = vec3(0.0, 0.0, -0.2);

        vec3 pos = ro + res.x * rd;

        vec3 normal = calcNormal(pos);
        vec3 reflection = reflect(rd, normal);

        float occ = res.y;
        float shadow1 = softshadow(pos + 0.01 * normal, light1);

        vec3 baseColor = GetMaterialColor(pos, res.w);
        vec3 ambient = vec3(0.2) * baseColor;      

        vec3 diffuse = baseColor * shadow1 * occ;
        color = diffuse + (ambient * occ);
    }

    return pow(color, vec3(0.4545));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) 
{
    vec2 p = 2.0 * (fragCoord.xy / iResolution.xy) - 1.0;
    p.x *= iResolution.x / iResolution.y;

    vec2 mouseXY = iMouse.xy / iResolution.xy;

    // camera
    vec3 ro = vec3(15.0, 2.9, -2.0);
    vec3 ww = normalize(vec3(0.0, mouseXY.y * 5.0 * PI, 0.0) - ro); 
    ww.xz = rotate2D(ww.xz, iMouse.x * 2.0 * PI);
    vec3 uu = normalize(cross(vec3(0.0, 1.0, 0.0), ww));
    vec3 vv = normalize(cross(ww, uu));
    vec3 rd = normalize(p.x * uu + p.y * vv + 2.0 * ww);
    rd.xz = rotate2D(rd.xz, PI * 2.55);

    ro.z -= iTime * 0.1; //Camera movement

    vec3 color = vec3(0.2);
    color -= vec3(0.3, 0.3, 0.9);
 	color += render(ro + vec3(0.0, -1.0, 0.0), rd);

    fragColor = vec4(color, 1.0);
}

 float disk(float x, float y, float r) {
    return 1. - step(r * r, x * x + y * y);
 }

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy - 0.5;

    float x = uv.x + 0.1 * sin(2.0 * iTime);
    float y = uv.y + 0.1 * sin(2.1 * iTime);
    float r = 0.2    + 0.1 * sin(2.2 * iTime);

    float c = disk(x, y, r);  
   	float c1 = disk(x+.2, y+.5, r); 

    vec3 color = vec3(c, c, 0.); 

	color += vec3(c1, c1, 0.);


    // Output to screen
    fragColor = vec4(sqrt(color),1.0);
}

