mat4  box_world_to_obj;
mat4  box_obj_to_world;
vec3  box_size;
float box_radius;

vec2 intersect( in vec3 ro, in vec3 rd )
{
    vec2 res = vec2(1e20,-1.0);

    // plane
    {
    float t = (-1.0-ro.y)/rd.y;
    if( t>0.0 ) res = vec2(t,1.0);
    }

    // rounded box
    {
    // convert ray from world to box space
    vec3 rdd = ntransform(box_world_to_obj, rd );
    vec3 roo = ptransform(box_world_to_obj, ro );
    // intersect in box space
    float t = roundedboxIntersect(roo,rdd,box_size,box_radius);
    if( t>0.0 && t<res.x ) res = vec2(t,2.0);
    }

    return res;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // rounded box animation
    box_obj_to_world = 
                       rotate( normalize(vec3(1.0,1.0,0.1)), iTime ); 
    box_world_to_obj = inverse( box_obj_to_world );
    box_size = vec3(0.5,0.4,0.3);
    box_radius = 0.2;

    // render
    vec3 tot = vec3(0.0);

    #if AA>1
    for( int m=0; m<AA; m++ )
    for( int n=0; n<AA; n++ )
    {
        // pixel coordinates
        vec2 o = vec2(float(m),float(n)) / float(AA) - 0.5;
        vec2 p = (2.0*(fragCoord+o)-iResolution.xy)/iResolution.y;
        #else    
        vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.y;
        #endif

	    vec3 ro = vec3(0.0, 0.0, 2.0 );
	    vec3 rd = normalize( vec3(p,-1.5) );

        // sky
        vec3 col = vec3(0.08)*(1.0-0.3*length(p)) + 0.02*rd.y;

        // raymarch geometry
        vec2 tm = intersect( ro, rd );
        if( tm.y>0.0 )
        {
            // shading
            float t = tm.x;
            vec3 pos = ro + t*rd;

            const vec3 lig = normalize(vec3(0.8,0.4,-0.6));

            if( tm.y<1.5 ) // floor
            {
                vec3 nor = vec3(0.0,1.0,0.0);
                float sha = step( intersect( pos+0.01*nor, lig ).y, 0.0 );
                col = mix( col*3.0*(vec3(0.2,0.3,0.4)+vec3(0.8,0.7,0.6)*sha), 
                       col, 1.0-exp(-0.02*t) );
            }
            else // rounded box
            {
                // convert position from world to box space
                vec3 bpos = ptransform(box_world_to_obj,pos);
                // compute normal in box space
                vec3 bnor = roundedboxNormal(bpos,box_size,box_radius);
                // convert normal from box to world space
                vec3 nor = ntransform(box_obj_to_world,bnor);

                vec3 lig = normalize(vec3(0.7,0.6,0.3));
                vec3 hal = normalize(-rd+lig);
                float dif = clamp( dot(nor,lig), 0.0, 1.0 );
                float amb = clamp( 0.5 + 0.5*dot(nor,vec3(0.0,1.0,0.0)), 0.0, 1.0 );

                const float fr = 3.14159*7.5;

                col = vec3(0.5);

                col += 0.4*smoothstep(-0.01,0.01,cos(bpos.x*fr*0.5)*cos(bpos.y*fr*0.5)*cos(bpos.z*fr*0.5)); 
                col *= 1.0*smoothstep(-1.0,-0.98,cos(bpos.x*fr))
                          *smoothstep(-1.0,-0.98,cos(bpos.y*fr))
                          *smoothstep(-1.0,-0.98,cos(bpos.z*fr));

                col *= vec3(0.2,0.3,0.4)*amb + vec3(1.0,0.9,0.7)*dif;

                col += 0.4*pow(clamp(dot(hal,nor),0.0,1.0),12.0)*dif;            }
        }

	    tot += col;
    #if AA>1
    }
    tot /= float(AA*AA);
    #endif

    // gamma
    tot = pow( tot, vec3(0.45) );

    // dither to remove banding in the background
    tot += fract(sin(fragCoord.x*vec3(13,1,11)+fragCoord.y*vec3(1,7,5))*158.391832)/255.0;

    fragColor = vec4( tot, 1.0 );
}

// All the distance functions from:http://iquilezles.org/www/articles/distfunctions/distfunctions.htm
#define rot(a) mat2(cos(a),-sin(a),sin(a),cos(a))
#define matRotateX(rad) mat3(1,0,0,0,cos(rad),-sin(rad),0,sin(rad),cos(rad))
#define matRotateY(rad) mat3(cos(rad),0,-sin(rad),0,1,0,sin(rad),0,cos(rad))
#define matRotateZ(rad) mat3(cos(rad),-sin(rad),0,sin(rad),cos(rad),0,0,0,1)
#define hash(h) fract(sin(h) * 43758.5453123)
#define PI 3.141592653589793

// noise and fbm function from https://gist.github.com/patriciogonzalezvivo/670c22f3966e662d2f83
//-----------------------------------------------------------------------------
mat3 m = mat3( 0.00,  0.80,  0.60,
              -0.80,  0.36, -0.48,
              -0.60, -0.48,  0.64 );

float noise( in vec3 x )
{
    vec3 p = floor(x);
    vec3 f = fract(x);

    f = f*f*(3.0-2.0*f);

    float n = p.x + p.y*57.0 + 113.0*p.z;

    float res = mix(mix(mix( hash(n+  0.0), hash(n+  1.0),f.x),
                        mix( hash(n+ 57.0), hash(n+ 58.0),f.x),f.y),
                    mix(mix( hash(n+113.0), hash(n+114.0),f.x),
                        mix( hash(n+170.0), hash(n+171.0),f.x),f.y),f.z);
    return res;
}

float fbm( vec3 p )
{
    float f;
    f  = 0.5000*noise( p ); p = m*p*2.02;
    f += 0.2500*noise( p ); p = m*p*2.03;
    f += 0.1250*noise( p );
    return f;
}

float sdBox( vec3 p, vec3 b, float r )
{
  vec3 d = abs(p) - b;
  return length(max(d,0.0))-r
         + min(max(d.x,max(d.y,d.z)),0.0);
}

float sdCappedCylinder( vec3 p, float h, float r )
{
  vec2 d = abs(vec2(length(p.xz),p.y)) - vec2(h,r);
  return min(max(d.x,d.y),0.0) + length(max(d,0.0));
}

float sdTriPrism( vec3 p, vec2 h )
{
    vec3 q = abs(p);
    return max(q.z-h.y,max(q.x*0.866025+p.y*0.5,-p.y)-h.x*0.5);
}

float sdTapeGear( vec3 p, vec2 h )
{
    float r = (length(p)-0.23);
    float w = 0.05;
    float h2 = 0.1;
  	vec2 d = abs(vec2(length(p.xz),p.y)) - h;
    float len = 0.38;
	float resd = min(max(d.x,d.y),0.0) + length(max(d,0.0));
    float res2d = max(-r,resd);
    float b = sdBox(p*matRotateY(radians(-10.0)),vec3(w,h2,len),0.0);
    float b2 = sdBox(p*matRotateY(radians(25.0)),vec3(len,h2,w),0.0);
    float b3 = sdBox(p*matRotateY(radians(-50.0)),vec3(len,h2,w),0.0);


    float res3d = max(-b,res2d);
    float res4d = max(-r,min(min(b,b2),b3));
  	return min(res3d,res4d);
}

vec4 combine(vec4 val1, vec4 val2 ){
    if ( val1.w < val2.w ) {
        return val1;
    }
    return val2;
}

vec4 map(vec3 p){    
    vec3 pref = p;
    vec2 uv = p.xy;

    float b0 = sdBox((p+vec3(0.0, 0.0,0.0)),vec3(2.0,1.2,0.2),0.1);
    float b1 = sdBox((p+vec3(0.0, -0.2,0.32)),vec3(1.8,0.7,0.03),0.1);

    p.x = abs(p.x);
    p.x -= 2.0;
    float b2 = sdTriPrism((p+vec3(0.0, 0.95,0.0))*matRotateZ(radians(-50.0)),vec2(1.0,0.6));
    p = pref;
    float b3 = sdBox((p+vec3(0.0, 0.92,0.0)),vec3(1.6,0.35,0.35),0.0);
    float b4 = sdBox((p+vec3(0.0, -0.2,-0.4)),vec3(1.8,0.7,0.03),0.1);

    p.x = abs(p.x);
    p.x -= 1.1;
    float hole0 = sdCappedCylinder((p+vec3(0.0, 1.1,0.0))*matRotateX(radians(90.0)),0.08,1.0);
    p = pref;

    p.x = abs(p.x);
    p.x -= 0.7;
    float hole1 = sdCappedCylinder((p+vec3(0.0, 0.95,0.0))*matRotateX(radians(90.0)),0.08,1.0);
    p = pref;

    float holes = min(hole0,hole1);

    float centerRectHole =  sdBox((p+vec3(0.0, -0.15,0.0)),vec3(0.4,0.2,0.3),0.05);

    p.x = abs(p.x);
    p.x -= 0.8;
    float centerHoles = sdCappedCylinder((p+vec3(0.0, -0.15,0.0))*matRotateX(radians(90.0)),0.27,1.0);
    p = pref;

   	p.x = abs(p.x);
    p.x -= 0.8;
    float tapeCircles = sdCappedCylinder((p+vec3(0.0, -0.15,0.0))*matRotateX(radians(90.0)),0.65,0.07);
    p = pref;

    float tape = max(-centerHoles,tapeCircles);

    p.x = abs(p.x);
    p.x -= 0.8;
    float tapeGear = sdTapeGear((p+vec3(0.0, -0.15,0.0))*matRotateX(radians(90.0))*matRotateY(radians(90.0)*iTime*0.5), vec2(0.1,0.1));
    p = pref;

    p.x += fbm(p*1.8)*1.2;
    p.y += fbm(p*1.2)*1.3;
    p.z += fbm(p*2.0)*1.1;
   	float fragileParts0 = (sdBox(p+vec3(1.8,0.8,-0.2),vec3(0.1,0.2,0.5),0.0)-1.0);
    p = pref;

    p.x += fbm(p*1.8)*1.2;
    p.y += fbm(p*1.2)*1.3;
    p.z += fbm(p*2.0)*1.1;
   	float fragileParts1 = (sdBox(p+vec3(-3.2,-1.7,-0.2),vec3(0.3,0.6,0.5),0.0)-1.0);
    p = pref;

    float f = p.y+1.3;

    float resB = max(-centerHoles,max(-centerRectHole, max(-holes,min(max(-b4,max(-b1,b0)),max(-b2,b3)))));
    vec4 res = vec4(vec3(1.0,1.0,1.0),min(f,max(-fragileParts1,max(-fragileParts0,min(min(resB,tape),tapeGear)))));

    return res;
}

vec3 normalMap(vec3 p){
    float d = 0.0001;
    return normalize(vec3(
        map(p + vec3(  d, 0.0, 0.0)).w - map(p + vec3( -d, 0.0, 0.0)).w,
        map(p + vec3(0.0,   d, 0.0)).w - map(p + vec3(0.0,  -d, 0.0)).w,
        map(p + vec3(0.0, 0.0,   d)).w - map(p + vec3(0.0, 0.0,  -d)).w
    ));
}

float shadowMap(vec3 ro, vec3 rd){
    float h = 0.0;
    float c = 0.001;
    float r = 1.0;
    float shadow = 0.5;
    for(float t = 0.0; t < 30.0; t++){
        h = map(ro + rd * c).w;
        if(h < 0.001){
            return shadow;
        }
        r = min(r, h * 16.0 / c);
        c += h;
    }
    return 1.0 - shadow + r * shadow;
}

// from simon green and others
float ambientOcclusion(vec3 p, vec3 n)
{
    const int steps = 4;
    const float delta = 0.15;

    float a = 0.0;
    float weight = 4.;
    for(int i=1; i<=steps; i++) {
        float d = (float(i) / float(steps)) * delta; 
        a += weight*(d - map(p + n*d).w);
        weight *= 0.5;
    }
    return clamp(1.0 - a, 0.0, 1.0);
}

mat3 setCamera( in vec3 ro, in vec3 ta, float cr )
{
    vec3 cw = normalize(ta-ro);
    vec3 cp = vec3(sin(cr), cos(cr),0.0);
    vec3 cu = normalize( cross(cw,cp) );
    vec3 cv =          ( cross(cu,cw) );
    return mat3( cu, cv, cw );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 p = (fragCoord.xy * 2.0 - iResolution.xy) / min(iResolution.x, iResolution.y);
    vec2 uv = p;

    float time = iTime*2.0;

    vec3 ro = vec3( 3.5*cos(0.1*time + 6.0), 0.0, -0.5+5.5*sin(0.1*time + 6.0) );
    vec3 ta = vec3( 0.0, -0.4, -0.7 );
    mat3 ca = setCamera( ro, ta, 0.0 );
    float zoom = 1.5;
    vec3 rd = ca * normalize( vec3(p.xy,zoom) );

    float t, dist;
    float lastDistEval = 1e10;
    float edge = 0.0;
    t = 0.0;
    vec3 distPos = ro+rd;
    vec4 distCl = vec4(0.0);
    for(int i = 0; i < 64; i++){
        distCl = map(distPos);
        dist = distCl.w;
        t += dist;
        distPos = ro+rd*t;

        if (dist < lastDistEval) lastDistEval = dist;
        if(dist < 0.01 || dist > 60.0) break;
    }

    vec3 color;
    float shadow = 1.0;
    if(dist < 1.0){
        // lighting
        vec3 lightDir = vec3(0.0, 1.0, 0.0);
        vec3 light = normalize(lightDir + vec3(0.5, 0.0, 0.9));
        vec3 normal = normalMap(distPos);

        // difuse color
        float diffuse = clamp(dot(light, normal), 0.6, 1.0);
        float lambert = max(.0, dot( normal, light));

        // ambient occlusion
        float ao = ambientOcclusion(distPos,normal);

        // shadow
        shadow = shadowMap(distPos + normal * 0.001, light);

        // result
        color += vec3(lambert);
        color = ao*diffuse*(distCl.xyz+(.1-length(p.xy)/3.))*vec3(1.0, 1.0, 1.0);

    }else{
        color =.84*max(mix(vec3(0.6,0.6,0.6)+(.1-length(p.xy)/3.),vec3(1),.1),0.);
    }

    // rendering result
    float brightness = 1.5;
    vec3 dst = (color * max(0.8, shadow))*brightness;

    fragColor = vec4(dst, 1.0);
}

#define INF 10000.
#define PI 3.141592

vec3 cameraPos = vec3(0., 0., 10.);
vec3 lightDir = normalize(vec3(1., 1., 1.));
float eps = 0.001;

vec3[2] sphere_center = vec3[](
	vec3(-0.5, 0., 0.),
    vec3(0.5, 0., 0.)
);
float[2] sphere_radius = float[](
    0.5,
    0.5
);
vec3[1] box_center = vec3[](
	vec3(0., 0., 0.)
);
vec3[1] box_size = vec3[](
	vec3(0.5, 0.5, 0.5)
);

mat3 rotateMatX(float angle) {
	return mat3(
        1, 0, 0, 
        0, cos(angle), -sin(angle),
        0, sin(angle), cos(angle)
    );
}

mat3 rotateMatY(float angle) {
	return mat3(
        cos(angle), 0, -sin(angle),
        0, 1, 0,
        sin(angle), 0, cos(angle)
    );
}

mat3 rotateMatZ(float angle) {
	return mat3(
        cos(angle), -sin(angle), 0,
        sin(angle), cos(angle), 0,
        0, 0, 1
    );
}


float distSphere(vec3 r, vec3 center, float radius) {
	return length(r-center) - radius;
}

float distBox(vec3 r, vec3 center, vec3 size) {
	return length(max(abs(r - center) - size, 0.0));
}

float distFunc(vec3 r) {
    float min_dist = INF;
    for (int i = 0; i < 1; i++) {
    	float dist = distBox(r, box_center[i], box_size[i]);
        if (dist < min_dist) {
        	min_dist = dist;
        }
    }
    return min_dist;
}

vec3 normalFunc(vec3 r) { //get Normal vector
	return normalize(vec3(
    	distFunc(r) - distFunc(vec3(r.x+eps, r.y, r.z)),
        distFunc(r) - distFunc(vec3(r.x, r.y+eps, r.z)),
        distFunc(r) - distFunc(vec3(r.x+eps, r.y, r.z+eps))
    ));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (2.0*fragCoord - iResolution.xy)/iResolution.y;
    // Time varying pixel color
    vec3 col;

    vec3 ray = normalize(vec3(uv, 0.) - cameraPos);
    vec3 cur = cameraPos;

    float t = iTime;
    float angle1 = t*2.*PI/5.0;
    float angle2 = t*2.*PI/7.5;
    lightDir = normalize(vec3(sin(angle1), 0.5*sin(angle2), cos(angle1)));

    for (int i = 0; i < 16; i++) {
        vec3 transformed = rotateMatZ(angle2) * rotateMatY(angle2) * rotateMatX(angle2) * cur;
    	float dist = distFunc(transformed);
        if (dist < eps) {
        	vec3 normal = normalFunc(transformed);
            float diff = dot(normal, lightDir);
            col = vec3(clamp(diff, 0.05, 0.95));
            break;
        }
        cur += ray * dist;
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}

#define tex iChannel0
