#define t iTime
vec3 m(vec3 p, float r){return vec3(p.x,cos(r)*p.y+sin(r)*p.z,cos(r)*p.z-sin(r)*p.y);}
vec3 mx(vec3 p, float x){return m(p.xzy, x).xzy;}
vec3 my(vec3 p, float y){return m(p.yxz, y).yxz;}
vec3 mz(vec3 p, float z){return m(p.zyx, z).zyx;}
vec3 mxyz(vec3 p, vec3 r){return mx(my(mz(p,r.z),r.y),r.x);}

float rep(float f){if(f > 0.){if(f < 1.){return 1.;}}return 0.;}
float point(vec2 uv, vec2 p, float s){return smoothstep(s, s-0.001, distance(uv,p)*4.);}
float ray(vec2 uv, vec3 pos, vec3 r, vec3 f, float s){
    pos += mxyz(f,r);
    pos.z += 1.;
    pos.xy /= pos.z;
    pos.z = 1./pos.z;
    pos.z *= 0.06125*s;
    return point(uv, pos.xy,pos.z);}
float cub(vec3 pos, vec2 uv, vec3 r, float s){
    float col;
    vec3 f;
    for(int d = 0; d < 2; d ++){
        for(int g = 0; g < 4; g ++){
            f = my(vec3(0.5,0.25,0)*s, float(g)*3.14/2. );
            f = mz(f, float(d)*3.14 );
            col += ray(uv, pos, r, f, s);
        }
    }
    return col;
}

vec3 plane(vec3 cor, vec3 r, vec2 fog){
