}

/**
 * https://qiita.com/7CIT/items/5113587c82fc544c0a58
 */
float heart(vec2 p){
 return pow(p.x*p.x+p.y*p.y-1.,3.)-p.x*p.x*p.y*p.y*p.y;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord/iResolution.xy;
    uv.y *= iResolution.y / iResolution.x;

    float gridLength;
    vec2 gridCoord;
    if(uv.x > 1.) {
        gridLength = 1. + iTime * .2;
    	gridCoord = floor(uv * gridLength + iTime * 2. * sin(iTime));
    } else {
        gridLength = 100. - (tan(iTime *3.));
    	gridCoord = floor(uv * gridLength - iTime * 1. + tan(iTime * 8.));
    }

    vec2 gridFrag = fract(uv * gridLength);

    vec3 color = vec3(random(gridCoord));

    if(fract(gridCoord.y / 2.) == 0.) {
    	if(color.x > .4 && gridFrag.x > color.x / 4.) {
            color = vec3(.0);
        }
    } else {
    	if(color.x > .4 && gridFrag.y > color.x / 4.) {
            color = vec3(.0);
        }
    }

    color.r = .0;
    color.g = .0;

    if(color.b > .5 + (.2 * .5 + .9) * 1. * ((1. - abs(uv.x * 2. - 1.)) / ((sin((iTime - 3.5) / 2.) * .5 + .5) * 10.))) {
    	color.r += (4. * .3 + .8) * 1. * (1. - uv.y);
    }

    color.r += fract(uv.x) * .2;

    // heart
    float l = 1.-step(0.,heart(vec2(uv.x - .5, uv.y - .25) * (5. - (tan(iTime * 3.) * .5 + .5))));
    //color *= vec3(l) * sin(iTime) - .3;
    color *= vec3(l) - .3;

    fragColor = vec4(color,1.0);
}

float rand(vec2 n) { 
	return fract(sin(dot(n, vec2(12.9898, 4.1414))) * 43758.5453);
}

float noise(vec2 p){
    p.x = sin(p.x);
	vec2 ip = floor(p);
	vec2 u = fract(p);
	u = u*u*(3.0-2.0*u);

	float res = mix(
		mix(rand(ip),rand(ip+vec2(1.0,0.0)),u.x),
		mix(rand(ip+vec2(0.0,1.0)),rand(ip+vec2(1.0,1.0)),u.x),u.y);
	return res*res;
}

float n(vec2 p, float s) {
    float o = s / iResolution.x;
    return (noise(p + vec2(o, 0.)) + noise(p + vec2(0., o)) + noise(p + vec2(-o, 0.)) + noise(p + vec2(0., -o))) / 4.;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 c = (fragCoord.xy / iResolution.xy - vec2(0.5)) * 0.25;
    float l = length(c);
    float s = pow(0.5, 1. / iTime) * 0.5;
    float t = atan(c.y, c.x);
    vec2 p = vec2(t, pow(2., l * 10.) / s + iTime);
    float e = 1. - smoothstep(s - .01, s, l);

    fragColor = vec4(n(p * 8., l * 100000.) * e);
}

float PI = 3.14159265;
mat2 genRot(float val){
	return mat2(cos(val),-sin(val),sin(val),cos(val));
}
vec3 modc(vec3 p,vec3 b){
	p = (fract(p / b + 0.5)-0.5) * b;
    return p;
}
vec2 pmod(vec2 p,float c){
	p *= genRot(PI/c);
    float at = atan(p.y/p.x);
    at = mod(at,PI * 2./c);
    float r = length(p);
    p = vec2(cos(at) * r,sin(at) * r);
    p *= genRot(-PI/c);
    return p;
}

float map(vec3 p){
    p.xy *= genRot(-iTime);
    p = modc(p,vec3(4.));
    p.xy *= genRot(iTime * 2.);
    p.xy = pmod(p.xy,8.);
	float sp = length(p - vec3(1.0,0.,0.)) - 0.5;
    float pil = length(p.xy - vec2(2.0,0.)) - 0.1; 
    return min(sp,pil);
}

vec3 getNormal(vec3 p){
	vec3 x = dFdx(p);
    vec3 y = dFdy(p);
    return normalize(cross(x,y));
}

vec4 trace(vec3 o,vec3 r){
	float t = 0.0;
    for(int i = 0; i < 128; i++){
    	vec3 p = o + r * t;
        float d = map(p);
        t += d * 0.25;
    }
    vec3 n = getNormal(o + r * t);
    vec4 result = vec4(n,t);
    return result;

}

vec3 ray(vec2 uv,float a){
	vec3 r = normalize(vec3(uv,a));
    return r;
}

vec3 cam(){
	vec3 c = vec3(0.,0.,-2.5 + iTime);
    return c;
}

vec3 getColor(vec3 o,vec3 r,vec4 data){
	float t = data.w;
    vec3 n = data.xyz;
    float fog = 1./(1. + t * t * 0.025);
    vec3 bc = vec3(1. - dot(r,n));
    float at = atan(r.y/r.x) * 2.;
    vec3 colA;
    colA.r = cos(at +iTime);
	colA.g = cos(at +iTime + PI * 2. / 3.);
    colA.b = cos(at +iTime + PI * 4. / 3.);
	bc *= colA * 0.5 + 0.5;
    return mix(bc,vec3(0.),1. - fog);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord.xy * 2. - iResolution.xy) / iResolution.x;
    vec3 o = cam();
    vec3 r = ray(uv,sin(iTime/2.) * 1.5);
    vec4 data = trace(o,r);
    vec3 col = getColor(o,r,data);
    fragColor = vec4(col,0.);
}

