float ifLE(float a, float b, float eps) {
    //if (a <= b) return 1.; else return 0.;
    //return float(a <= b);
    //return float(a - b <= .0);
    //return float(b - a >= .0);
    //return step(.0,b - a);
    return smoothstep(.0,eps,b - a);
}
float Ellipse (vec2 p, float w, float h)
{
    //if (length(vec2(p.x/w,p.y/h)) <= .5) return 1.; else return 0.;
    //return float(length(vec2(p.x/w,p.y/h)) <= .5);
    //return float(length(vec2(p.x/w,p.y/h)) - .5 <= .0);
    //return float(.5 - length(vec2(p.x/w,p.y/h)) >= .0);
    //return step(.0,.5 - length(vec2(p.x/w,p.y/h)));
    //return ifLE(length(vec2(p.x/w,p.y/h)),.5,.005);
    float a = length(vec2(p.x/w,p.y/h));
    float b = .5;
    float eps = .005;
    //return ifLE(a,b,eps);
    eps *= 450./iResolution.y;
    return ifLE(a,b+0.5*eps,eps);
}
float ifLE(float a, float b) {
    //return ifLE(a,b,0.00000005*b);
    //return ifLE(a,1.00000005*b,0.00000005*b);
    //return ifLE(a,1.05*b,0.05*b);
