    //return ifLE(a,1.025*b,0.050*b);
    //return ifLE(a,1.25*b,0.50*b);
    float eps = 0.50*b;
    eps *= 450./iResolution.y;
    return ifLE(a,b+0.5*eps,eps);
}

vec2 EllipsePoint (float t, float w, float h) //Point on an ellipse at a certain angle, stretched circle
{
    return vec2(sin(t)*w / 2., cos(t)*h / 2.);
}

float sdLine( vec2 p, vec2 a, vec2 b ) //Line segment SDF made by Inigo Quilez (Thanks!)
{
	vec2 pa = p-a, ba = b-a;
	float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
	return length( pa - ba*h );
}
float bump(float x) { return clamp(min(x,4.-x),0.,1.); }
float act(float x, float which, float total) { return bump(mod(x-4.*which,4.*total)); }
void mainImage( out vec4 u, in vec2 o )
{
    vec2 p = (o - .5*iResolution.xy)/iResolution.y;
    vec4 col = vec4(.5);

    float pw = .2;

    col = mix (col, vec4(.8), Ellipse (p, W + pw, H + pw));
    col = mix (col, vec4(.7), Ellipse (p, H, W) * .5);
    col = mix (col, vec4(1.), Ellipse (p, W, H));
    col = mix (col, vec4(.7), Ellipse (p, H, W) * .25);

    //Ellipse Normal Calculations and drawing
    float t1 = (iTime / 15.) * radians(360.);
    float t2 = t1 + radians(90.);
    vec2 j = EllipsePoint (t1, W + pw, H + pw);
    vec2 k = EllipsePoint (t1, H + pw, W + pw);
    vec2 l = EllipsePoint (t2, W + pw, H + pw);
    vec2 a = EllipsePoint (t1, W, H);
    vec2 b = EllipsePoint (t1, H, W);
    vec2 c = EllipsePoint (t2, W, H);
    vec2 d = EllipsePoint (t2, H, W);
    vec2 nk = normalize(k)*.1;
    vec2 nl = normalize(l)*.1;
    vec2 na = normalize(a)*.1;
    vec2 nb = normalize(b)*.1;
    vec2 nc = normalize(c)*.1;
    vec2 nd = normalize(d)*.1;

    float total = 4.;
    float ref = iTime/2.;
    float act1 = 1.-act(ref,3.,total);
    //float act2 = 1.-act(ref,2.,total);
    float act3 = 1.-act(ref,1.,total);
    float act4 = 5.*act(ref,2.,total);

    col = mix(col, vec4(1,1,0,0), ifLE( sdLine(p, a+(j-a-nl)*act1, a+(j-a+nl)*act1), .005)*.5*act1);
    col = mix(col, vec4(0,1,1,0), ifLE( sdLine(p, a+(j-a   )*act1, a+(j-a+nk)*act1), .005)*.5*act1);
    col = mix(col, vec4(0,1,1,0), ifLE( sdLine(p, a+(j-a   )*act1, a  ), .005)*.5*act1);//better incorrect normal
    col = mix(col, vec4(1,0,0,0), ifLE( sdLine(p, a*act3   , a+na*act1), .005)*.5*act1);//bad stretched circle normal
    col = mix(col, vec4(0,0,0,0), ifLE( sdLine(p, a-nb*act4, a+nb     ), .005)*.5     );//Correct normal - thanks IQ!
    col = mix(col, vec4(0,1,0,0), ifLE( sdLine(p, a-nc     , a+nc     ), .005)*.5     );
    col = mix(col, vec4(0,0,1,0), ifLE( sdLine(p, a-0.     , a+nd*act1), .005)*.5*act1);
    col = mix(col, vec4(1,0,0,0), ifLE( sdLine(p, b-na*act4, b+na     ), .005)*.5     );
    col = mix(col, vec4(0,0,0,0), ifLE( sdLine(p, b*act3   , b+nb*act1), .005)*.5*act1);
    col = mix(col, vec4(0,1,0,0), ifLE( sdLine(p, b-0.     , b+nc*act1), .005)*.5*act1);
    col = mix(col, vec4(0,0,1,0), ifLE( sdLine(p, b-nd     , b+nd     ), .005)*.5     );
    col = mix(col, vec4(1,0,0,0), ifLE( sdLine(p, c-na     , c+na     ), .005)*.5     );
    col = mix(col, vec4(0,0,0,0), ifLE( sdLine(p, c-0.     , c+nb*act1), .005)*.5*act1);
    col = mix(col, vec4(0,1,0,0), ifLE( sdLine(p, c*act3   , c+nc*act1), .005)*.5*act1);
    col = mix(col, vec4(0,0,1,0), ifLE( sdLine(p, c-nd*act4, c+nd     ), .005)*.5     );
    col = mix(col, vec4(1,0,0,0), ifLE( sdLine(p, d-0.     , d+na*act1), .005)*.5*act1);
    col = mix(col, vec4(0,0,0,0), ifLE( sdLine(p, d-nb     , d+nb     ), .005)*.5     );
    col = mix(col, vec4(0,1,0,0), ifLE( sdLine(p, d-nc*act4, d+nc     ), .005)*.5     );
    col = mix(col, vec4(0,0,1,0), ifLE( sdLine(p, d*act3   , d+nd*act1), .005)*.5*act1);

    u = col;
}

