vec3 raymarch(vec4 orig, vec4 dir) {
    float dist = 0.0;
    float minDist = 1e9;
    int steps = 0;
    vec4 pos = orig;
    Result res;
    res.dist = 1e9;

    while (dist < MAXDIST && steps < MAXSTEPS && res.dist >= EPSILON) {
    	res = ED(pos);
        minDist = min(minDist,res.dist);

        dist += FUZZ*res.dist;

        pos = orig + dist*dir;
    	steps++;
    }
    glow = pow(glow,vec3(1.2));
    return 1.0-glow;

}

