#define MAX_DIST 2048.
#define EPS 0.01
#define PI 3.141592654

float time;
float sphere(vec3 p, vec3 center, float radius)
{
    return length(center - p) - radius;
}

mat2x2 rot(float ang)
{
    ang *= PI / 180.;
 	return mat2x2(cos(ang), sin(ang), -sin(ang), cos(ang));   
}

float terrain(vec3 p)
{
    p.z += time;
    float d = 0.;
    d += 3.*sin(time+p.z/10.)*cos(time+p.x/10.);
    d += 7.*sin(p.x*p.z/5500.+time);
    d += 5.*abs(cos(PI/2. + (p.x+time)/50.));
    return d;
}

vec4 map(vec3 p)
{
    float h = terrain(p);
 	h *= terrain(p+cos(time+p.z*p.x/50.))/2.;

    vec3 col = vec3(0.);

    vec3 rotCenter = vec3(0, 0, 55);

    float spRotRad = 50.;
    vec3 spCenter = rotCenter + vec3(
        10.*sin(5.1235*time) + 50.*sin(time)*cos(time),
        spRotRad * abs(cos(time)),
        10.*cos(5.1235*time)
     );

    float sp = sphere(p, spCenter, 7.5 + 7.5*cos(PI/2. + time));


    float d = p.y + h;
    vec3 pp = normalize(p - spCenter);

    if(sp < d)
    {
        d = sp;
        col = vec3(1, .5, .2);
        col.xy = (rot(time*25.8520) * col.zy)*(h+3.);
        col.zy = (rot(time*324.753) * col.xz);
        col.zx = abs(col.zx);
    }

    float an = dot(pp, vec3(0, -1, 0)) + .5*sin(time);
    float rad = .4;
    if(an > rad)
    {
        if(an < rad + .1)
        {
            if(an > rad + .09)
                col = vec3(3.);
            else col = vec3(0.);
        }else
        {
            col = vec3(.5, 0, 0);
            col.xy = (rot(time*15.) * col.xy)*(h+.7);
            col.zy = (rot(time*30.) * col.yx);
            col.zx = abs(col.zx);
            col *= vec3(.7, 0.3, .5)*1.2;
            col *= (an-rad)/rad;
        }
    }

    return vec4(col, d);
}

vec4 march(vec3 ro, vec3 rd)
{

    vec4 d=vec4(0.);
    float dv;
    for(int i = 0; i < 250; i++)
    {
        vec3 newRay = ro + dv * rd;
        d = map(newRay);
        if(d.w < EPS || dv > MAX_DIST)
            break;
        dv += d.w * .3;
    }

    return vec4(d.xyz, dv);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    time = iTime/3.;
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy - .5;
    uv.x *= iResolution.x / iResolution.y;

    vec3 ro = vec3(0, 175, -150. + 2.*iMouse.y);
    /*if(iMouse.w == 1.)
        ro.xz *= rot(iMouse.x);*/
    ro.xz *= rot(iMouse.x + time*20.);
    ro.yz *= rot(90.*sin(time/10.));
    ro.y = abs(ro.y)+20.;
    vec3 lookat = vec3(0, 0, 50);

    vec3 fw = normalize(lookat - ro);
    vec3 ri = normalize(vec3(fw.z, 0, -fw.x));
    vec3 up  = cross(fw, ri);

    vec3 rd = mat3x3(ri, up, fw) * vec3(uv, 1);

    vec4 d = march(ro, rd);

    vec3 col;
    if(d.w < MAX_DIST)
        col = d.xyz * .3;
    // Output to screen

    fragColor = vec4(col,1.0);
}

