float sdf(vec3 p,float time){
    float pi=3.1415926;
   float d=1e4;
   d=smin(length(p-vec3(-.75,0.,-.1))-.4,length(p-vec3(.75,0.,-.1))-.5,2.);
    d=smin(d,length(p-vec3(0,0.4,-.1))-.9,1.6);
    //NOSE
    d=smin(d,length(p-vec3(0,0.4,1.0))-.5,.3);
    d=smin(d,length(p-vec3(0,0.35,2.0))-.2,1.2);
    //d=smin(d,length(p-vec3(0,0.35,3.0))-.2,1.2);
    //DOG EARS
    d=smin(d,length(p-vec3(.8,1.3,-0.5))-.3,0.4);
    d=smin(d,length(p-vec3(1.0,1.6,-0.5))-.05,0.4);
    d=smin(d,length(p-vec3(-.8,1.3,-0.5))-.3,0.4);
    d=smin(d,length(p-vec3(-1.0,1.6,-0.5))-.05,0.4);
    //neck
    vec3 pneck=p;
    pneck.yz*=rotmat(pi/8.0);
    d=smin(d,distance(vec3(0.,clamp(pneck.y,-3.0,-1.1),0.),pneck.xyz)-0.95,.4);
    //front chest
    d=smin(d,length(p-vec3(.0,-3.0,-1.0))-1.2,0.4);
    //front legs
    d=smin(d,distance(vec3(0.55,clamp(p.y,-5.0,-3.1),-1.0),p.xyz)-0.4,.4);
    d=smin(d,distance(vec3(-0.55,clamp(p.y,-5.0,-3.1),-1.0),p.xyz)-0.4,.4);
    //belly
    d=smin(d,distance(vec3(0.,-3.0,clamp(p.z,-3.5,-1.0)),p.xyz)-1.0,.4);
    d=smin(d,length(p-vec3(0,-3.5,-2.7))-1.2,0.4);
    //backleg
    d=smin(d,length(p-vec3(.4,-3.3,-4.0))-.8,0.4);
    d=smin(d,length(p-vec3(-.4,-3.3,-4.0))-.8,0.4);
    d=smin(d,length(p-vec3(.6,-4.2,-4.2))-.6,0.7);
    d=smin(d,length(p-vec3(-.6,-4.2,-4.2))-.6,0.7);
    //leg
    vec3 plleg=p;
    plleg.yz*=rotmat(-pi/12.0);
    d=smin(d,distance(vec3(0.55,clamp(plleg.y,-6.0,-5.0),-2.3),plleg.xyz)-0.35,.4);
    d=smin(d,distance(vec3(-0.55,clamp(plleg.y,-6.0,-5.0),-2.3),plleg.xyz)-0.35,.4);
    d=smin(d,distance(vec3(0.55,clamp(plleg.y,-5.0,-3.6),-5.6),plleg.xyz)-0.35,.4);
    d=smin(d,distance(vec3(-0.55,clamp(plleg.y,-5.0,-3.6),-5.6),plleg.xyz)-0.35,.4);
    //tail
    vec3 plt1=p;
    plt1.y+=3.3;
    plt1.z+=2.3;
    plt1.yz*=rotmat(sin(time*10.0)*pi/12.0);
    plt1.y-=3.3;
    plt1.z-=2.3;
    vec3 plt2=p;
    plt2.y+=3.3;
    plt2.z+=5.3;
    plt2.yz*=rotmat(sin(time*10.0)*pi/12.0);
    plt2.y-=3.3;
    plt2.z-=5.3;
    d=smin(d,length(p-vec3(-.0,-3.3,-5.0))-.4,0.4);
    d=smin(d,length(plt1-vec3(-.0,-3.3,-6.0))-1.1,0.4);
    d=smin(d,length(plt2-vec3(-.0,-3.3,-7.5))-.1,1.6);
    return d;
}
float raymarch(vec3 ro,vec3 rd,float begin,float end,float time){
    float d= begin;
    for (int i = 0; i < 255; i++) {
        float dist = sdf(ro+rd*d,time);
        if (dist < 0.001) {
			return d;
        }
        d += dist;
        if (d >= end) {
            return end;
        }
    }
    return end;
}
