//from iq
float CalcAO(vec3 p, vec3 n)
{
   float r = 0.0;
   float w = 1.0;
   for (int i=1; i<=6; i++)
   {
      float d0 = float(i) * 0.2;
      r += w * (d0 - sdf(p + n * d0,g_time));
      w *= 0.4;
   }
   return 1.0 - clamp(r,0.0,1.0);
}
//from iq
float CalcSSS(vec3 ro, vec3 rd,vec3 nor)
{
    vec4 kk;
	float occ = 0.0;
    for( int i=0; i<8; i++ )
    {
        float h = 0.002 + 0.11*float(i)/7.0;
        vec3 dir = normalize( sin( float(i)*13.0 + vec3(0.0,2.1,4.2) ) );
        dir *= sign(dot(dir,nor));
        occ += (h-sdf(ro-h*dir,g_time));
    }
    occ = clamp( 1.0 - 11.0*occ/8.0, 0.0, 1.0 );    
    return occ*occ;
}


vec3 GetNormal(vec3 pos)
{
   vec3 n;
   vec2 delta=vec2(0.001,0);
   n.x = sdf( pos + delta.xyy,g_time ) - sdf( pos - delta.xyy,g_time );
   n.y = sdf( pos + delta.yxy,g_time ) - sdf( pos - delta.yxy,g_time );
   n.z = sdf( pos + delta.yyx ,g_time) - sdf( pos - delta.yyx ,g_time);

   return normalize(n);
}
void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
	float t=iTime;
    g_time=iTime;
	vec3 rd=Ray(0.3,fragCoord);
	rd=normalize(rd);
    vec3 ro=vec3(0.0,0.0,-8.0);
	ro=RY(ro,t/3.0);
	rd=RY(rd,t/3.0);
	float dt=raymarch(ro,rd,0.0,100.0,t);
	if(dt>100.0-0.001){
		fragColor=vec4(0.0,0.0,0.0,0.0);
		return;
	}
    vec3 norm=GetNormal(ro+dt*rd);
	vec3 light = vec3(1) * max(0.0, dot(norm, vec3(0.2,0.1,0.02)));

   //light = (0.25 * light);
   float sss=CalcSSS(ro+rd*dt, rd,norm);
   float ao= CalcAO(ro+rd*dt, norm);
   light = mix(light, vec3(0.8,0.6,0.6),sss);
   light +=ao*0.25;
    //light+=vec3(0.1,0.1,0.1);

    fragColor=vec4(clamp(light,0.0,1.0),1.0);
}

