float smin( float a, float b, float k )
{
    float res = exp2( -k*a ) + exp2( -k*b );
    return -log2( res )/k;
}

vec2 pmod(vec2 p,float c){
    float tmp = PI * 2. / c;
	float l = length(p);
    float theta = atan(p.y/p.x);
    theta = mod(theta,PI * 2. / c);
    return vec2(l * cos(theta), l * sin(theta));

}


