float cube(vec3 p,vec3 o,vec3 s){
    float x = abs(p.x - o.x) - s.x/2.;
    float y = abs(p.y - o.y) - s.y/2.;
    float z = abs(p.z - o.z) - s.z/2.;
    return max(x,max(y,z));
}

float line(vec3 p,vec3 q1,vec3 q2,float r){
	float t = clamp(
    	dot(q2 - p,q2 -q1)/dot(q2-q1,q2-q1),
    	0.,
        1.
    );
    vec3 q = t * q1 + (1. - t) * q2;
    return length(q - p) - r;
}


//Gathering SDF

float map(vec3 q){
    q.xy *= genRot(PI/10.);
    q.xy = pmod(q.xy,10.);
    q.xy *= genRot(-PI/10.);
    q.xy = (fract(q.xy /10.0 + 0.5) - 0.5) * 10.0;
    q.z = (fract(q.z / 8.0 + 0.5) - 0.5) * 8.0;
	//q.xz *= genRot(iTime/2.);
    float at = atan(q.y/q.x);
    q.xy *= genRot(PI/10.);
    q.xy = pmod(q.xy,10.);
    q.xy *= genRot(-PI/10.);
    q.xz *= genRot(PI/10.);
    q.xz = pmod(q.xz,10.);
    q.xz *= genRot(-PI/10.);
    float h1 = 0.2 * cos(abs(at) + iTime);
    float h2 = 0.3 * sin(abs(at) + iTime * 1.2);
    float pole = sphere(q,vec3(2.,0.,h1),0.2);
    pole = min(pole,
              line(q,vec3(0.),vec3(2.,0.,h1),0.05));
    pole = min(pole,sphere(q,vec3(3.,0.,h2),0.15));
    pole = min(pole,
              line(q,vec3(3.,0.,h2),vec3(2.,0.,h1),0.05));
    return pole;
}

//Getting Normal

vec3 getNormal(vec3 p) {
    return normalize(vec3(
        map(p + vec3(EPS, 0.0, 0.0)) - map(p + vec3(-EPS,  0.0,  0.0)),
        map(p + vec3(0.0, EPS, 0.0)) - map(p + vec3( 0.0, -EPS,  0.0)),
        map(p + vec3(0.0, 0.0, EPS)) - map(p + vec3( 0.0,  0.0, -EPS))
    ));
}

//Setting CameraPos
vec3 Camera(float t){
	return vec3(5. * cos(iTime/4.),5. * sin(iTime/4.),-6.0 + iTime);
}

//Setting Ray
vec3 Ray(vec2 uv, float z){
	vec3 ray = normalize(vec3(uv,z));
    ray.yz *= genRot(PI/4.);
    ray.xy *= genRot(iTime/4.); 
    ray.xz *= genRot(PI/4.);

    return ray;
}

//Tracing Ray

vec4 trace (vec3 o, vec3 r){
	float t = 0.0;
    vec3 p = vec3(0.0,0.0,0.0);

    for(int i = 0; i < 256; ++i){
        p = o + r * t;
        float d = map(p);
        t += d * 0.25;
    }
    return vec4(getNormal(p),t);
}

//Making color
vec3 getColor(vec3 o,vec3 r,vec4 data){
    float t = data.w;
    float fog = 1.0 / (1.0 + t * t * 0.015);
    float a = dot(data.xyz,r);
    vec3 p = o + r * t;
    vec3 fc = min(fract(p.x + p.y),fract(p.z + p.y))<0.03 ? vec3(0.95) : vec3(0.05);
    fc = mix(fc,vec3(1.),1. + a*1.5);
    fc = mix(vec3(0.95),fc,fog);
    fc = t < 1000. ? fc : vec3(0.95);
    //fc = vec3(fog);
    return fc;
}

//Drawing

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //set canvas
    vec2 uv = fragCoord.xy /iResolution.xy;
	uv = uv * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;

    //set camera and ray
    vec3 r = Ray(uv,1.2);
    vec3 o = Camera(iTime);

    //trace ray
    vec4 data = trace(o,r);
    vec3 fc = getColor(o,r,data) ;
	//fc = vec3(fog);
    // Output to screen
    fragColor = vec4(fc,1.0);
}

#define PI2 6.28318530718
#define PI 3.14159265359
#define e 2.71828182845904523536

float lineDF(vec2 a, vec2 b, vec2 p){
    vec2 ab = b-a;
    vec2 ap = p-a;
    float t = dot(ap,ab)/dot(ab,ab);
    t = clamp(t,0.,1.);
    vec2 c = a + t*ab;
    return length(p-c);
}

float lineDFSqrd(vec2 a, vec2 b, vec2 p){
    vec2 ab = b-a;
    vec2 ap = p-a;
    float t = dot(ap,ab)/dot(ab,ab);
	t = clamp(t,0.,1.);
	vec2 c = a + t*ab;
    c = p-c;
    return c.x*c.x+c.y*c.y;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	float t = iTime*3.;
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    float scale = 1.5;
	uv *= scale;

    float t2 = t*2.;
    float t3 = t*.1;
    float c = 999999.;
    float c2 = 999999.;
    vec2 prev = vec2(0);
    float skip = 23.;//floor(mod(t2,24.)/2.)*2.+3.;
    float waves = floor(mod(t3,6.));
    float f = pow(cos(t3*PI2-PI2)*.5+.5, 8.);

    for (float a = 0.; a < PI2+.1; a += PI2/50.){
        // lines
        float a2 = a * skip + PI/2.;
        vec2 p = vec2(cos(a2), sin(a2))*.5;
        vec2 off = normalize(p) * mix(.0,.15,cos(a2*waves+t2)*.5+.5);
        p += mix(off, vec2(0), f);;
        if (a > 0.){        
        	float ci = lineDFSqrd(prev,p,uv);
        	c = min(c,ci);
        }
        prev = p;

        // circles
        float a0 = a + PI/2.;
        vec2 p0 = vec2(cos(a0), sin(a0))*.5;
        vec2 off0 = normalize(p0) * mix(.0,.15,sin(a0*waves+t2)*.5+.5);
        p0 += mix(off0, vec2(0), f);
        float s = .005;
        float c2i = length(p0-uv)-s;
        c2 = min(c2,c2i);
    }

    // combine
    c = sqrt(c);
    c = min(c,c2);

    // step
    float w = 1.5*scale/iResolution.y;//*mix(1.,300.,sin(t)*.5+.5);
    float lw = .001;
    c = smoothstep(lw+w,lw-w, c);

    vec3 col = vec3(c);
    col *= (0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4)));

    // Output to screen
    fragColor = vec4(col,1.0);
}

float wave_amp=0.02;
float wave_freq=3.0;
float wave_speed=5.0;


bool red_strip( vec2 uv )
{
    if (uv.y>-0.06 && uv.y<0.06)
        return true;

    if (uv.y>-0.3 && uv.y<-0.18)
        return true;

    if (uv.y>0.18 && uv.y<0.3)
        return true;

    return false;
}

vec4 yellow_flag(vec2 uv)
{
    if (red_strip(uv))
        return vec4(0.98, 0.24, 0.2, 1.0);

    return vec4(1.0, 1.0, 0.0, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 v = fragCoord.xy / iResolution.xy;
    vec2 uv=v*2.-vec2(1.);
    uv.x*=iResolution.x/iResolution.y;

    float d=-uv.x+0.3*uv.y + texture(iChannel0,uv).x*0.03 - exp(-1.0+uv.x) +  exp(-uv.y);
    float a=mod(wave_speed*iTime+d*wave_freq,4.0*3.14159265359);
    uv.x += cos(a)*wave_amp;
    uv.y += sin(a)*wave_amp;
    vec4 color = yellow_flag(uv);
    float shadow = .75 - 0.25*(sin(a-0.3));
    fragColor = color * shadow;
}

// From Inigo Quilez
vec2 hash( vec2 x )  // replace this by something better
{
    const vec2 k = vec2( 0.3183099, 0.3678794 );
    x = x*k + k.yx;
    return -1.0 + 2.0*fract( 16.0 * k*fract( x.x*x.y*(x.x+x.y)) );
}

// https://www.shadertoy.com/view/XdXGW8 
float noise( in vec2 p )
{
    vec2 i = floor( p );
    vec2 f = fract( p );

	vec2 u = f*f*(3.0-2.0*f);

    return mix( mix( dot( hash( i + vec2(0.0,0.0) ), f - vec2(0.0,0.0) ), 
                     dot( hash( i + vec2(1.0,0.0) ), f - vec2(1.0,0.0) ), u.x),
                mix( dot( hash( i + vec2(0.0,1.0) ), f - vec2(0.0,1.0) ), 
                     dot( hash( i + vec2(1.0,1.0) ), f - vec2(1.0,1.0) ), u.x), u.y);
}

float random(in float seed) {
    return fract(sin(dot(vec2(seed),
                         vec2(11.039234,66.74392)))*
        5706.2373);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec3 color = vec3(0.0078, 0.0588, 0.0588);

    vec2 st = gl_FragCoord.xy/max(iResolution.x, iResolution.y);
    vec2 mt = iMouse.xy/max(iResolution.x, iResolution.y);
    vec2 pos = vec2(st * 11.);
    float n = 11.9 + 1.8 * noise(pos);
    float dis = distance(st,vec2(.65*n)) * distance(st,vec2(.046*n));
    dis += distance(st, mt);

    float steps = 18.;

    float pct = floor(mod(dis *6. - iTime * 0.8, steps))*(1./(steps - .1));
        pct = fract(pct*(random(pct) * 0.5 + 0.5));
    vec3 malaColors[4];
        malaColors[0] = vec3(0.0078, 0.1412, 0.102);
        malaColors[1] = vec3(0.051, 0.3608, 0.2941);
        malaColors[2] = vec3(0.2275, 0.8471, 0.7137);
        malaColors[3] = vec3(0.1255, 0.6706, 0.5333);

    float cutoff = 0.22;

    if (pct < cutoff) {
        color += mix(malaColors[0], malaColors[1], pct);
    } else {
        color = mix(malaColors[2], malaColors[3], (pct - cutoff) * (1. / cutoff));
    }
    fragColor = vec4(color, 1.);
}

