}

#define PI 3.141592
float speed = 5.0;
vec3 light;

mat2 rotate(float a){
    float c = cos(a), s = sin(a);
    return mat2(c,-s,s,c);
}

float hash21( vec2 p) {
    return fract(sin(dot(p, vec2(12.9898,78.233)))* 43758.5453123);
}


float sdSphere(vec3 pos, float r){
    return length(pos) - r;
}

float sdCylinder( vec3 pos, vec3 c ){
  return length(pos.xy-c.xy)-c.z;
}

float sdBox(vec3 pos, vec3 c){
    vec3 d = abs(pos) - c ;
    return length(max(d,0.0)) + min(max(max(d.x,d.y),d.z),0.0);
}

float sdBar(vec3 pos, vec3 c){
    vec2 d = abs(pos.xy) - c.xy;
    return length(max(d,0.0)) + min(max(d.x,d.y),0.0);
}

vec2 matMin(vec2 a, vec2 b){
    return (a.x < b.x) ? a : b;
}


vec2 map(vec3 pos){
    vec2 map = vec2(10000.0,-1.0); 

    vec3  p = pos;
    float outC = sdCylinder(p,vec3(0.0,0.0,4.0));
    float inC = sdCylinder(p,vec3(0.0,0.0,3.9));
    p.xy *= rotate(p.z);
    float mask = sdBar(p,vec3(4.0,3.8,0.0));
    float d = max(outC, -inC);
    d = max(d,mask);
    map = matMin(map,vec2(d,3.0)); 

    p = pos;
    float pOutC = sdCylinder(p,vec3(0.0,0.0,2.0));
    float pInC = sdCylinder(p,vec3(0.0,0.0,1.95));
    float cycle = floor(hash21(vec2(floor(pos.z),-floor(pos.z)))*5.0);
    p.xy *= rotate(cycle);
    float t = clamp(mod(iTime,cycle) - (cycle-0.5), 0.0, 1.0)/0.5;
    float rad =  mix(floor(iTime/cycle),floor(iTime/cycle+1.0),t)*PI/2.0;
    rad = (mod(cycle,2.0)==0.0) ? rad : -rad;
    p.xy *= rotate(rad);
    float pMask = sdBar(p,vec3(0.5,3.0,0.0)); 
    pMask = min(pMask,sdBar(p,vec3(3.0,0.5,0.0)));
    vec3 q = pos;
    q.z = mod(q.z,2.0) - 1.0;
    pMask = min(pMask,sdBox(q,vec3(4.0,4.0,0.5)));
    float pC = max(pOutC,-pInC);
    pC = max(pC,-pMask);
    map = matMin(map,vec2(pC,2.0));

    p = pos;
    float outC2 = -sdCylinder(p,vec3(0.0,0.0,5.0));
    map = matMin(map,vec2(outC2,4.0));

    p = mod(pos,2.0)-1.0;
    float sp = sdSphere(p,0.2);
    float box = sdBox(p,vec3(0.2));
    map = matMin(map,vec2(mix(sp,box,sin(iTime)),1.0));
    return map;
}


vec3 calcNorm(vec3 pos){
    float d = 0.0001;
    return normalize(vec3(
        map(pos + vec3(  d, 0.0, 0.0)).x - map(pos + vec3( -d, 0.0, 0.0)).x,
        map(pos + vec3(0.0,   d, 0.0)).x - map(pos + vec3(0.0,  -d, 0.0)).x,
        map(pos + vec3(0.0, 0.0,   d)).x - map(pos + vec3(0.0, 0.0,  -d)).x
    ));
}

vec3 trace(vec3 ro, vec3 rd,int N){
    vec3 pos;
    float dist = 0.0;
    vec2 m;
    for(int i = 0; i < N; i++){
        pos = ro + dist*rd;
        m = map(pos);
        //https://qiita.com/ukeyshima/items/221b0384d39f521cad8f
        dist += min(min((step(0.0,rd.x)-fract(pos.x))/rd.x, 
            (step(0.0,rd.z)-fract(pos.z))/rd.z)+0.01,m.x)*0.75;
    }
    return vec3(dist,m);
}


vec3 lighting(vec3 pos, vec3 N,float mat){
    vec3 col = vec3(0.0);
    vec3 L = normalize(light - pos);

    float diffuse = clamp(dot(N,L),0.1,1.0);
    if(mat == 1.0){
        col += diffuse * vec3(0.01,0.4,0.6);
    }else if(mat == 2.0){
        col += diffuse * vec3(0.8);
    }else if(mat == 3.0){
        col += diffuse * vec3(0.4);
    }else if(mat == 4.0){
        col += diffuse * vec3(0.4,0.4,0.9)*0.8;
    }
    return col;
}

vec3 getCol(vec3 ro,vec3 rd,vec2 uv){
    light = vec3(0.0,0.0,0.0-iTime*speed);
    vec3 col = vec3(0.0);

    vec3 pos;
    vec2 m; 

    vec3 t = trace(ro,rd,128);
    pos = ro + t.x*rd;
    if(abs(t.y) < 0.001){
        vec3 N = calcNorm(pos);
        vec3 L = normalize(light - pos);
        col += lighting(pos,N,t.z);
        float diff = clamp(dot(N,L),0.0,1.0);
        float f = (sin(uv.x) * 0.5 + 0.5) + (sin(uv.y) * 0.5 + 0.5);//halftone
        col +=  mix(0.0,f*0.05,diff);

        vec3 R = normalize(reflect(rd,N));
        pos += N*0.001;
        vec3 rT = trace(pos,R,32);
        pos += rT.x*R; 
        if(abs(rT.y) < 0.001){
            vec3 rN = calcNorm(pos);
            col += lighting(pos,rN,rT.z);

            vec3 rR = normalize(reflect(R,rN));
            pos += rN*0.001;
            vec3 rrT = trace(pos,rR,32);
            pos += rrT.x*rR;
            if(abs(rrT.x)<0.001){
                vec3 rrN = calcNorm(pos);
                col += lighting(pos,rrN,rrT.z);
            }
        }
    }

    col += vec3(0.01)*exp(0.15*t.x);
    col += vec3(0.002,0.02,0.02)*exp(0.5*sin(iTime)*t.x);
    return col;

}

mat3 cam(vec3 ro, vec3 ta){
    vec3 cUp = vec3(0.0,1.0,0.0);
    vec3 cDir = normalize(ta-ro);
    vec3 cSide = normalize(cross(cDir,cUp));
    cUp = normalize(cross(cDir,cSide));

    return mat3(cSide,cUp,cDir);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 p = fragCoord.xy*2.0 - iResolution.xy;
    p /= min(iResolution.x,iResolution.y);

    float z = -iTime*speed; 
    vec3 ro = vec3(0.0,0.0,0.0+z);
    vec3 ta = vec3(3.0*cos(iTime*1.2),-3.0*sin(iTime*0.8),6.0*cos(iTime*0.5)+z);
    vec3  rd = normalize(vec3(p,1.0)) * cam(ro,ta);

    vec2 uv = fragCoord;
    vec3 col = getCol(ro,rd,uv);

    fragColor = vec4(col,1.0);
}

const int MAX_MARCHING_STEPS = 64;
const float EPSILON = 0.0015;
const float NEAR_CLIP = 0.0;
const float FAR_CLIP = 100.00;
const float PI = 3.14159265359;

vec3 toGamma( vec3 col ){
	return pow( col, vec3(1.0/2.2));
}

float clampeddot(vec3 a, vec3 b){
    return max(0.,dot(a, b));
}

vec3 lightDirection = vec3(1.0, 1.0, 1.0);

vec2 rotate(vec2 pos, float angle){
    float c = cos(angle);
    float s = sin(angle);
    return mat2(c, s, -s, c) * pos;
} 

float sdBox( vec3 p, vec3 b ){
    vec3 d = abs(p) - b;
    return min(max(d.x,max(d.y,d.z)),0.0) + length(max(d,0.0));
}

float sphere(vec3 pos, float radius){
    return length(pos) - radius;
}

vec3 opRep( vec3 p, vec3 c ){
    return mod(p,c)-0.5*c;
}

float map(vec3 pos){
    pos.xy = rotate(pos.xy, sin(iTime) * PI/2. * 0.03);
    float offset = 0.5;
    //float sph = sphere(pos, 1.5);
    // just a sphere
    //return sph;

    // domain repetition, more edges, antialias more visible
    pos = opRep(pos, vec3(offset, offset, offset));

    // test boxes
    return sdBox(pos, vec3(0.1, 0.1, 0.1));

    // test spheres
    // /return sphere(pos, .2);
}

vec3 computeNormal(vec3 pos){
    vec2 eps = vec2(0.01, 0.);
    return normalize(vec3(
        map(pos + eps.xyy) - map(pos - eps.xyy),
        map(pos + eps.yxy) - map(pos - eps.yxy),
        map(pos + eps.yyx) - map(pos - eps.yyx)
    ));
}

float diffuse(vec3 normal){
    return clampeddot(normal, lightDirection);
}

float specular(vec3 normal, vec3 dir){
    vec3 h = normalize(normal - dir);
    float specularityCoef = 40.;
    return pow(clampeddot(h, normal), specularityCoef);
}

mat3 setCamera( in vec3 ro, in vec3 ta, float cr ){
    vec3 cw = normalize(ta-ro);
    vec3 cp = vec3(sin(cr), cos(cr),0.0);
    vec3 cu = normalize( cross(cw,cp) );
    vec3 cv = normalize( cross(cu,cw) );
    return mat3( cu, cv, cw );
}

vec3 getColor(vec3 eye, vec3 dir,float dist){
    float ambient = 0.2;
    vec3 red = vec3(1.0, 0.353, 0.208);
    vec3 collision = (eye += (dist*0.995) * dir );
    vec3 normal = computeNormal(collision);
    float diffLight = diffuse(normal) * ambient + ambient;
    float specLight = specular(normal, dir);

    return (diffLight + specLight ) * red;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = 2.0 * fragCoord / iResolution.xy - 1.0;
    uv.x *= iResolution.x / iResolution.y;

    vec3 eye = vec3(3.5, 3.0, -1.0);
    vec3 ta = vec3( -0.5, -0.9, 0.5 );
    mat3 camera = setCamera( eye, ta, 1.0 );
    float fov = 2.5;
    vec3 dir = camera * normalize(vec3(uv, fov));

    // bg color
    vec3 finalColor = vec3(0.086, 0.19, 0.8);
    // t is the position of the center of the sphere along the ray.
    float t = NEAR_CLIP;
    float dist = EPSILON;
    float zoom = 1.5;

    // this is the radius of the sphere that models the cone.
    // formula from https://www.scratchapixel.com/lessons/advanced-rendering/rendering-distance-fields/basic-sphere-tracer
    float vfov = 0.9; //vfov is the vertical camera field-of-view (in radians)
    float radius = (2.0*tan(vfov/2.0)) /(iResolution.y*zoom);
    // it can also be simplyfied using a constant, like in via https://www.shadertoy.com/view/MsBGWm
    // float radius = .7071/(u_resolution.y*zoom);


    //Depth of Field
    float aperture = .03 * iMouse.x/iResolution.x;
	float focus = 5.1 * iMouse.y/iResolution.y;

    // color accumulation variable. This is used to accumulate the colors sampled for all
    // the intersection between the cone and the scene, and to accumulate the coverage
    // on the alfa channel
    vec4 acc = vec4(0, 0, 0, 1);

    for (int i = 0; i < MAX_MARCHING_STEPS; i++) {
        float rad = t*radius + aperture*abs(t-focus);

        // move the center of the sphere along the ray
        dist = map(eye + t * dir);

        // if the distance from the scene is smaller than the radius,
        // it means that there is an intersection between the cone and the scene.
        // we accumulate and blend the colors sampled for those intersections into the acc variable.
        if (dist < rad) {
            // how much of the surface of the projected pixel is covering the scene?
            // this is what the coverage value stands for.

            // This is the easiest coverage calculation that I have found:
            // float alpha = smoothstep(rad, -rad, dist);
            // https://www.shadertoy.com/view/4scBW8
            float alpha = smoothstep(rad, -rad, dist);

            // You can also estimate the coverage of the surface using it's distance
            // over the projected aperture of the cone. 
            // coverage = dist / rad;
            // of course the blending algorithm have to be adapted. see https://www.shadertoy.com/view/MsBGWm

            // then sample the color at the given distance.
            // I know, I could save the allocation of this variable, but, for the sake of clarity ...
            vec3 sampleCol = getColor(eye, dir, t);

            // the new color is calculated multiplying
            // the previous alpha for the new color times the new alpha
            acc.rgb += acc.a * (alpha*sampleCol.rgb);
            // here we save the new alpha
            acc.a *= (1.0 - alpha);

            // when acc.a is really small, it means that we hit the object, brake the loop
            if (acc.a < EPSILON ) break;   
        }


        // move the center of the sphere forward along the ray. The smaller
        // the steps the higher the precision and the slower the computation.
        t += max(abs(dist*0.85), 0.001); //https://www.shadertoy.com/view/4dlyWB
        //t += max( dist, rad*.5 ); // https://www.shadertoy.com/view/MsBGWm

        // brake if we are going too far away
        if (t > FAR_CLIP) break;
    }

    finalColor = mix(acc.rgb, finalColor, acc.a);
    fragColor = vec4(clamp(toGamma(finalColor),0.0,1.0),1.0);
}

#define STEPS 200
float mand(vec2 z, vec2 c) {
    int iter = 0;
    float sqrDist = 0.0;
    for (;iter<STEPS;iter++) {
        float rr = z.x*z.x;
        float ii = z.y*z.y;
        float tmpR = rr - ii + c.x;
        float tmpI = z.x * z.y + z.y * z.x + c.y;
        sqrDist = length(z);
        if (sqrDist > 4.0) {
            break;
        }
        z.x = tmpR;
        z.y = tmpI;
    }
	return float(iter) - (log (log (sqrDist))) / log(2.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
     // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.x - 0.2;
	vec2 mandelCoords = uv.xy*(1.0/(iTime*iTime))-0.57;

    float mu = mand(mandelCoords, mandelCoords);
    vec3 col = vec3( 1.0 - mu / float(STEPS));
    fragColor = vec4(col,1.0);
}

#define PI 3.1415926
#define TAU 6.2831852

#define SF 1./min(iResolution.x,iResolution.y)

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - iResolution.xy*.5)/iResolution.y;

    float a = atan(uv.y, uv.x)/TAU;

    float l = length(uv);
    float m = 0.;
    for(float n=0.; n<6.; n+=1.){
        float i = n*PI*.25;
        float t = iTime*1. + i;

        float tt = 16.;
        float f = t + a*PI*tt;

        float s = sin(f)*.5+.5;
        float w = sin(PI*iTime + f + a*PI*4.)*.5+.5;

        float bs = w*.01;

    	m = max(m, smoothstep(SF*2.+bs, bs, abs(l - (s*.25 + .225))) * (w*.5+.5) );
    }


    fragColor = vec4(m);
}

float PI = 3.14159265;
mat2 genRot(float v){
	return mat2(cos(v),-sin(v),sin(v),cos(v));
}
vec2 pmod(vec2 p,float c){
	p *= genRot(PI/c);
    float at = atan(p.y/p.x);
    at = mod(at,PI * 2./c);
    float r = length(p);
    p = vec2(r * cos(at),r * sin(at));
    p *= genRot(-PI/c);
    return p;
}
float map(vec3 p){
    vec3 mc = vec3(3.5,3.5,1.5);
        float r = 0.9 + 0.4 * sin(p.z);
    p.xy *= genRot(p.z / 8. + sqrt(length(p.xy))/4. + iTime/32.);
//    p = (fract(p /mc + 0.5)-0.5)*mc;
    p.xy = pmod(p.xy,6.);
    p.xy -= vec2(1.73,0.) * r;

    p.xy += vec2(1.73,0.) * r;
    p = (fract(p /mc + 0.5)-0.5)*mc;

	float pil = length(p.xz - r * vec2(1.73,0.0)) - 0.1;
	pil = min(pil,length(vec2(p.x,abs(p.y)) - r * vec2(1.73,1.)) - 0.1);
    vec3 q = p;
    q.y = abs(q.y);
    pil = min(pil,length(q - vec3(1.73,1.,0.) * r) - 0.25);
    vec3 cp = abs(p - vec3(1.73,0.,0.) * r /2.);
    cp.xy *= genRot(abs(sin(iTime)*1.25));
    float cb = max(cp.x,max(cp.y,cp.z)) - 0.1;
    pil = min(pil,cb);
    return pil;
}
vec3 getNormal(vec3 p){
	vec3 x = dFdx(p);
    vec3 y = dFdy(p);
    return normalize(cross(x,y));
}
vec4 trace(vec3 o,vec3 r){
	vec4 data;
    float t = 0.;
    for(int i = 0; i < 128; i++){
    	vec3 p = o + r * t;
        float d = map(p);
        t += d * 0.5;
    }
    vec3 p = o + r  * t;
    vec3 n = getNormal(p);
    return vec4(n,t);
}	
vec3 getCol(vec3 o,vec3 r,vec4 d){
	float t = d.w;
    vec3 n = d.xyz;
    vec3 p = o + r * t;
    float fog = 1./(1. + t * t * 0.005);
    vec3 col = vec3(pow(1. - dot(n,r) * 0.75,2.));
    vec3 ccol ;
    float at = atan(p.y/p.x) * 2. + p.z;
    ccol.x = sin(at + iTime*2.);
    ccol.y = sin(at + iTime*2. + PI * 2./3.);
    ccol.z = sin(at + iTime*2. - PI * 2./3.);
	ccol= ccol/1.8 + 0.5;
    col *= ccol;
    col = mix(col,vec3(0.),1. - fog);
    return col;
}

vec3 cam(){
	vec3 c = vec3(0.,0.,-2.5 + iTime * 3.);
    return c;
}
vec3 ray(vec2 uv,float z){
	vec3 r = normalize(vec3(uv,z));
    r.xz *= genRot(PI/8.);
    r.yz *= genRot(PI/16.);
    r.xy *= genRot(iTime/4.);
    return r;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord.xy * 2. - iResolution.xy)/iResolution.y;
    uv.y += sign(uv.x) * sign(sin(iTime)) * sqrt(abs(sin(iTime))) * 0.5;
    uv.x += sign(uv.y) * sign(sin(iTime)) * sqrt(abs(sin(iTime))) * 0.5;
	vec3 o = cam();
    vec3 r = ray(uv,1.2 + 0.5 * sin(iTime * PI / 4.));
    vec4 d = trace(o,r);
    vec3 c = getCol(o,r,d);
    float vig = 1. - length(uv) * 0.05;

    c *= vig;
    // Time varying pixel color
    fragColor = vec4(c,1.0);
}

const float PI = 3.14159265;
//use for differential
const float EPS = 0.001;

//common function
mat2 genRot(float val){
	return mat2(cos(val), -sin(val),
               sin(val), cos(val));
}

float rand (float x){
    x = fract(sin(x*416.31434));
    x = fract(sin(x*234.41523));
    x = fract(sin(x*235.51424));
	return x;
}

float smin( float a, float b, float k )
{
    float res = exp2( -k*a ) + exp2( -k*b );
    return -log2( res )/k;
}

vec3 pmod(vec3 p,float c){
    float tmp = PI * 2. / c;
	float l = length(p.xy);
    float theta = atan(p.y/p.x);
    theta = mod(theta,PI * 2. / c);
    return vec3(l * cos(theta), l * sin(theta),p.z);

}


//Common SDF

float sphere(vec3 p,vec3 o,float r){
	return length(p - o) - r;
}

float cylinder(vec2 p,vec2 o,float r){
	return length(p - o) - r;
}

float cube(vec3 p,vec3 o,vec3 s){
    float x = abs(p.x - o.x) - s.x/2.;
    float y = abs(p.y - o.y) - s.y/2.;
    float z = abs(p.z - o.z) - s.z/2.;
    return max(x,max(y,z));
}

float line(vec3 p,vec3 q1,vec3 q2,float r){
	float t = clamp(
    	dot(q2 - p,q2 -q1)/dot(q2-q1,q2-q1),
    	0.,
        1.
    );
    vec3 q = t * q1 + (1. - t) * q2;
    return length(q - p) - r;
}


//Gathering SDF

float map(vec3 p){
    p.xy = (fract(p.xy / 8. + 0.5) - 0.5) *8.;
    vec3 q = p;
    float inner = length(p.xy) - 1.0 - 0.15 * sin(p.z);
    float at = atan(p.y/p.x);
    inner -= 0.1 *cos(at * 2.0 + p.z * PI);
    q.xy *= genRot(PI/16. + q.z/5.);
    q = pmod(q,16.);
    q.xy *= genRot(-PI/16.);
    q.z = (fract(q.z / 2.0 + 0.5)-0.5) * 2.0 ;
    float h1 = 0.4 * cos(abs(at) + iTime);
    float h2 = 0.6 * sin(abs(at) + iTime * 1.2);
    float pole = sphere(q,vec3(2.,0.,h1),0.2);
    pole = min(pole,
              line(q,vec3(0.),vec3(2.,0.,h1),0.05));
    pole = min(pole,sphere(q,vec3(3.,0.,h2),0.15));
    pole = min(pole,
              line(q,vec3(3.,0.,h2),vec3(2.,0.,h1),0.05));
    return min(inner,pole);
}

//Getting Normal

vec3 getNormal(vec3 p) {
    return normalize(vec3(
        map(p + vec3(EPS, 0.0, 0.0)) - map(p + vec3(-EPS,  0.0,  0.0)),
        map(p + vec3(0.0, EPS, 0.0)) - map(p + vec3( 0.0, -EPS,  0.0)),
        map(p + vec3(0.0, 0.0, EPS)) - map(p + vec3( 0.0,  0.0, -EPS))
    ));
}

//Setting CameraPos
vec3 Camera(float t){
	return vec3(4. * cos(-iTime/4.),4. * sin(-iTime/4.),-2.0 + iTime);
}

//Setting Ray
vec3 Ray(vec2 uv, float z){
	vec3 ray = normalize(vec3(uv,z));
    ray.xz *= genRot(PI / 4.);
    ray.yz *= genRot(PI / 4.);
    ray.xy *= genRot(-iTime/4.);
    return ray;
}

//Tracing Ray

vec4 trace (vec3 o, vec3 r){
	float t = 0.0;
    vec3 p = vec3(0.0,0.0,0.0);

    for(int i = 0; i < 256; ++i){
        p = o + r * t;
        float d = map(p);
        t += d * 0.25;
    }
    return vec4(getNormal(p),t);
}

//Making color
vec3 getColor(vec3 o,vec3 r,vec4 data){
    float t = data.w;
    float fog = 1.0 / (1.0 + t * t * 0.015);
    float a = dot(data.xyz,r);
    vec3 p = o + r * t;
    vec3 fc = min(fract(p.x + p.y),fract(p.z + p.y))<0.03 ? vec3(0.95) : vec3(0.05);
    fc = mix(fc,vec3(1.),1. + a*1.5);
    fc = mix(vec3(0.95),fc,fog);
    fc = t < 1000. ? fc : vec3(0.95);
    //fc = vec3(fog);
    return fc;
}

