void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //set canvas
    vec2 uv = fragCoord.xy /iResolution.xy;
	uv = uv * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;

    //set camera and ray
    vec3 r = Ray(uv,1.2);
    vec3 o = Camera(iTime);

    //trace ray
    vec4 data = trace(o,r);
    vec3 fc = getColor(o,r,data) ;
	//fc = vec3(fog);
    // Output to screen
    fragColor = vec4(fc,1.0);
}

// Set parameters here

#define MAX_ITER 1024.0
#define COLOR_SCALE MAX_ITER

#define AA true
#define AA_8X true

#define ALT_COLORS 1

// Code

#define ASPECT (iResolution.x / iResolution.y)

#define Real float

struct Options {
	vec2 zoom_point;
    int frame;
    float zoom_factor;
    int max_iter;
};

/*
 * Types used:
 * Bounds are a vec4(x1, y1, x2, y2)
 * Coordinates are a vec2(x, y)
 */

// Map the current pixel and frame to a point,
// zoomed to the given point by a given factor.
vec2 frame_map(vec2 location, Options options) {    
    vec4 bounds = vec4(vec2(-1.0, 1.0) * ASPECT, vec2(-1.0, 1.0)).xzyw;
    vec2 zoomed_bounds = (bounds.zw - bounds.xy) \
        * pow(options.zoom_factor, Real(-options.frame));

    vec4 px_bounds = vec4(
        options.zoom_point.xy - zoomed_bounds.xy / 2.0,
        options.zoom_point.xy + zoomed_bounds.xy / 2.0
    );

    return px_bounds.xy + (px_bounds.zw - px_bounds.xy) * location / iResolution.xy;
}


int iterations(in vec2 c, in int max_iter) {
    vec2 z = vec2(0);

    for (int i = 0; i < max_iter; i++) {
        if (distance(z.x, z.y) > 2.0) return i;

        z = vec2(
            z.x*z.x - z.y*z.y + c.x,
            z.y*z.x + z.x*z.y + c.y
        );
    }

	return -1;
}

vec3 color(vec2 fragCoord, Options options) {
    vec2 c = frame_map(fragCoord, options);

    Real i = Real(iterations(c, options.max_iter));
    vec3 col;

    if (i == -1.0) {
    	col = vec3(0);
    } else {
#if ALT_COLORS
        col = vec3(
            sin((i / COLOR_SCALE)),
            sin(i / (2.0 * COLOR_SCALE)) / 2.0,
            sin((i / COLOR_SCALE))
            //COLOR_SCALE - (i / COLOR_SCALE)
        );
#else
        col = vec3(
            (sin(1.0/log(i)) + sin((i / COLOR_SCALE))) / 2.0,
            (0.5 * sin(1.0/log2(i)) + sin(i / (2.0 * COLOR_SCALE)) / 2.0) / 2.0,
            (sin(1.0/log(i)) + sin((i / COLOR_SCALE))) / 2.0
        );
#endif
    }

    return col;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    Options options;
   	options.frame = iFrame;

    /*
    Real angle = 0.026;

    Real r = (1.0 - cos(angle)) / 2.0;
	options.zoom_point = vec2(
		r * cos(angle) + 0.25,
		Real y = r * sin(angle)
	);
	*/

    options.zoom_point = vec2(-0.74529, 0.113075);

    options.max_iter = int(pow(1.0, Real(options.frame)) * MAX_ITER);
    options.zoom_factor = 1.01;

    vec3 col = vec3(0);
    col += color(fragCoord + vec2(0.0, 0.0), options);

    if (AA) {
        col += color(fragCoord + vec2(0.5, 0.0), options);
        col += color(fragCoord + vec2(0.0, 0.5), options);
        col += color(fragCoord + vec2(0.5, 0.5), options);
