            col += color(fragCoord + vec2(0.25, 0.25), options);
            col += color(fragCoord + vec2(0.75, 0.25), options);
            col += color(fragCoord + vec2(0.25, 0.75), options);
            col += color(fragCoord + vec2(0.75, 0.75), options);
            col /= 8.0;
        } else {
            col /= 4.0;
        }
    }


    // Output to screen
    fragColor = vec4(col,1.0);
}

#define PI 3.1415
#define SIN(t) (.5*sin(t)+.5)
#define COS(t) (.5*cos(t)+.5)
#define ss(a,b,c) smoothstep(a,b,c)


float spiral(vec2 puv, int iter, float thick)
{
    float sp = length( puv.x - puv.y);
    float movableRef = puv.y;


	// after 2.*pi
    for(int i = 0; i < iter; i++)
    {
        movableRef = movableRef + 2.*PI;
    	sp = min(sp, length(puv.x - movableRef ));    
    }

    // before 0
	sp = min(sp, length(puv.x - puv.y + 2.*PI ));  
	sp = min(sp, length(puv.x - puv.y + 4.*PI ));  

    return ss(thick, 0., sp);
}

vec3 spiralRGB(vec2 puv, float thick, float offset)
{
    int iter = 15;
    vec2 scale = vec2(30.,1.);
    vec2 offs = vec2(offset,0.);

    puv *= scale;

	float r = spiral(puv, iter, thick);
    puv = puv + offs;
	float g = spiral(puv, iter, thick);
    puv = puv + offs;    
	float b = spiral(puv, iter, thick);

    return vec3(r,g,b);
}

float radials(vec2 puv, float segments, float intensity,
              float width, float power)
{
    // last value in interval
    float end = 2.*PI/segments;
    // angle mod
    float puvM = mod(puv.y, end);
    //normalize
    puvM /= end;
    // inverse
    float puvMInv = 1.-puvM; 
    puvM = 	  pow(puvM, power);
    puvMInv = pow(puvMInv, power);
    float res =  intensity*ss(1. - width/2., 1., puvM);
    res = (res + intensity*ss(1. - width/2., 1.,puvMInv));
    return res;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (2.*fragCoord - iResolution.xy)/iResolution.y;
    vec2 muv = iMouse.xy/iResolution.y;

    if(true)
    {
    	vec2 save = uv;
        float grid = 7.;
    	uv.x += (.0851*length(save))*sin(uv.y*grid + iTime);
    	uv.y += (.0851*length(save))*sin(uv.x*grid + iTime);
    }

    vec2 puv = vec2(length(uv), atan(uv.y, uv.x));

    puv.x += radials(puv, 14., -0.21 + .05*sin(puv.y*(12.) + iTime),
                     1., .5);

    // Rotation
    puv.y += mod(iTime, 2.*PI) - PI;

    vec3 col = spiralRGB(puv,
                         4.5 - .9*COS(1.5*iTime),
                         .2 + SIN(iTime*2.) + 0.5*SIN(puv.x*10.));

    // DEBUG Radials
    //col = vec3(radials(puv, 10., 0.1, .9, .6));

    // Output to screen
    fragColor = vec4(col,1.0);
}

//this function is from https://www.shadertoy.com/view/4djSRW
vec2 hash22(vec2 p)
{
	vec3 p3 = fract(vec3(p.xyx) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yzx+33.33);
    return fract((p3.xx+p3.yz)*p3.zy);

}

vec2 magnify(vec2 fragCoord,float mag){
	return hash22(floor(fragCoord/pow(2.0,mag)));
}

vec2 pixel_above(vec2 fragCoord,float mag){
	return magnify(fragCoord+vec2(pow(2.0,mag),0)*iTime,mag);
}

