	col.g = col.r*floor(mod(d*.5+.5+iTime*.5, 2.));
    float f = smoothstep(-.1, .1,sin(a*3.+(sin(iTime*.5)*2.)*d-iTime));
    col.rg = mix(col.rg, 1.-col.rg, f);

    // Output to screen
    fragColor = vec4(col,1.0);
}

