// Exact distance to a cone that intersects a sphere, or a
// "solid angle". Beware doing the max() of a cone and a 
// sphere won't produce an exact euclidean distance.
// Based on sdPie(): https://www.shadertoy.com/view/3l23RK

// List of other 3D SDFs: https://www.shadertoy.com/playlist/43cXRl
//
// and http://iquilezles.org/www/articles/distfunctions/distfunctions.htm


// c is the sin/cos of the desired cone angle
float sdSolidAngle(vec3 p, vec2 c, float ra)
{
    vec2 q = vec2( length(p.xz), p.y );

    float l = length(q) - ra;
	float m = length(q - c*clamp(dot(q,c),0.0,ra) );
    return max(l,m*sign(c.y*q.x-c.x*q.y));
}

float map( in vec3 pos )
{
    pos.y += 0.4;
    pos.xy = (mat2(4,3,-3,4)/5.0)*pos.xy;

    return sdSolidAngle(pos, vec2(3,4)/5.0, 0.7 );
}

// http://iquilezles.org/www/articles/normalsSDF/normalsSDF.htm
vec3 calcNormal( in vec3 pos )
{
    vec2 e = vec2(1.0,-1.0)*0.5773;
    const float eps = 0.0005;
    return normalize( e.xyy*map( pos + e.xyy*eps ) + 
					  e.yyx*map( pos + e.yyx*eps ) + 
					  e.yxy*map( pos + e.yxy*eps ) + 
					  e.xxx*map( pos + e.xxx*eps ) );
}

#define AA 3

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
     // camera movement	
	float an = 0.5*iTime;
	vec3 ro = vec3( 1.0*cos(an), 0.4, 1.0*sin(an) );
    vec3 ta = vec3( 0.0, 0.0, 0.0 );
    // camera matrix
    vec3 ww = normalize( ta - ro );
    vec3 uu = normalize( cross(ww,vec3(0.2,1.0,0.0) ) );
    vec3 vv = normalize( cross(uu,ww));



    vec3 tot = vec3(0.0);

    #if AA>1
    for( int m=0; m<AA; m++ )
    for( int n=0; n<AA; n++ )
    {
        // pixel coordinates
        vec2 o = vec2(float(m),float(n)) / float(AA) - 0.5;
        vec2 p = (2.0*(fragCoord+o)-iResolution.xy)/iResolution.y;
        #else    
        vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.y;
        #endif

	    // create view ray
        vec3 rd = normalize( p.x*uu + p.y*vv + 1.5*ww );

        // raymarch
        const float tmax = 5.0;
        float t = 0.0;
        for( int i=0; i<256; i++ )
        {
            vec3 pos = ro + t*rd;
            float h = map(pos);
            if( h<0.0001 || t>tmax ) break;
            t += h;
        }


        // shading/lighting	
        vec3 col = vec3(0.0);
        if( t<tmax )
        {
            vec3 pos = ro + t*rd;
            vec3 nor = calcNormal(pos);
            float dif = clamp( dot(nor,vec3(0.57703)), 0.0, 1.0 );
            float amb = 0.5 + 0.5*dot(nor,vec3(0.0,1.0,0.0));
            col = vec3(0.2,0.3,0.4)*amb + vec3(0.8,0.7,0.5)*dif;
        }

        // gamma        
        col = sqrt( col );
	    tot += col;
    #if AA>1
    }
    tot /= float(AA*AA);
    #endif

	fragColor = vec4( tot, 1.0 );
}

float plot(vec2 st, float pct)
{
    return smoothstep(pct - 0.02, pct, st.y) -
           smoothstep(pct, pct + 0.02, st.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

   	float y = smoothstep(0.1, 0.9, abs(sin(uv.x + iTime)));
    vec3 color = vec3(y);
    float pct = plot(uv, y);
    color = pct * vec3(0.0, 1.0, 0.0);

    for (int i = 2; i < 15; i += 1) {
      float iFloat = float(i) / 10.0;

      float stp = smoothstep(iFloat, 1.0 - iFloat, sin(uv.x + iTime));
      float plotted = plot(uv, stp);

      vec3 col = i % 2 == 0 ? vec3(1.0, 0.0, 0.0) : vec3(0.0, 0.0, 1.0); 
      color += plotted * col;
    }


    // Output to screen
    fragColor = vec4(color, 1.0);
}

#define AspectRatio  (iResolution.y / iResolution.x);

#define SSTEP(a, b, x) smoothstep((a)-(x), (a)+(x), b)


float inCircle(in vec2 pt, in float r) {
    pt.y *= AspectRatio;
    float dp = dot(pt, pt);
    return SSTEP(dp, r*r, 0.0015*r);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Background color;
    vec3 bgColor = vec3(1.0, 0.7, 0.0);

    vec3 col = bgColor;

    float t = 12.0f + iTime;

	// Time variation
    float vt1 = sin(0.25*t)*sin(-0.4*t);
    float vt2 = 0.5*(1.0 + sin((2.5-0.05*vt1)*t));
    float vt3 = sin(0.5*t);

    // Eyes
    float eyeSpace = 0.17;
    float eyeLevel = 0.7;
    vec2 leftEyePos = uv - vec2(0.5 - eyeSpace, eyeLevel);
    vec2 rightEyePos = uv - vec2(0.5 + eyeSpace, eyeLevel);

    vec3 backEyeColor = vec3(0.15, 0.15, 0.1);    
    float backEyeRadius = 0.1;
    float inLeftEye = inCircle(leftEyePos, backEyeRadius);
    float inRightEye = inCircle(rightEyePos, backEyeRadius);

    // back (black) eyes with contour
    col = mix(col, backEyeColor, inLeftEye + inCircle(leftEyePos, 1.05*backEyeRadius));
    col = mix(col, backEyeColor, inRightEye + inCircle(rightEyePos, 1.05*backEyeRadius));

    // front (white) eyes
    vec3 frontEyeColor = vec3(0.9, 0.9, 0.95);
    float frontEyeSpace = (0.3 + mix(0.0, 0.2, vt1)) * backEyeRadius;
    float frontEyeRadius = backEyeRadius - frontEyeSpace;
    col = mix(col, frontEyeColor, inLeftEye * inCircle(leftEyePos - frontEyeSpace * vec2( vt1, 0.4 + vt3), frontEyeRadius));
    col = mix(col, frontEyeColor, inRightEye * inCircle(rightEyePos - frontEyeSpace * vec2( vt1, 0.4 + vt3), frontEyeRadius));

    // shiny corner eyes
    frontEyeSpace *= 1.5;
    frontEyeRadius = mix(0.075, 0.75, abs(vt1)) * (backEyeRadius - frontEyeRadius);
    col = mix(col, frontEyeColor, inLeftEye * inCircle(leftEyePos + frontEyeSpace * vec2(0.75, 1.0), frontEyeRadius));
    col = mix(col, frontEyeColor, inRightEye * inCircle(rightEyePos + frontEyeSpace* vec2(0.75, 1.0), frontEyeRadius));

    // mouth
    {
        vec2 mouthPos = uv - vec2(0.5, 0.23 + 0.0125*vt2);

        vec2 pt = 2.0*mouthPos;
        pt.y -= abs(sin(pt.x * pt.x * 2.5));
        float d = dot(pt, pt);

        // contour
        float insideMouth = SSTEP(d, 0.1025, 0.005);
        col = mix( col, vec3(0.0), insideMouth);
        // inside
        insideMouth = SSTEP(d, 0.1, 0.005);
        vec3 mouthColor = vec3( 0.3, 0.0, 0.05);
        col = mix( col, mouthColor, insideMouth);


        // teeth
        pt = 50.0*(mouthPos - vec2(0.0, 0.11));

        float d1 = 2.25 * (2.0*(fract(0.25*pt.x + 0.5)-0.5));
        float d2 = 1.0 - sqrt(1.0 - 0.4*d1*d1);

        // curve teeth
        d = d2 + 3.0*(1.0 - sqrt( 1.0 - 0.025*pt.x*pt.x ));

        float isTeeth = insideMouth * SSTEP(d, pt.y, 0.15);
        vec3 teethColor = vec3(0.9, 0.85, 0.8);
        col = mix(col, teethColor, isTeeth);

        // tongue
        pt = (15.0 + 0.5*vt2)*(mouthPos - vec2(0.0, -0.125));
        d = -pt.x * sqrt( 1.5 - 2.0*pt.x*pt.x );
        float isTongue = insideMouth * SSTEP(pt.y, d, 0.05);
        vec3 tongueColor = vec3(0.95, 0.4, 0.5);
        col = mix(col, tongueColor, isTongue);
    }

    // doodly noodle
    {
        vec2 doodleCenter = 1.0*(uv - vec2(0.5, 0.2));

        vec2 pt = 20.0*doodleCenter;
        float ds = 9.50 * sin(pt.x) / pt.x;

        // inside
        float ds_top = inCircle(doodleCenter, 0.28);
        float ds_btm = 1.0 + pt.y - ds;
        ds = min(smoothstep( 0.0, 0.15, ds_btm), ds_top);
        col = mix( col, bgColor, ds);

        // contour
        ds_top -= inCircle(doodleCenter, 0.2788);
        ds_btm = smoothstep(0.0, 0.05, ds_btm) - smoothstep(0.05, 0.25, ds_btm);

        ds *= ds_btm + ds_top;
        col = mix( col, 0.15 * bgColor, ds);
	}   

    // Truffle
    vec3 truffleColor = vec3(0.2, 0.2, 0.15);
    vec2 trufflePos = uv - vec2(0.5, eyeLevel - 1.5*backEyeRadius);
    float truffleRadius = pow(abs(1.2*trufflePos.y), 14.0*trufflePos.y*trufflePos.y) * 0.08;
    col = mix(col, truffleColor, inCircle( trufflePos, truffleRadius ));


    // Output to screen
    fragColor = vec4(col, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float size=4.;
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv=uv*2.-1.;
    uv.x*=iResolution.x/iResolution.y;
    float d= length(uv);
    uv*=fract(d+iTime);//cos(d+iTime);

    // Time varying
    vec2 v=uv*size;
    uv=fract(uv*size); 

	float x=mod(v.x,2.);
    x=x-1.;
    x=sign(x);
    x=smoothstep(0.,1.,x);
    float y=mod(v.y,2.);
    y=y-1.;
    y=sign(y);
    y=smoothstep(0.,1.,y);
    float l =(x+y)==0.?1.:0.;
    float l2=(x+y)>1.?1.:0.;
    // Output to screen
    fragColor = vec4(l+l2);
}

float circle(vec2 p,vec2 o,float r){
	return length(p - o) - r;
}
float dist_1(vec2 p){
	float baseCircle = length(p) - 0.5;
    float waveCircle = fract(length(p) - iTime/1.5) - 0.2;
    float wave0 = p.y + 0.8 - 0.10 * cos(p.x * 5.0) * sin(iTime * 2.0);
    float wave3 = p.y - 0.8 - 0.10 * cos(p.x * 5.0) * sin(iTime * 2.0);
    return min(min(baseCircle,waveCircle),min(wave0,-wave3));

}
float dist_2(vec2 p){
    float result = 100000.0;
    for(float xi = -3.0; xi < 3.0; xi += 0.1){
        for(float yi = -3.0; yi < 3.0; yi += 0.1){
            vec2 tmp = vec2(xi+fract(cos(yi * 3.0)*iTime / 4.),yi);
        	float r = 0.04 - max(dist_1(tmp),0.00);
            result = min(result,circle(p,tmp,r));
        }
    }
    return result;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / min(iResolution.x, iResolution.y);
	vec3 col = dist_2(uv) > 0. ? vec3(94,191,140)/255. : vec3(58,37,34)/255.;
    fragColor = vec4(col,1.0);

}

float sdBox( vec3 p, vec3 b )
{
  vec3 d = abs(p) - b;
  return length(max(d,0.0))
         + min(max(d.x,max(d.y,d.z)),0.0); // remove this line for an only partially signed sdf 
}

float sdSphere( vec3 p, float s )
{
  return length(p)-s;
}

float opSmoothUnion( float d1, float d2, float k ) {
    float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) - k*h*(1.0-h); }

float opSmoothSubstraction( float d1, float d2, float k ) {
    float h = clamp( 0.5 - 0.5*(d2+d1)/k, 0.0, 1.0 );
    return mix( d2, -d1, h ) + k*h*(1.0-h); }

float opSmoothIntersection( float d1, float d2, float k ) {
    float h = clamp( 0.5 - 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) + k*h*(1.0-h); }




float map(in vec3 pos)
{	
    vec3 sphereSpacing = vec3(4.0, 1.0, 5.0);
    vec3 spherePos = vec3(0.5 * cos(iTime), 0.5 * sin(iTime), 0.);
    spherePos = vec3(0.,-2.0, mod(iTime, 10.) - 10.);
    vec3 q = mod(pos - spherePos, sphereSpacing) - 0.5 * sphereSpacing;
    float sphere = sdSphere(q, 0.25);


    vec3 boxPos = vec3(0.0, 0.0, 0.0);
    vec3 boxSpacing = sphereSpacing;
    vec3 qBox = mod(pos - boxPos, boxSpacing) - 0.5 * boxSpacing;

    float box = sdBox(qBox, vec3(0.2, 0.2, 0.2));

    float d = opSmoothUnion(sphere, box, 0.3);          


    float ground = pos.y - (-0.25);



    return min(d, ground);
}

vec3 calcNormal(in vec3 pos)
{
    vec2 e = vec2(0.0001, 0.0);
	return normalize(vec3(map(pos+e.xyy)-map(pos-e.xyy),
                          map(pos+e.yxy)-map(pos-e.yxy),
                          map(pos+e.yyx)-map(pos-e.yyx)));
}

float castRay(in vec3 ro, vec3 rd)
{
    float t = 0.0;
    float limit = 30.0;

    for (int i=0; i <100; i++)
    {
    	vec3 pos = ro + t*rd;

        float h = map(pos);

        if(h<0.001) break;


        t += h;

        if (t > limit) break;
    }

    if (t>limit) t = -1.0;

    return t;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from -1 to 1)
    vec2 p = (2.0 * fragCoord-iResolution.xy)/iResolution.y;

    vec3 ro = vec3(0.0, 0.0, 2.0);
    ro.y = 3.0 + sin(iTime) * 2.0;
    //ro.z += iTime * 10.;
    vec3 rd = normalize(vec3(p, -1.5));
	vec3 col = vec3(0.6, 0.7, 0.9) - 0.5 * rd.y;

    vec3 fogColor = vec3(0.7, 0.7, 0.8);

    col = mix(col, fogColor, exp(-10.0*rd.y));

    float t = castRay(ro, rd);


    if (t > 0.0)
    {
        vec3 pos = ro + t * rd;
        vec3 nor = calcNormal(pos);

        vec3 mate = vec3(0.18);

        vec3 sunDir = normalize(vec3(0.8, 0.2, 0.2));
        float sunDif = clamp(dot(nor, sunDir), 0.0, 1.0);
        float skyDif = clamp(0.5 + 0.5 *dot(nor, vec3(0.0, 1.0, 0.0)), 0.0, 1.0);
        float sunShadow = step(castRay(pos + nor*0.001, sunDir), 0.0);
        float bounceDif = clamp(0.5 + 0.5 *dot(nor, vec3(0.0, -1.0, 0.0)), 0.0, 1.0);

        // main light
        col = mate * vec3(7.0, 5.0, 3.0) * sunDif*sunShadow;
        // sky light
        col += mate * vec3(0.5, 0.8, 0.9) * skyDif;
		// bounce light
        col += mate * vec3(0.7, 0.3, 0.2) * bounceDif;

    }

    col = pow(col, vec3(0.4545));



    // Output to screen
    fragColor = vec4(col,1.0);
}

