#define true				1
#define false				0

#define PI 3.14

#define saturate(x) 		clamp(x, 0.0, 1.0)

#define MENGER_ITERATIONS	2
#define SOFT_SHADOW_STEPS 	16

#define INTERSECT_STEPS		350
#define INTERSECT_MIN_DIST	0.0001
#define INTERSECT_MAX_DIST	100.0


vec2 rotate2D(vec2 p, float angle)
{
    float sRot = sin(angle);
    float cRot = cos(angle);
    return p * cRot + p.yx * sRot * vec2(-1.0, 1.0);
}

float sdRoundBox(vec3 p, vec3 b, float r)
{
     return length(max(abs(p) - b, 0.0)) - r;
}

float sdPlane(vec3 p, float height)
{
     return p.y - height;
}

vec4 opU(vec4 d1, vec4 d2)
{
     return (d1.x < d2.x) ? d1 : d2;
}

vec4 map(in vec3 p)
{
    p.xz = mod(p.xz + 1.0, 2.0) - 1.0;
    p.y  = mod(p.y + 1.0, 2.0) - 1.0;

    float d = sdRoundBox(p, vec3(0.5), 1.0);
    vec4 res = vec4(d, 1.0, 0.0, 0.0);

    //....................................................................
    // Based on https://www.iquilezles.org/www/articles/menger/menger.htm
    //....................................................................
    float s = 8.0;
    for(int i = 0; i < MENGER_ITERATIONS; ++i)
    {	 
        vec3 a = mod(p * s, 2.0) - 1.0;
        s *= 8.0;
        vec3 r = abs(1.0 - 5.0 * abs(a));
        float da = max(r.x, r.y);
        float db = max(r.y, r.z);
        float dc = max(r.z, r.x);
        float c = (min(da, min(db, dc)) - 1.0) / s;

        if(c > d)
        {
            d = c;
            res = vec4(d, min(res.y, 0.2 * da * db * dc), 0.0, 1.0);
        }
    }
    //....................................................................

    vec4 ground = vec4(sdPlane(p, -0.05), -0.5, 0.0, 1.0);
    res = opU(ground, res);

    return res;
}

vec4 intersect(in vec3 ro, in vec3 rd)
{
    float t = 0.0;
    vec4 res = vec4(-1.0);
    vec4 h = vec4(1.0);
    for (int i = 0; i < INTERSECT_STEPS; ++i)
    {
		if(h.x < INTERSECT_MIN_DIST || t > INTERSECT_MAX_DIST) 
		{
	    	break;
		}

        h = map(ro + rd * t);
        res = vec4(t, h.yzw);
        t += h.x;
    }

    if (t > INTERSECT_MAX_DIST) 
    {
		res = vec4(-1.0);
    }

    return res;
}

float softshadow(in vec3 ro, in vec3 rd)
{
    float res = 1.0;
    float t = 0.0;
    for (int i = 0; i < SOFT_SHADOW_STEPS; ++i)
    {
		vec3 pos = ro + rd * t;
        float h = map(pos).x;
        res = min(res, float(SOFT_SHADOW_STEPS) * h / t);
        if(res < 0.001)
		{
            break;
		}
        t += clamp(h, 0.0, 0.2);
    }
    return saturate(res);
}

vec3 calcNormal(in vec3 pos)
{
    vec3 eps = vec3(0.0001, 0.0, 0.0);
    vec3 n;
    n.x = map(pos + eps.xyy).x - map(pos - eps.xyy).x;
    n.y = map(pos + eps.yxy).x - map(pos - eps.yxy).x;
    n.z = map(pos + eps.yyx).x - map(pos - eps.yyx).x;
    return normalize(n);
}

vec3 render(in vec3 ro, in vec3 rd)
{
    vec3 color = vec3(0.5);
    vec4 res = intersect(ro,rd);
    if(res.x > 0.0)
    {   
        const vec3 light1 = vec3(0.5, 0.5, -0.5);
        vec3 pos = ro + res.x * rd;
        vec3 baseColor = vec3(0.0);
        if (res.y == -0.5)
		{           
            baseColor = vec3(0.0, 1.0, 1.0);
		}
        else if (res.y > 0.0)
        {
            baseColor = mix(vec3(0.0, 0.4, 0.6), vec3(0.0, 0.0, 0.0), res.y);
        }

        vec3 ambient = vec3(0.2) * baseColor;

        vec3 normal = calcNormal(pos);
        vec3 reflection = reflect(rd, normal);

        float occ = res.y;
        float shadow1 = softshadow(pos + 0.001 * normal, light1);

        vec3 diffuse = baseColor * shadow1 * occ;

        color = diffuse + ambient;		
        color += 0.8 * smoothstep(0.0, 0.1, reflection.y) * softshadow(pos + 0.1 * normal, reflection);
    }

    return pow(color, vec3(0.4545));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) 
{
    vec2 p = 2.0 * (fragCoord.xy / iResolution.xy) - 1.0;
    p.x *= iResolution.x / iResolution.y;

    vec2 mouseXY = iMouse.xy / iResolution.xy;

    // camera
    vec3 ro = vec3(8.75, 1.0, -2.0);
    vec3 ww = normalize(vec3(0.0, mouseXY.y * 5.0 * PI, 0.0) - ro);
    ww.xz = rotate2D(ww.xz, -mouseXY.x * 2.0 * PI);
    vec3 uu = normalize(cross(vec3(0.0, 1.0, 0.0), ww));
    vec3 vv = normalize(cross(ww, uu));
    vec3 rd = normalize(p.x * uu + p.y * vv + 2.0 * ww);

    ro.x -= iTime * 0.05; //Camera movement

    vec3 color = render(ro + vec3(0.0, -1.0, 0.0), rd);

    fragColor = vec4(color, 1.0);
}

const float speed = 0.3;

float cnoise(vec2 p);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = fragCoord/iResolution.xy * 2.;

    float amp = 1.;
    float freq = 2.;
    float v = 0.;
    for (int i = 0; i < 2; i++) {
        v += amp * cnoise(freq * st + cnoise(st + vec2(-iTime, iTime) * speed));
        st *= 2.;
        amp *= .5;
    }

    v = abs(v);

    // Output to screen
    fragColor = vec4(vec3(v),1.0);
}


// GLSL textureless classic 2D noise "cnoise",
// with an RSL-style periodic variant "pnoise".
// Author:  Stefan Gustavson (stefan.gustavson@liu.se)
// Version: 2011-08-22
//
// Many thanks to Ian McEwan of Ashima Arts for the
// ideas for permutation and gradient selection.
