vec4 mod289(vec4 x)
{
  return x - floor(x * (1.0 / 289.0)) * 289.0;
}

vec4 permute(vec4 x)
{
  return mod289(((x*34.0)+1.0)*x);
}

vec4 taylorInvSqrt(vec4 r)
{
  return 1.79284291400159 - 0.85373472095314 * r;
}

vec2 fade(vec2 t) {
  return t*t*t*(t*(t*6.0-15.0)+10.0);
}

// Classic Perlin noise
float cnoise(vec2 P)
{
  vec4 Pi = floor(P.xyxy) + vec4(0.0, 0.0, 1.0, 1.0);
  vec4 Pf = fract(P.xyxy) - vec4(0.0, 0.0, 1.0, 1.0);
  Pi = mod289(Pi); // To avoid truncation effects in permutation
  vec4 ix = Pi.xzxz;
  vec4 iy = Pi.yyww;
  vec4 fx = Pf.xzxz;
  vec4 fy = Pf.yyww;

  vec4 i = permute(permute(ix) + iy);

  vec4 gx = fract(i * (1.0 / 41.0)) * 2.0 - 1.0 ;
  vec4 gy = abs(gx) - 0.5 ;
  vec4 tx = floor(gx + 0.5);
  gx = gx - tx;

  vec2 g00 = vec2(gx.x,gy.x);
  vec2 g10 = vec2(gx.y,gy.y);
  vec2 g01 = vec2(gx.z,gy.z);
  vec2 g11 = vec2(gx.w,gy.w);

  vec4 norm = taylorInvSqrt(vec4(dot(g00, g00), dot(g01, g01), dot(g10, g10), dot(g11, g11)));
  g00 *= norm.x;  
  g01 *= norm.y;  
  g10 *= norm.z;  
  g11 *= norm.w;  

  float n00 = dot(g00, vec2(fx.x, fy.x));
  float n10 = dot(g10, vec2(fx.y, fy.y));
  float n01 = dot(g01, vec2(fx.z, fy.z));
  float n11 = dot(g11, vec2(fx.w, fy.w));

  vec2 fade_xy = fade(Pf.xy);
  vec2 n_x = mix(vec2(n00, n01), vec2(n10, n11), fade_xy.x);
  float n_xy = mix(n_x.x, n_x.y, fade_xy.y);
  return 2.3 * n_xy;
}

//====================================
// Quaternion
//====================================
vec4 qmul(vec4 q1, vec4 q2) {
	return vec4(
		q2.xyz * q1.w + q1.xyz * q2.w + cross(q1.xyz, q2.xyz),
		q1.w * q2.w - dot(q1.xyz, q2.xyz)
	);
}

vec3 rotVec(vec3 v, vec4 r) {
	vec4 r_c = r * vec4(-1, -1, -1, 1);
	return qmul(r, qmul(vec4(v, 0), r_c)).xyz;
}

vec4 qaxis(float angle, vec3 axis) {
	float sn = sin(angle * 0.5);
	float cs = cos(angle * 0.5);
	return vec4(axis * sn, cs);
}

//====================================
// Distance functions
//====================================
float dist(vec3 pos)
{
    float d = 2.0 - length(pos.xy);
    float scale = 1.0;

    for (int i = 0; i < 10; ++i)
    {
        vec3 p = mod(pos, 8.0) - 4.0;
    	d = max(d, ((sin(iTime + float(i) * 0.3) * 1.5 + 1.0) - length(p.x)) / scale);
        vec4 rot = qaxis(0.8, normalize(vec3(1, 1, 1)));
        rot = qmul(rot, qaxis(iTime * 0.003 + float(i), vec3(0, 0, 1)));
    	pos = rotVec(pos, rot);
        pos *= 1.2;
        scale *= 1.2;
    }
    return d;
}

//====================================
// Normal
//====================================
vec3 calcNormal(vec3 pos)
{
    vec2 ep = vec2(0.01, 0);
    float d0 = dist(pos);
    return normalize(vec3(
        d0 - dist(pos - ep.xyy),
        d0 - dist(pos - ep.yxy),
        d0 - dist(pos - ep.yyx)
    ));
}

//====================================
// Color
//====================================
vec3 calcColor(vec2 uv)
{
    vec4 rot = qaxis(sin(iTime) * 0.1, vec3(1, 0, 0));
    rot = qmul(rot, qaxis(sin(iTime * 0.8) * 0.1, vec3(0, 1, 0)));
    rot = qmul(rot, qaxis(iTime * 0.5, vec3(0, 0, 1)));
    vec3 lightDir = rotVec(normalize(vec3(1, 1, -1)), rot);
    vec3 pos = vec3(0, 0, mod(iTime, 100.0) * 10.0);
    vec3 dir = rotVec(normalize(vec3(uv, 1)), rot);

    for (int i = 0; i < 128; ++i)
    {
        float d = dist(pos);
        if (d < 0.001)
        {
            vec3 albedo = vec3(1, 1, 0.5);
            vec3 norm = calcNormal(pos);
            float atten = max(0.0, dot(norm, lightDir));
            float fog = float(i) / 128.0;
            return mix(atten * albedo, vec3(1.0, 1.0, 1.0), fog);
        }
        pos += d * dir;
    }

    return vec3(1, 1, 1);
}

//====================================
// Entry point
//====================================
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - iResolution.xy / 2.0) / iResolution.y;
    fragColor = vec4(calcColor(uv), 1.0);
}

float PI = 3.14159265;
mat2 genRot(float v){
	return mat2(cos(v),-sin(v),sin(v),cos(v));
}
vec3 pmod(vec3 p,float c){
    float tmp = PI * 2. / c;
	float l = length(p.xy);
    float theta = atan(p.y/p.x);
    theta = mod(theta,PI * 2. / c);
    return vec3(l * cos(theta), l * sin(theta),p.z);

}
vec3 cellMod(vec3 p,vec3 c){
    p.z = mod(p.z + c.z / 2.,c.z) - c.z/2.;
	p.x = mod(p.x + c.x / 2.,c.x) - c.x/2.;
    p.y = mod(p.y + c.y / 2.,c.y) - c.y/2.;
    return p;
}

float cube(vec3 p, vec3 o){
	return max(
    	abs(p.x - o.x),
        max(abs(p.y - o.y),
            abs(p.z - o.z))
    );
}

float map(vec3 p){
	float base = 100000.;
    for(int i = 0; i < 5; i++){
        base = min(base,max(cube(p,vec3(0.))-0.5,-(length(p)-0.6) ));
    	p.x = abs(p.x);
        p.z = abs(p.z);
        p.y = abs(p.y);
        p.xy *= genRot(PI / 12.);        
        p.yz *= genRot(PI / 12.);
        p.zx *= genRot(PI / 12.);

        p = (p - vec3(0.4)) * 2.0;
    }

    return base;

}

const float EPS = 0.001;
vec3 getNormal(vec3 p) {
    return normalize(vec3(
        map(p + vec3(EPS, 0.0, 0.0)) - map(p + vec3(-EPS,  0.0,  0.0)),
        map(p + vec3(0.0, EPS, 0.0)) - map(p + vec3( 0.0, -EPS,  0.0)),
        map(p + vec3(0.0, 0.0, EPS)) - map(p + vec3( 0.0,  0.0, -EPS))
    ));
}

vec4 trace (vec3 o, vec3 r){
	float t = 0.0;
    vec3 p = vec3(0.0,0.0,0.0);

    for(int i = 0; i < 128; ++i){
        p = o + r * t;
        float d = map(p);
        t += d * 0.15;
    }
    return vec4(getNormal(p),t);
}


void mainImage( out vec4 fragColor, in vec2 U )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec3 R = iResolution,
    r = normalize(vec3((2.*U - R.xy )/  R.y,1.2)),
    o = vec3((1.5+ 0.5 * sin(iTime)) *sin(iTime),0.,(1.5+ 0.5 * sin(iTime))*-cos(iTime));
    r.xz *= genRot(iTime);
    vec4 data = trace(o,r);
    vec3 n = vec3(data.xyz);
    float t = data.w;
    float fog = 1.0 / (1.0 + t * t * 0.05);
    float tmp = sqrt(dot(n,r));
vec3 fc = mix(vec3(0.5 - data.x,0.5 -data.y,0.5-data.z),vec3(0.),1.0 - fog);
    //fc = vec3(fog);
    // Output to screen
    fragColor = vec4(fc,1.0);
}

float PI = 3.14159265;
float sTime(float scale){
	float a = floor(iTime * scale);
    float b = smoothstep(0.,1.,fract(iTime * scale));
    return a + b;
}
mat2 genRot(float v){
	 return mat2(cos(v),-sin(v),sin(v),cos(v));
}
vec3 modC(vec3 p,vec3 b){
	p = (fract(p / b + 0.5)-0.5) * b;
    return p;
}

float maxEx(vec3 p){
	return max(p.x,max(p.y,p.z));
}

float map(vec3 p){
    //p.xy = abs(p.xy);
    float tmp = 2.;
    p.xy += tmp/2.;
    p = modC(p,vec3(tmp));
    vec3 q = p;
	float sp = length(p) - 0.4;
    sp = min(sp,length(p.yz) - 0.1);
    sp = min(sp,length(p.xz) - 0.1);
    sp = min(sp,length(p.xy) - 0.1);

    q.xy *= genRot(PI/4.);
    q.xz *= genRot(iTime);
    float cb = maxEx(abs(q)) - 0.5;
    cb = max(cb, -(max(abs(q.x),abs(q.y)) - 0.25));
    cb = max(cb, -(max(abs(q.x),abs(q.z)) - 0.25));
    cb = max(cb, -(max(abs(q.z),abs(q.y)) - 0.25));

    return mix(sp,cb,sin(fract(sTime(0.5)) * PI));
}
vec3 getNormal(vec3 p){
	vec3 x= dFdx(p);
    vec3 y = dFdy(p);
    return normalize(cross(x,y));
}

vec4 trace(vec3 o,vec3 r){
    float t = 0.;
    for(int i = 0; i < 96; i++){
    	vec3 p = o + r * t;
        float d = map(p);
        t += d * 0.5;
    }
    vec3 p = o + r * t;
    vec3 n = getNormal(p);
    return vec4(n,t);
}

vec3 ray(vec2 uv,float z){
	vec3 r = normalize(vec3(uv,z));
    r.xz *= genRot(PI / 12.);
    r.yz *= genRot(PI / 12.);

    r.xy *= genRot(cos(sTime(0.125) * PI) * 0.75 * PI);
    return r;
}
vec3 cam(){
	vec3 c = vec3(0.,0.,-2.5 + iTime * 4.);
    return c;
}

vec3 getColor(vec3 o,vec3 r,vec4 d){
	float t = d.w;
    vec3 n = d.xyz;
    float a = dot(r,n);
    vec3 bc = vec3(1. - a *0.75);
    vec3 cc;
    float at = atan(r.y/r.x) * 2.;
    cc.x = sin(sTime(1.) + at);
    cc.y = sin(sTime(1.) + at + PI  *2. / 3.);
    cc.z = sin(sTime(1.) + at + PI * 4. / 3.);
    cc = cc * 0.5 + 0.5;
    vec3 p = o + r * t;
    cc = fract((length(p.z) + iTime) / 2.) < 0.75 ? cc : vec3(1.);
    bc *= cc * 1.5;

    float fog = 1./(1. + t * t * 0.05);
    bc = mix(bc,vec3(0.),1.-fog);
    return vec3(bc);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord.xy * 2. - iResolution.xy)/iResolution.x;
	vec3 o = cam();
    vec3 r = ray(uv,1. * sin(iTime * 0.25));
    vec4 d = trace(o,r);
    // Time varying pixel color
    vec3 col = getColor(o,r,d);

    // Output to screen
    fragColor = vec4(col,1.0);
}

# define PI 3.141592653589793
float rand(vec2 co){
    return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy/iResolution.y;
    vec2 u = fract(uv.xy*5.);
    vec2 s = fract(uv.xy*10.+0.5);

    u-=0.5;
    vec2 g = iTime*0.2+floor(uv*5.);

    float r = .7;
    float angle = PI*0.25+floor(rand(floor(g))*5.)*PI*.5;      
    u.x+=sin(angle)*r;
    u.y+=cos(angle)*r;

    float d = length(u);
 	float k = smoothstep(d,d*2.0,0.5);
    k =fract(d*angle/11.1 - iTime*sin(angle*123.4)*0.1);

    k -= step(length(s-0.5),0.02);
    k= floor(k+0.5);
    fragColor = vec4(k);
}

