vec4 getPos(float t)
{    
    float t1 = 1.-t;   
    return vec4(t1*t1*t1, 3.*t1*t1*t, 3.*t1*t*t, t*t*t);
}

#define THI .079

#ifdef LINES
float doSomething(int icharPos, int nextLine, vec2 p, float m) {
    if( icharPos >=32*4 && icharPos<40*4) m =pow(m,.4);
    mat4 My =         
        transpose( mat4(
        mix(chars[icharPos  ],chars[nextLine  ],m),
        mix(chars[icharPos+1],chars[nextLine+1],m),
   		mix(chars[icharPos+2],chars[nextLine+2],m),
        mix(chars[icharPos+3],chars[nextLine+3],m) ));
    if( icharPos ==60||icharPos ==92) My *= (1.+.25*sin(6.28*sin(clamp(5.*m,0.,1.57))));
    if( icharPos==4) My = mat(12.56*m)*My;
    float d=10.;
    for(float i1=0.; i1<1.-STEP; i1+=STEP) {
        vec4 h0 = My*getPos(i1    );
        vec4 h1 = My*getPos(i1+STEP);
        float h = distance(h0.xy,h1.xy);
        d = min(d,distance(p, h0.xy ));   
        d = min(d,distance(p, h1.xy ));   
        if(distance(p,h0.xy)<h+THI && distance(p,h1.xy)<h+THI) {
            if(h1.x==h0.x) d=abs(p.y-h0.y);
            else {
            	float f=(h1.y-h0.y)/(h1.x-h0.x);
            	d = min(d, abs((p.y-h0.y-f*(p.x-h0.x))) / sqrt(1.+f*f));
            }
        }
        h = distance(h0.zw,h1.zw);
        d = min(d,distance(p, h0.zw ));   
        d = min(d,distance(p, h1.zw ));   
