const int max_iterations = 16;
const float stop_threshold = 0.001;
const float grad_step = 0.0001;
const float clip_far = 10.0;

const float PI = 3.14159265359;
const float DEG_TO_RAD = PI / 180.0;

vec2 translate(vec2 pos, vec2 offset){
	return pos - offset;
}

float circle(vec2 pos, float radius){
	return length(pos) - radius;
}

float time = 0.0;
vec2 screen_size;
vec2 texcoord;

float scene(vec2 pos) {
	float dist = 0.61+cos((time)*0.5)*0.6;
	float xr = 0.2;
	float yr = 0.4;
	float speed = (time)*0.4+pos.x+pos.y;
	for (float i = 0.0; i < 16.0; i+=1.0) {
		vec2 circlePos = translate(pos, vec2(cos(i*0.1*speed)*xr, sin(i*0.1*speed)*yr));
		float scenedist = circle(circlePos*screen_size*0.0015, 0.1-i*0.005);
		dist = min(scenedist,dist);
	}
	return 3.0-dist*screen_size.x*0.1;
}

float rand(vec2 co){
	return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}


float sdBox( vec3 p, vec3 b ) {
  vec3 d = abs(p) - b;
  return min(max(d.x,max(d.y,d.z)),0.0) + length(max(d,0.0));
}
float dist( vec3 p ) {
    float d = sdBox( p, vec3(0.5) );
    return d;
}

vec3 gradient( vec3 pos ) {
	const vec3 dx = vec3(grad_step, 0.0, 0.0);
	const vec3 dy = vec3(0.0, grad_step, 0.0);
	const vec3 dz = vec3(0.0, 0.0, grad_step);
	return normalize (
		vec3(
			dist( pos + dx ) - dist(pos - dx),
			dist( pos + dy ) - dist(pos - dy),
			dist( pos + dz ) - dist(pos - dz)			
		)
	);
}

bool ray_marching( vec3 o, vec3 dir, inout float depth, inout vec3 n ) {
	float t = 0.0;
    float d = 10000.0;
    float dt = 0.0;
    for ( int i = 0; i < 128; i++ ) {
        vec3 v = o + dir * t;
        d = dist( v );
        if ( d < 0.001 ) {
            break;
        }
        dt = min( abs(d), 0.1 );
        t += dt;
        if ( t > depth ) {
            break;
        }
    }

    if ( d >= 0.001 ) {
        return false;
    }

    t -= dt;
    for ( int i = 0; i < 4; i++ ) {
        dt *= 0.5;

        vec3 v = o + dir * ( t + dt );
        if ( dist( v ) >= 0.001 ) {
            t += dt;
        }
    }

    depth = t;
    n = normalize( gradient( o + dir * t ) );
    return true;

    return true;
}

vec3 ray_dir( float fov, vec2 size, vec2 pos ) {
	vec2 xy = pos - size * 0.5;

	float cot_half_fov = tan( ( 90.0 - fov * 0.5 ) * DEG_TO_RAD );	
	float z = size.y * 0.5 * cot_half_fov;

	return normalize( vec3( xy, -z ) );
}

mat3 rotationXY( vec2 angle ) {
	vec2 c = cos( angle );
	vec2 s = sin( angle );

	return mat3(
		c.y      ,  0.0, -s.y,
		s.y * s.x,  c.x,  c.y * s.x,
		s.y * c.x, -s.x,  c.y * c.x
	);
}


vec4 colormap_hsv2rgb(float h, float s, float v) {
	float r = v;
	float g = v;
	float b = v;
	if (s > 0.0) {
		h *= 6.0;
		int i = int(h);
		float f = h - float(i);
		if (i == 1) {
			r *= 1.0 - s * f;
			b *= 1.0 - s;
		} else if (i == 2) {
			r *= 1.0 - s;
			b *= 1.0 - s * (1.0 - f);
		} else if (i == 3) {
			r *= 1.0 - s;
			g *= 1.0 - s * f;
		} else if (i == 4) {
			r *= 1.0 - s * (1.0 - f);
			g *= 1.0 - s;
		} else if (i == 5) {
			g *= 1.0 - s;
			b *= 1.0 - s * f;
		} else {
			g *= 1.0 - s * (1.0 - f);
			b *= 1.0 - s;
		}
	}
	return vec4(r, g, b, 1.0);
}

vec4 colormap(float x) {
	if (x < 0.0) {
		return vec4(0.0, 0.0, 0.0, 1.0);
	} else if (1.0 < x) {
		return vec4(0.0, 0.0, 0.0, 1.0);
	} else {
		float h = clamp(-9.42274071356572E-01 * x + 8.74326827903982E-01, 0.0, 1.0);
		float s = 1.0;
		float v = clamp(4.90125513855204E+00 * x + 9.18879034690780E-03, 0.0, 1.0);
		return colormap_hsv2rgb(h, s, v);
	}
}

vec4 kuutio( in vec2 uv )
{
	float zz = 35.;

    float vv = sin(time*0.4);
	if (vv < 0.0) vv = 0.;
	zz+=vv*cos(time+sin(time*0.7*distance(texcoord.xy*0.1,vec2(0.5,0.5))))*8.;
	vec3 dir = ray_dir(zz, screen_size.xy, texcoord.xy*screen_size.xy );

	vec3 eye = vec3( 0.0, 0.0, 3.5 );

	mat3 rot = rotationXY(vec2((time)*0.75,(time)*0.5));
	dir = rot * dir;
	eye = rot * eye;

    float depth = clip_far;

    vec3 n = vec3( 0.0 );
	if ( !ray_marching( eye, dir, depth, n ) ) {
        return vec4(1.0,1.0,1.0,0.0);
	}

    vec3 color = vec3(1.0-abs(cos((texcoord.y*screen_size.y*cos(time)*1.2*eye.xyz*cos(time*0.2*texcoord.y)*0.65)*0.01+abs(cos(depth*5.+time)*2.)*(0.05+sin(time*0.5)*0.03)*0.5)));
	color = clamp(color,0.0,1.0);
    return vec4( pow( colormap(((color.x+color.y+color.z)/3.)*0.4).rgb+color, vec3(1.0/2.2) ), 1.0 );
}



