	vec4 col = vec4(cos(time*0.05)*100.5-dist, 10.5-dist, 1.8-dist, 1)*0.5;
	vec4 bg = 2.0-vec4(atan(uv.y*uv.x*32.0-(time)*0.5)-rand(uv*(time)*0.05))*4.;
	vec4 bg2 = vec4(1.0+0.7*cos(uv.y*uv.x*32.0-time*0.5)-rand(uv*(time)*0.0000005))*1.0;

	bg = -0.5*min(bg,-bg2);
	col = min(col, bg*vec4(1.2,0.6+cos((time)*0.25+col.b)*0.2,1.2,1.0));

