const vec4 cross_color = vec4(0.008,0.018,0.270,1.000);

// Transformations
vec2 rotate( vec2 uv, float degree ){
  float theta = -degree * deg2rad;
  return vec2( cos(theta) * uv.x - sin(theta) * uv.y,
			   sin(theta) * uv.x + cos(theta) * uv.y
			   );
 }

vec2 blind_offset( vec2 uv, vec2 offset, float width ){
  //vec2 fragCoord.xy = uv * iResolution;
  if ( mod( uv.t * iResolution.t / width, 2.0 ) > 1.0 ) {
	uv *= offset;
	}
  return uv;
  }

vec2 tile( vec2 uv, float tiles ){
  return fract( uv * vec2( tiles ) - vec2( 0.5 ) );
  }

// Shapes
vec4 draw_rect(float width, float height, vec2 uv, vec4 color) {
  vec4 canvas = vec4(0.0);
  if ( -width < uv.s && uv.s < width  ) {
	if ( -height < uv.t && uv.t < height ) {
	  canvas = color;
	  }
    }
  return canvas;
  }

vec4 draw_cross( float width, float height, vec2 uv, vec4 color) {
  vec4 canvas = vec4(0.0);
  vec4 rect1 = draw_rect( width, height / 2.0, uv, color );
  vec4 rect2 = draw_rect( width, height / 2.0, rotate( uv, 90.0 ), color );
  canvas = mix(rect1,rect2,rect2.a);
  return canvas;
  }

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 st = fragCoord/iResolution.xy;
  st -= vec2 (0.5);
  st.x *= iResolution.x/iResolution.y;
  st *= vec2( abs(cos(length( st ) * cos(iTime / 10.0) * 10.0)) ); //multiply by distance from center
  st = rotate( st, iTime * 10.0 );
  st = blind_offset( st, vec2( 0.01, 0.01), sqrt(length(st) - 0.25) * 10.0);
  st = tile( st, 3.0 );
  vec4 color = vec4( length(st)/2.0, ( 1.0 - length(st - vec2(0.5)) + (sin(iTime)-0.5)/2.0) * 0.7, 0.5, 1.0 );
  st -= vec2 (0.5, 0.5);
  st = rotate( st, iTime * -30.0 );
  vec4 cros = draw_rect( 0.05 * (sin(iTime / 10.0) + 1.0), 0.5, st, cross_color);
  color = mix( color, cros, cros.a );
  fragColor = color;
  }

#define PI 3.14159265358979323846264338327950288
#define SETP_MAX 100

#define NEAR 0.01
#define FAR 1000.0

#define OFFSET_TO_NORMAL 0.001



vec3 Background (vec2 uv) {
    return vec3(0.0, 0.0, 0.0);

    uv.x -= iTime * 0.5 + sin(iTime * 0.2);
    uv.y -= iTime * 0.1 + sin(iTime * 0.7);

	float offsetR = sin(iTime * 2.0 + PI * 0.0);
    float offsetG = sin(iTime * 2.0 + PI * 0.33333333) ;
    float offsetB = sin(iTime * 2.0 + PI * 0.66666666) ;

    vec3 col = vec3(abs(sin(uv.x * 50.0)) * offsetR,
                    abs(sin(uv.x * 50.0 + PI * 0.33333333)) * offsetG,
                    abs(sin(uv.x * 50.0 + PI * 0.66666666)) * offsetB) * 0.5 ;

    col += vec3(abs(sin(uv.y * 50.0)) ,
                abs(sin(uv.y * 50.0 + PI * 0.33333333)),
                abs(sin(uv.y * 50.0 + PI * 0.66666666))) * 0.5;

    return col;
}



float DstSphere (vec3 currentStep, vec3 origin, float radius) {
	return length(origin - currentStep) - radius;
}


float DstPlane (vec3 currentStep, float height) {
	return currentStep.y - height;
}



float sdRoundBox( vec3 p, vec3 b, float r )
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0) - r;
}

float sdBox( vec3 p, vec3 b )
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}





float SmoothSubtraction (float a, float b, float u) {
    float v = clamp(0.5 - 0.5 * (a + b) / u, 0.0, 1.0);
	return mix(b, -a, v) + u * v * (1.0 - v);
}

float smin( float a, float b, float k )
{
    float h = clamp( 0.5+0.5*(b-a)/k, 0.0, 1.0 );
    return mix( b, a, h ) - k*h*(1.0-h);
}

vec2 sminCubic( float a, float b, float k )
{
    float h = max( k-abs(a-b), 0.0 )/k;
    float m = h*h*h*0.5;
    float s = m*k*(1.0/3.0); 
    return (a<b) ? vec2(a-s,m) : vec2(b-s,1.0-m);
}


float World (vec3 currentStep) {

    float world = 0.0;




    float sphere0 = DstSphere (currentStep , vec3(-0.6, 0.3, 1.2 + 0.4 * sin(iTime * 1.1)), 0.6);
    float sphere1 = DstSphere (currentStep , vec3(-0.6, 0.3, 2.5), 1.8);

    float plane = DstPlane(currentStep, -0.8);




    float brick0 = sdRoundBox(currentStep + vec3(-1.9 + sin(iTime * 1.5), -0.5, -0.5), vec3(0.5, 0.5, 0.01), 0.2);





    world = SmoothSubtraction(sphere0, sphere1, 0.5);
    world = min(plane, world);

    //world = min(brick0, world);
    world = smin(brick0, world, 1.1);



    world = min(world, 1.0);

    return world;
}



vec3 raymarching (vec3 origin, vec3 dir) {
	float dist = 0.0;
    int currentStepCount = 0;

    while(true) {
        float world = World(origin + dir * dist);
        dist += world;


        if(dist >  FAR)
            return vec3(0.0, 0.0, 0.0);

        if(world < NEAR || currentStepCount > SETP_MAX)
            break;

        currentStepCount++;
    }

    return dir * dist;
}


float GetShadow (vec3 origin, vec3 lightPos) {
    vec3 dir = normalize(origin - lightPos);
    return distance(raymarching(lightPos, dir), origin) < NEAR ? 1.0 : 0.0;
}



