float random (in float x) { return fract(sin(x) * 1e4); }
float random (in vec2 _st) { return fract(sin(dot(_st.xy, vec2(12.9898, 78.233))) * 43758.5453123); }

// credit to @patriciogv from The Book of Shaders
// https://thebookofshaders.com/edit.php#10/ikeda-04.frag
float DeFrag(in vec2 _st)
{
	vec2 tmpSpace = (_st / 2.0) - vec2(0.08, -0.09);
    vec2 grid = vec2(100.0,50.);
    tmpSpace *= grid;

    vec2 ipos = floor(tmpSpace);

    vec2 vel = floor(vec2(1.0));

    float totalCells = grid.x * grid.y;
    float t = mod(48.989 * max(grid.x, grid.y), totalCells);
    vec2 head = vec2(mod(t, grid.x), floor(t / grid.x));

    vec2 offset = vec2(0.0, 0.0);

    float color = 1.0;
    color *= step(grid.y - head.y, ipos.y);
    color = clamp(color, 0.0, 1.0);

    color *= random(floor(tmpSpace + vel));    
    color *= random( floor( 1.0 - color * sin( (iTime * color) / 3.0) ) );

    color *= step(0.075, fract(tmpSpace.x + vel.x)) * step(0.2, fract(tmpSpace.y + vel.y));

	return color;
}

vec2 rotate( in vec2 _st, in float angle )
{
	return mat2( cos(angle * PI), -sin(angle * PI),
                 sin(angle * PI), cos(angle * PI) ) * _st;
}

float rect( in vec2 _st, in vec2 xCoords, in vec2 yCoords)
{
    vec2 bl = step( vec2(xCoords.x, yCoords.x), _st );
    vec2 tr = step( vec2(xCoords.y, yCoords.y), 1.0 - _st );

	return bl.x * bl.y * tr.x * tr.y;
}

float vaporGrid(in vec2 _st)
{
    float vaporGrid     = 0.0;
    vec2 lineDirection  = vec2(cos(PI), sin(PI));

    vec2 tmpSpace       = _st;

    if(tmpSpace.y < 1.0)
        tmpSpace.y = mod(0.58 + tmpSpace.y, 1.0);
    else tmpSpace.y = 1.0;
        vaporGrid +=
            1.0 - rect( (tmpSpace * vec2(0.35, 0.9)) - vec2(-0.2, -0.489), vec2(0.00001), vec2(0.490));

    tmpSpace = _st - vec2(0.0, -0.025);
    if(tmpSpace.y < 0.45)
    {
        float yVal 	= cos( (_st.y * 0.775) - 3.15 );     
    	float speed = ( iTime / (4.97 - yVal)) ;
        tmpSpace.y 	= mod( speed * 0.94 + tmpSpace.y, 0.09);
    }
    else tmpSpace.y = 1.0;
    vaporGrid *=
        1.0 - rect( tmpSpace * vec2(0.35, 1.0) + vec2(0.2, 0.4925), vec2(0.00001), vec2(0.4985));

    tmpSpace = 1.0 * _st;
    tmpSpace -= vec2(0.85, -0.059);

    for(int i = -6; i <= 6; i++)
    {
        float offsetAdjustMent = ( float(i)  / 8.5);

        vec2 ts         = tmpSpace + vec2(clamp(0.45 - offsetAdjustMent, -90.0, 90.0), 0.0);
        ts              -= 0.5;
        ts              = rotate(ts, float(i) / 15.75);
        ts              += 0.5;
        float rectDF    = rect( ts + vec2(0.0, 0.5), vec2(0.4975), vec2(0.000001));
        vaporGrid       = vaporGrid - (vaporGrid * rectDF);
    }

    return vaporGrid;
}

// by Inigo Quilez @iq 
// more info can be found here: http://www.iquilezles.org/www/articles/distfunctions2d/distfunctions2d.htm
float sdBox( in vec2 p, in vec2 b )
{
    vec2 d = abs(p) - b;
    return length(max(d, vec2(0))) + min( max(d.x, d.y), 0.0 );
}

float skyline(in vec2 _st, in float _inner_stretch, in float _outer_stretch, in float _shadow_factor)
{
    float rnd   	= random(floor(_st.xx * 9.0 + vec2(_shadow_factor, _shadow_factor) ));
    vec2 ra     	= vec2(2.0, _inner_stretch + rnd);

    return sign( sdBox(_st, ra + vec2(0.0, _shadow_factor / 5.0)) );
}

vec3 calculateFinalColor(sceneObject _scene, vec2 _st)
{
    vec3 color         = vec3(0.0);
    vec3 gradient      = vec3(0.0);
    float tmpDF        = 0.0;

    //************************** sky ******************     
    if(_st.y >= 0.43) 	{ color 	= mix(vec3(0.0, 0.0, 0.7), vec3(0.0, 0.0, 0.0), _st.y - 0.10); }
    else 				{ color 	= mix( vec3(0.0, 0.0, 0.0), vec3(0.0, 0.0, 0.7), _st.y + 0.15); }

	//************************** back-layer ******************
    tmpDF			   = _scene.shadowBackLayer;
    color			   = mix(vec3(0.153, 0.153, 1.0), color, tmpDF);
    gradient = vec3(0.0);

    if(_st.y < 0.43)
    {
        tmpDF		   = abs( 0.025 - ( _st.y *  1.5) );
        gradient       = mix(gradient, vec3(0.0, 0.0, 0.7), tmpDF);
    }
    color              = mix(gradient, color, _scene.buildingBackLayer);

	tmpDF			   =  1.0 - ( _scene.windowsBackLayer *  (1.0 - _scene.buildingBackLayer) );    
	color              = mix(vec3(0.0, 0.0, 0.0), color, tmpDF);
	color              = mix(vec3(0.475, 0.325, 0.141), color, tmpDF);

    tmpDF              = 1.0 - (_scene.wReflectBackLayer - (_scene.buildingBackLayer * _scene.wReflectBackLayer));
	color              = mix(vec3(0.0, 0.0, 0.0), color, tmpDF);		
	color              = mix(vec3(0.0, 0.0, 0.4), color, tmpDF); 

    //************************** front-layer ******************   
    color			   = mix(vec3(0.153, 0.153, 1.0), color, _scene.shadowFrontLayer);    
    gradient		   = vec3(0.0);


    if(_st.y >= 0.43)
    {
        tmpDF		   =  5.875 - ( _st.y *  2.325) - 0.6;
        gradient       = mix(vec3(0.251, 0.356, 0.054), vec3(0.0), tmpDF);
    }
    else
    {
        tmpDF		   = 0.5 - ( _st.y *  1.5) - 0.6;
        gradient       = mix(vec3(0.0, 0.0, 0.0), vec3(0.0, 0.0, 0.7), tmpDF);
    }
    color              = mix(gradient, color, _scene.buildingFrontLayer);

    tmpDF			   =  1.0 - ( _scene.windowsFrontLayer *  (1.0 - _scene.buildingFrontLayer)  );    
	color              = mix(vec3(0.0, 0.0, 0.0), color, tmpDF);
    color              = mix(vec3(0.424, 0.565, 0.137), color, tmpDF);

    tmpDF              = 1.0 - (_scene.wReflectFrontLayer - (_scene.buildingFrontLayer * _scene.wReflectFrontLayer));
	color              = mix(vec3(0.0, 0.0, 0.0), color, tmpDF);
    color              = mix(vec3(0.0, 0.0, 0.9), color, tmpDF);

    //************************** grid ******************   
    vec2 tmpCoord      = (gl_FragCoord.xy / iResolution.xy).y + vec2(0.0, -0.275);
    gradient           = mix(vec3(0.0, 0.0, 0.75), vec3(0.0, 0.8, 0.7), tmpCoord.y * 7.0);
    color              = mix(gradient, color, _scene.vaporGrid);

    return color;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    sceneObject scene;
    vec3 color  = vec3(0.0, 0.0, 0.306);

    vec2 st   = (fragCoord.xy / iResolution.xy);
    st.x      *= iResolution.x / iResolution.y;


    vec2 buildingLocalSpace     = st * vec2(2.0, 2.35) - vec2(1.770 + sin(iTime / 4.0) / 6.0, 1.0);	
    scene.buildingBackLayer		= skyline(buildingLocalSpace, 0.175, 0.8, 0.0);
    scene.shadowBackLayer    	= skyline(buildingLocalSpace, 0.176, 0.775, 0.075);

    buildingLocalSpace     		= st * vec2(1.0, 2.82) - vec2(1.77 + sin(iTime / 4.0) / 5.85, 1.0);
    scene.buildingFrontLayer    = skyline(buildingLocalSpace, 0.453, 0.875, 0.0);  
    scene.shadowFrontLayer    	= skyline(buildingLocalSpace, 0.453, 0.875, 0.05);

    scene.vaporGrid             = vaporGrid(st - vec2(sin(iTime / 4.0) / 5.85, 0.0) );

    vec2 defragSpace            = (2.0 * fragCoord.xy - iResolution.xy) / min(iResolution.y, iResolution.x);
    scene.windowsBackLayer      = DeFrag(defragSpace - vec2(-0.5 + sin(iTime / 4.0) / 6.0, 0.0));
    scene.wReflectBackLayer     = DeFrag(defragSpace * vec2(1.0, -1.0) - vec2(-0.5 + sin(iTime / 4.0) / 6.0, 0.27));

    scene.windowsFrontLayer     = DeFrag(defragSpace - vec2(-1.77 + sin(iTime / 4.0) / 3.0, 0.0));
    scene.wReflectFrontLayer    = DeFrag(defragSpace * vec2(1.0, -1.0) - vec2(-1.77 + sin(iTime / 4.0) / 3.0, 0.27));

    fragColor = vec4( calculateFinalColor(scene, st), 1.0 );
}

