float csg_nand (float a, float b) {
    return -max(a,b);
}

// join two NANDs together
float csg_not (float x) {
    return csg_nand(x, x);
}

// NOT(NAND(A,B))
float csg_and (float a, float b) {
    float o = csg_nand(a, b);
    return csg_nand(o, o);
}

// aka A AND NOT B, NOT(NAND(A,NOT(B)))
float csg_sub (float a, float b) {
    float o = csg_nand(a, csg_nand(b, b));
    return csg_nand(o, o);
}

// NAND(NOT(A),NOT(B))
float csg_or (float a, float b) {
    return csg_nand(
        csg_nand(a,a),
        csg_nand(b,b));
}

// NAND(NAND(A,NAND(A,B)),NAND(B,NAND(A,B)))
float csg_xor (float a, float b) {
    float o = csg_nand(a, b);
    return csg_nand(
        csg_nand(a, o),
        csg_nand(b, o));
}

