  vec3 up = normalize(vec3(0, 1, 0));//eye[1].xyz;
  vec3 forward = normalize(-eyep);//normalize(vec3(sin(iMouse.x/250.), 0, cos(iMouse.x/250.)));//eye[2].xyz;
