vec2 perspective_project( in vec2 _st )
{
    float pct = 0.0;
	_st += vec2(-1.0, 0.0);						// perspective
    pct  = smoothstep( -2.5, 1.5, -_st.y );	
    _st /= pct; 

    return _st;
}

float rect( in vec2 _st, in vec2 w, in vec2 h )
{
    vec2 bl = step( vec2(w.x, h.x),_st);       // bottom-left
	vec2 tr = step( vec2(w.y, h.y),1.0 - _st);   // top-right
	return bl.x * bl.y * tr.x * tr.y;
}

float circle( in vec2 _st, in float radius, in float inner, in float outer )
{
