float noise (in vec2 st) {
    vec2 i = floor(st);
    vec2 f = fract(st);
    float a = random(i);
    float b = random(i + vec2(1.0, 0.0));
    float c = random(i + vec2(0.0, 1.0));
    float d = random(i + vec2(1.0, 1.0));
    vec2 u = f*f*(3.0-2.0*f);
    return mix(a, b, u.x) +
            (c - a)* u.y * (1.0 - u.x) +
            (d - b) * u.x * u.y;
}

vec2 rotate( in vec2 _st, in float angle ) {
	return mat2( cos(angle * PI), -sin(angle * PI),
                 sin(angle * PI), cos(angle * PI) ) * _st;    
}

mat2 rotate2d(float angle) {
    return mat2(cos(angle),-sin(angle),
                sin(angle),cos(angle));
}

// from "The Book of Shaders"
//  Chapter 9: Patterns
//  https://thebookofshaders.com/09/
vec2 brickTile(vec2 _st, float _zoom) {
    _st *= _zoom;
    _st.x += step(1., mod(_st.y,2.0)) * 0.5;
    return fract(_st);
}

// based off of lines() from "The Book of Shaders"
//  https://thebookofshaders.com/edit.php#11/wood.frag
float grafitti(in vec2 pos, float b) {
    float scale = 10.0;
    pos *= scale;    
    return smoothstep(0.0, 1.25 + b * 0.5, abs((sin(pos.x*3.1415)+b*2.2)) * 0.25);
}

// https://thebookofshaders.com/06/
// Also...
//  Function from Iigo Quiles
//  https://www.shadertoy.com/view/MsS3Wc
vec3 hsb2rgb( in vec3 c ) { 
    vec3 rgb = clamp(abs(mod(c.x*6.0+vec3(0.0,4.0,2.0), 6.0)-3.0)-1.0, 0.0, 1.0);
    rgb = rgb*rgb*(3.0-2.0*rgb);
    return c.z * mix(vec3(1.0), rgb, c.y);
}

float decay(in vec2 st, in float coef_x, in float coef_y)
{
    vec2 scalar = vec2(2.5 + noise(st * coef_x), 2.5 + noise(st * coef_y));
    vec2 shift = vec2(2.6 + noise(st), -6.95 + noise(st) );
    return rect( (rotate(st, 0.33) * scalar - shift), vec2(0.01), vec2(0.05) );
}

float hopscotch(in vec2 st)
{
    float pct = 0.0, res = 0.0;
    for(int i = 0; i < 6; i++)
    {
        if(res == 0.6)
        {
            pct += rect( st - vec2(res, 0.1), vec2(0.4), vec2(0.4) );
            pct -= rect( st - vec2(res, 0.1), vec2(0.415), vec2(0.415) );
            pct += rect( st - vec2(res, -0.1), vec2(0.4), vec2(0.4) );
            pct -= rect( st - vec2(res, -0.1), vec2(0.415), vec2(0.415) );    
        }
        else
        {
            pct += rect( st - vec2(res, 0.0), vec2(0.4), vec2(0.4) );
            pct -= rect( st - vec2(res, 0.0), vec2(0.415), vec2(0.415) );
        }

        res += 0.2;
    }

    return pct;
}

float seams(in vec2 st)
{
    float pct = 0.0, res = 0.0;
    for(int i = 0; i < 5; i++)
    {
        pct += rect( (st * vec2(85.0, 7.75)) + vec2(14.5 - res, -0.6), vec2(0.175), vec2(0.05) );
        res += 60.0 + clamp(float(i), 0.0, 1.0);
    }

    st = perspective_project(st);

    res = 0.0;
    for(int i = 0; i < 6; i++)
    {
        pct += rect( (st * vec2(60.0, 2.0)) + vec2(185.0 - res, -0.6), vec2(0.175), vec2(0.05) );
        res += 70.0;
    }
    res = 0.0;
    for(int i = 0; i < 5; i++)
    {
        pct += rect( (st * vec2(60.0, 1.0)) + vec2(140.0 - res, 1.35), vec2(0.175), vec2(0.05) );
        res += 70.0;
    }

    return pct;
}

float lamp_shadow(in vec2 _st)
{
    float res = 0.0, pct = 0.0;
    res = step(0.5, mod(iTime, 2.0) * mod(iTime, 3.5));
    pct = rect( (_st * vec2(3.2, 0.35)) - vec2(4.65, 0.0945), vec2(0.2), vec2(0.05) ); 

    //tmp2_2 = tmp2;									// perspective (for shadow bits)
    _st = perspective_project(_st);

    pct += rect( (_st * vec2(2.0, 7.0)) + vec2(-1.55, -3.6), vec2(0.2), vec2(0.05));
    pct += rect( (_st * vec2(1.2, 7.0)) - vec2(0.7, 2.70), vec2(0.2), vec2(0.05));
    pct += rect( (_st * vec2(3.0, 4.35)) - vec2(2.70, 2.5), vec2(0.2), vec2(0.05));

    return pct * res;
}

float lamp(in vec2 _st)
{
    float pct = 0.0;
    pct = rect( (_st * vec2(1.75, 12.0)) + vec2(-2.35, -2.7), vec2(0.2), vec2(0.05) );
    pct += rect( (_st * vec2(2.5, 12.0)) + vec2(-3.58, -3.5), vec2(0.2), vec2(0.05) );
    pct += rect( (_st * vec2(4.1, 0.35)) + vec2(-6.2, -0.078), vec2(0.2), vec2(0.05) );
    return pct;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = fragCoord/iResolution.xy;

    st  	-= 0.5;
    st.x 	*= iResolution.x / iResolution.y;
    st 	+= 0.5;

    float pct = 0.0, res = 0.0;
    vec2 tmp2;
    vec3 col = vec3(0.0, 0.059, 0.286), tmp3;	

    tmp2 = st * 2.0 - 1.0;
    tmp2 += vec2(1.0, 0.5);

    // brick wall + inlay
    vec2 tmp2_2 = tmp2;
    pct = rect( (tmp2_2 * vec2(0.25, 0.5)) + vec2(0.25, 0.0), vec2(0.01), vec2(0.05) );
    col = mix( col, vec3(0.765, 0.898, 0.831), pct );
    tmp2_2 /= vec2(2.15,0.65) / 1.5;
    tmp2_2 = brickTile(tmp2_2 - vec2(0.0, -0.08), 5.0);
    pct *= rect(tmp2_2, vec2(0.0125), vec2(0.0375));
    col = mix( col, vec3(0.427, 0.0, 0.0), pct );

    // grafitti 1  
    tmp2_2 = tmp2;
    tmp2_2 += tk_baha(tmp2, 4.1);		// wind animation (for grafitti)
    tmp2_2 -= tk_baha(tmp2_2, 4.1) / 2.0; 
    tmp3 = hsb2rgb(vec3(tmp2_2.x * 0.2 - 0.29, 1.0, tmp2_2.y)); 

    tmp2_2 = vec2(0.85) - tmp2_2;
    float r = length(tmp2_2)*2.0;
    float a = atan(tmp2_2.y,tmp2_2.x);
    float f = cos(a * 3.0);

    pct = circle(tmp2 - vec2(1.35, 0.5), noise(tmp2_2.yx * r - tmp2_2.y + a * a - f), 1.0, 2.00); 
    col = mix(col, tmp3, pct);

    // road
	float rd = rect( (tmp2 * vec2(0.25, 2.20)) + vec2(0.25, 0.76), vec2(0.01), vec2(0.05) );
    col = mix( col, vec3(0.039, 0.039, 0.039), rd );

    // sidewalk
    pct = rect( (tmp2 * vec2(0.25, 2.80)) + vec2(0.25, -0.20), vec2(0.01), vec2(0.05) );
    pct += rect( (tmp2 * vec2(0.25, 2.80)) + vec2(0.25, 1.85), vec2(0.01), vec2(0.05) );
    col = mix( col, vec3(0.345), pct );
    pct = rect( (tmp2 * vec2(0.25, 8.0)) + vec2(0.25, -0.65), vec2(0.01), vec2(0.05) );
    col = mix( col, vec3(0.184), pct );

    // street lamp + shadow    
    col = mix( col, vec3(0.0), lamp_shadow(tmp2));    
    col = mix( col, vec3(0.075, 0.063, 0.0), lamp(tmp2) );   

    // lamp glow
    res = step(0.5, mod(iTime, 2.0) * mod(iTime, 3.5));
    pct	= circle(tmp2 - vec2(1.25, 1.2), res, -2.65, 2.00);
    col	= mix(col, vec3(3.5), pct);

    tmp2_2 = tmp2;


    tmp2 = perspective_project(tmp2); 


    // road-stripes	
    pct = 0.0;
    res = 0.0;
    pct += rect( (tmp2 * vec2(0.125, 14.0)) + vec2(0.5 - res, 2.2), vec2(0.01), vec2(0.05) );
    pct += rect( (tmp2 * vec2(0.125, 14.0)) + vec2(0.5 - res, 3.4), vec2(0.01), vec2(0.05) );
    col = mix( col, vec3(0.592, 0.765, 0.0), pct );

    // sidewalk seams    
    col = mix( col, vec3(0.0), seams(tmp2_2) );
    col = mix( col, vec3(0.0), 0.8 );


    // starfield
    pct = rd;
    pct = decay(tmp2_2, 48.0, 32.0);
    res = decay(tmp2_2 * vec2(1.0, 1.0) - vec2(0.15, 0.25), 10.0, 10.0);
    pct += res - pct * res;
    res = decay(tmp2_2 * vec2(1.0, 1.0) - vec2(0.15, 0.25), 10.0, 10.0);
    pct += res - pct * res;
    res = decay(tmp2_2 * vec2(0.7, 0.7) - vec2(-0.4, -0.25), 10.0, 10.0);
    pct += res - pct * res;
    res = decay(tmp2_2 * vec2(0.35, 0.4) - vec2(-1.45, -0.10), 28.0, 94.0);
    pct += res - pct * res;
    res = decay(tmp2_2 * vec2(0.35, 0.4) - vec2(-1.35, 0.1), 28.0, 94.0);
    pct += res - pct * res;
    res = decay(tmp2_2 * vec2(0.56, 0.56) - vec2(-0.85, 0.25), 28.0, 94.0);
    pct += res - pct * res;
    res = decay(tmp2_2 * vec2(0.75, 0.75) - vec2(-0.4, 0.35), 28.0, 94.0);
    pct += res - pct * res;
    res = decay(tmp2_2 * vec2(0.75, 0.75) - vec2(-0.50, 0.0), 10.0, 10.0);
    pct += res - pct * res;
    pct *= rd;
    col = mix( col, vec3(0.0, 0.0, 0.421), pct);

    tmp2_2.x = random(tmp2 * 3512.0);
    tmp2_2.y = random(tmp2 * 8461.0);
    tmp2_2.x = pow(tmp2_2.x, 21.0);
    tmp2_2.y = pow(tmp2_2.y, 16.0);

    pct *= tmp2_2.x * tmp2_2.y * sin((tmp2.x - 21.0 - (iTime / 3.85) + cos(tmp2.y * 14.0 + 31.0)) * 13.0);
    col += pct;



    tmp2 += tk_baha(tmp2, 4.78);		// wind animation
    tmp2 -= tk_baha(tmp2, 2.78) / 1.5;   	

    // hop scotch
    tmp2 = (tmp2 * vec2(0.5, 1.1)) - vec2(-1.8, 0.10);    
    col = mix(col, vec3(0.2), hopscotch(tmp2));

    // wind
    tmp2_2 = vec2(st * 1.5 + vec2(3.0 - iTime * 2.5, 4.0));
    pct = noise(tmp2_2);
    col += vec3(pct) / 3.0;

    fragColor = vec4(col,1.0);
}
