#define PI 3.141592653589793

float rand(vec2 id)
{
    return sin(dot(id, vec2(12.9898, 78.233)) *43758.5453); 
}

float distanceFunction(vec3 pos, float size){
    return length(pos) - size;
}

float distanceFunction2( vec3 p, vec2 h )
{
    vec3 q = abs(p);
    return max(q.z-h.y,max(q.x*0.866025+p.y*0.5,-p.y)-h.x*0.5);
}

float distanceFunction3(vec3 pos, vec3 b, float r){

    vec3 q = abs(pos) - b;
    return length(max(q, 0.0) + min(max(q.x, max(q.y, q.z)), 0.0)) - r;
}

float df(vec3 p){

    float df1 = distanceFunction(p, 1.7);
    p.x -= 5.5;
    p.y += 0.2;
    float df2 = distanceFunction2(p, vec2(2.0, 2.0));
    p.x -= 5.5;
    p.y -= 0.2;
    float df3 = distanceFunction3(p, vec3(1.0, 1.0, 1.0), 0.5);
    float m = min(df1, df2);
    m = min(m ,df3);
    return m;
}



vec3 normal(vec3 p, float size){
    vec2 e = vec2(0.0001, 0);
    float d  = df(p);
    vec3 n = d- vec3(
        df(p - e.xyy), 
        df(p - e.yxy),
    	df(p - e.yyx));
    return normalize(n);
}


