#define MAX_STEPS 100
#define MAX_DIST 100.
#define SURFACE_DIST .001


//Sphere function
float sdSphere(in vec3 p, in vec3 c, in float r) {
    return length(c - p) - r;
}

//Box function
float sdBox( vec3 p, vec3 b )
{
  vec3 d = abs(p) - b;
  return length(max(d,0.0))
         + min(max(d.x,max(d.y,d.z)),0.0); // remove this line for an only partially signed sdf 
}


float GetDist(vec3 p){
    vec4 sphere = vec4(0,1,6,1);
    vec4 sphere2 = vec4(0,0.5,3.0,0.25);

    //float dSphere = length(p-sphere.xyz)-sphere.w;


    float dSphere, dSphere2;
    dSphere=sdSphere( sphere.xyz, p ,sphere.w );
    dSphere2=sdSphere( sphere2.xyz, p ,sphere2.w );

    float dPlane = p.y;
    float d;
     d= min(dSphere, dPlane);
     d= min(d, dSphere2);



    float bx1, bx2, bx3, bx4, bx5, bx6;
    float bxl1, bxl2, bxl3, bxl4, bxl5, bxl6;

    //bx1=sdBox( p-vec3(0.5,0.1,0), vec3(0.8,0.5,0.5) );



    bx1 = sdBox(p- vec3(0, 0, 16), vec3(5.5, 5.8, 0.5));
    bx2 = sdBox(p- vec3(1.5, 0, 6), vec3(0.5, 1.8, 0.5));
    bx3 = sdBox(p- vec3(3.5, 0, 6), vec3(0.15, 1.0, 1.5));
    bx4 = sdBox(p- vec3(3.7, 1.5, 6), vec3(0.15, 0.5, 1.5));
    bx5 = sdBox(p- vec3(3.9, 2.5, 6), vec3(0.15, 0.5, 1.5));
    bx6 = sdBox(p- vec3(4.1, 3.5, 6), vec3(0.15, 0.5, 1.5));


    bxl1 = sdBox(p- vec3(-0.1, 0,10), vec3(4.5, 2.8, 0.5));
    bxl2 = sdBox(p- vec3(-1.5, 0, 6), vec3(0.5, 1.8, 0.5));
    bxl3 = sdBox(p- vec3(-3.5, 0, 6), vec3(0.15, 1.0, 1.5));
    bxl4 = sdBox(p- vec3(-3.7, 1.5, 6), vec3(0.15, 0.5, 1.5));
    bxl5 = sdBox(p- vec3(-3.9, 2.5, 6), vec3(0.15, 0.5, 1.5));
    bxl6 = sdBox(p- vec3(-4.1, 3.5, 6), vec3(0.15, 0.5, 1.5));


    d= min(d, bx1); 	
  	d= min(d, bx2);
  	d= min(d, bx3);
  	d= min(d, bx4);
  	d= min(d, bx5);
  	d= min(d, bx6);

    d= min(d, bxl1); 	
  	d= min(d, bxl2);
  	d= min(d, bxl3);
  	d= min(d, bxl4);
  	d= min(d, bxl5);
  	d= min(d, bxl6);


    return d;
}

float RayMarch(vec3 ro, vec3 rd){
    float dO = 0.;
    for(int i=0; i<MAX_STEPS; i++) {
    	vec3 p = ro + dO*rd;
        float dS = GetDist(p);
        dO += dS;
        if(dS<SURFACE_DIST || dO>MAX_DIST) break;
    }
    return dO;
}

vec3 GetNormal(vec3 p){
    vec2 e = vec2(.01, 0);
    float d = GetDist(p);
    vec3 n = vec3(
    	d-GetDist(p-e.xyy),
        d-GetDist(p-e.yxy),
        d-GetDist(p-e.yyx)
    );

    return normalize(n);
}

float GetLight(vec3 p){
    vec3 lightPos = vec3(0, 5, 6);
    lightPos.xz += vec2(sin(iTime), cos(iTime))*2.;
    vec3 l = normalize(lightPos-p);
    vec3 n = GetNormal(p);

    float dif = clamp(dot(n, l), 0., 1.);
    float d = RayMarch(p+n*SURFACE_DIST*2., l);
    if (d<length(lightPos-p)) dif *= .1;
    return dif;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    vec3 col = vec3(0);

    vec3 ro = vec3(0,3.5+2.5*sin(iTime),0);
    vec3 rd = normalize(vec3(uv.x, uv.y, 1));

    float d = RayMarch(ro, rd);

    vec3 p = ro + rd*d;

    float dif = GetLight(p);
    col = vec3(dif);

    fragColor = vec4(col,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
