    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(7.8*((fract(iTime)*0.002)+0.34)*(uv.y+0.74)*sin(3.9*(uv.x+0.7)*cos(iTime+uv.xyx+vec3(0,2,4))));

    // Output to screen
    fragColor = vec4(col,0.1);
}

#define PI (acos(-1.))

struct mapdata {
    int id;
    float dist;
};

struct hitpoint {
    bool hit;
    mapdata data;
	vec3 pos;
    vec3 normal;
};

float sdSphere(vec3 p, float r) {
    return length(p) - r;
}

float sdCapsule( vec3 p, vec3 a, vec3 b, float r )
{
    vec3 pa = p - a, ba = b - a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h ) - r;
}

mapdata mapMin(mapdata d1, mapdata d2) {
    mapdata result = d1;
    if (d2.dist < d1.dist) {
        result.id = d2.id;
        result.dist = d2.dist;
    }
    return result;
}

mapdata map(vec3 p) {
    mapdata result;

    vec3 pp1 = p;
    vec3 pp2 = p;

    float r = 0.2;
    float t = PI + 0.5 * iTime;
    float twist = -0.67 * p.y;
    pp1.x += r * cos(t + twist);
    pp1.z += r * sin(t + twist);
    pp2.x += r * cos(t + PI + twist);
    pp2.z += r * sin(t + PI + twist);

    float rep = 0.33;
    pp1.y = mod(p.y, rep)-0.5*rep;
    pp2.y = pp1.y;

    // Spheres
    float spr = 0.09;
    float spd1 = sdSphere(pp1, spr);
    float spd2 = sdSphere(pp2, spr);
    result = mapMin(mapdata(1, spd1), mapdata(2, spd2));

    // Connecting line
   	float cad = sdCapsule(pp1, vec3(0, 0, 0), pp1 - pp2, 0.02);
    result = mapMin(result, mapdata(3, cad));

    return result;
}

vec3 lookAt(vec2 uv, vec3 origin, vec3 target, float fov) {
	vec3 zz = normalize(target - origin);
    vec3 xx = normalize(cross(zz, vec3(0,1,0)));
    vec3 yy = normalize(cross(xx, zz));
    return normalize(uv.x * xx + uv.y * yy + fov * zz); 
}

vec3 normal(vec3 pos) {
    vec2 e = vec2(0.0005, 0.);
    return normalize(
        vec3(
            map(pos + e.xyy).dist - map(pos - e.xyy).dist,
            map(pos + e.yxy).dist - map(pos - e.yxy).dist,
            map(pos + e.yyx).dist - map(pos - e.yyx).dist
        )
    );
}

float diffuse(vec3 nor, vec3 lightDir) {
    return clamp( dot(nor, lightDir), 0.0, 1.0);
}

hitpoint raycast(vec3 ro, vec3 rd) {
    hitpoint h;

    // Raymarching
    float t = 0.0;
    mapdata m;
    for(int i=0; i<100; i++) {
        mapdata m = map(ro + t * rd);
        float d = m.dist; 
        if (abs(d) < 0.001) {
            h.hit = true;
            h.data = m;
            h.pos = ro + t * rd;
            h.normal = normal(h.pos);
            break;
        }
        t += d;
        if (t > 20.) {
            break;
        }
    }
    return h; 
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy)/iResolution.y;
    vec3 col = vec3(0.1, 0.1, .6 * (uv.y + 0.5));

    // Camera
    float ct = -0.5 * iTime;
    vec3 ro = vec3(0, ct, 1.);
    vec3 rd = lookAt(uv, ro, vec3(0, -2. + ct, 0), 1.);

    // Render
    hitpoint h = raycast(ro, rd);
    if (h.hit) {
    	float c = 0.0;
    	c += (2. * uv.y + 0.8) * diffuse(h.normal, vec3(1, 1, 2));
        float shadow = 0.8 * diffuse(h.normal, vec3(-1, 0, 0));
    	c += pow(shadow, 3.);

        vec3 mat = vec3(0);
        if (h.data.id == 1) {
            mat = vec3(0.9, 0.1, 0.1);
        } else if (h.data.id == 2) {
            mat = vec3(0.1, 0.1, 0.9);
        } else if (h.data.id == 3) {
            mat = vec3(0.3, 0.6, 0.3);
        }

	    col = mat * c;
    }


    fragColor = vec4(col,1.0);
}

bool inSquare(float x, float y, float w, float h,
              float px, float py) {
 	return px > x-w/2.0 && px < x+w/2.0
        && py > y-h/2.0 && py < y+h/2.0;
}

bool inSquareOutline(float x, float y, float w, float h,
                     float px, float py) {
    return inSquare(x, y, w, h, px, py)
        && !inSquare(x, y, w-0.012, h-0.012, px, py);
}

bool inArc(float x, float y, float r,
           float angle, float timeScalar, float timeOffset,
           float size,
           float px, float py) {
    float dist = distance(vec2(x, y), vec2(px, py));

    bool inAngle = dot(vec2(px-x, py-y),
        vec2(cos(angle), sin(angle))) < size;
    float angleShift = iTime*timeScalar + timeOffset;
    bool inAngleShifting = dot(vec2(px-x, py-y),
        vec2(cos(angleShift), sin(angleShift))) < size;

    return dist < r && dist > r-0.005 && inAngle && inAngleShifting;
}

bool inCircle(float x, float y, float r,
              float px, float py) {
    float dist = distance(vec2(x, y), vec2(px, py));
    return dist < r && dist > r-0.005;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Temp scale it down so it's a square in ShaderToy
    uv.x *= 800.0/450.0;

    // Mirror x and y
    uv.x = abs(uv.x-0.5)+0.5;
    uv.y = abs(uv.y-0.5)+0.5;

    vec4 outColor = vec4(0, 0, 0, 0);

    // White glow from center
    //float r = distance(uv, vec2(0.5, 0.5));
    //float alpha = 1.0-r*2.0;
    //outColor = vec4(alpha, alpha, alpha, 1);

    vec4 squareColor = vec4(0.9, 0.4, 0.7, 1.0);

    // Static square
    outColor += float(inSquareOutline(0.5, 0.5, 0.25, 0.25, uv.x, uv.y))
        	* (-outColor + squareColor*0.3);

    // Moving squares
    float squareT = mod(iTime*0.25, 1.0);
    float w0 = mix(0.05, 0.2, squareT);
    float a0 = mix(0.0, 1.0, squareT);
    float w1 = mix(0.2, 0.4, squareT);
    float a1 = 1.0;
    float w2 = mix(0.4, 0.65, squareT);
    float a2 = mix(1.0, 0.0, squareT);
    outColor += float(inSquareOutline(0.5, 0.5, w0, w0, uv.x, uv.y))
        	* (-outColor + squareColor*a0);
    outColor += float(inSquareOutline(0.5, 0.5, w1, w1, uv.x, uv.y))
        	* (-outColor + squareColor*a1);
    outColor += float(inSquareOutline(0.5, 0.5, w2, w2, uv.x, uv.y))
        	* (-outColor + squareColor*a2);

    // Center square
    outColor += float(inSquareOutline(0.5, 0.5, 0.05, 0.05, uv.x, uv.y))
        	* (-outColor + squareColor);

    // Inner arcs
    float inX = 0.013;
    float inY = 0.12;
    float inR = 0.1;
    float inA = 0.2;
    float inAS = 3.0;
    float inAO = 3.0;
    float inSize = -0.03;
    vec4 inArcColor = vec4(1.0, 1.0, 0.7, 1.0);

    outColor += float(inArc(0.5-inX, 0.5+inY, inR,
                            inA, -inAS, 0.0, inSize,
                            uv.x, uv.y))
        	* (-outColor + inArcColor);

    outColor += float(inArc(0.5-inX, 0.5-inY, inR,
                            -inA, inAS, 0.0, inSize,
                            uv.x, uv.y))
        	* (-outColor + inArcColor);

    outColor += float(inArc(0.5+inX, 0.5+inY, inR,
                            3.14-inA, inAS, inAO, inSize,
                            uv.x, uv.y))
        	* (-outColor + inArcColor);

    outColor += float(inArc(0.5+inX, 0.5-inY, inR,
                            3.14+inA, -inAS, inAO, inSize,
                            uv.x, uv.y))
        	* (-outColor + inArcColor);

    // Outer arcs
    inX = 0.22;
    inY = 0.15;
    inR = 0.2;
    inA = 4.3;
    inAS = -3.0;
    inAO = 3.0;
    inSize = -0.05;
    inArcColor = vec4(0.7, 0.8, 1.0, 1.0);

    outColor += float(inArc(0.5-inX, 0.5+inY, inR,
                            inA, -inAS, 0.0, inSize,
                            uv.x, uv.y))
        	* (-outColor + inArcColor);

    outColor += float(inArc(0.5-inX, 0.5-inY, inR,
                            -inA, inAS, 0.0, inSize,
                            uv.x, uv.y))
        	* (-outColor + inArcColor);

    outColor += float(inArc(0.5+inX, 0.5+inY, inR,
                            3.14-inA, inAS, inAO, inSize,
                            uv.x, uv.y))
        	* (-outColor + inArcColor);

    outColor += float(inArc(0.5+inX, 0.5-inY, inR,
                            3.14+inA, -inAS, inAO, inSize,
                            uv.x, uv.y))
        	* (-outColor + inArcColor);

    // Static circles
    //outColor += float(inCircle(0.7, 0.5, 0.012, uv.x, uv.y))
    //    	* (-outColor + vec4(0.5, 1.0, 0.7, 1.0));

    // Final output assignment
    fragColor = outColor;
}

