  	d= min(d, bxl5);
  	d= min(d, bxl6);


    return d;
}

float RayMarch(vec3 ro, vec3 rd){
    float dO = 0.;
    for(int i=0; i<MAX_STEPS; i++) {
    	vec3 p = ro + dO*rd;
        float dS = GetDist(p);
        dO += dS;
        if(dS<SURFACE_DIST || dO>MAX_DIST) break;
    }
    return dO;
}

vec3 GetNormal(vec3 p){
    vec2 e = vec2(.01, 0);
    float d = GetDist(p);
    vec3 n = vec3(
    	d-GetDist(p-e.xyy),
        d-GetDist(p-e.yxy),
        d-GetDist(p-e.yyx)
    );

    return normalize(n);
}

float GetLight(vec3 p){
    vec3 lightPos = vec3(0, 5, 6);
    lightPos.xz += vec2(sin(iTime), cos(iTime))*2.;
    vec3 l = normalize(lightPos-p);
    vec3 n = GetNormal(p);

    float dif = clamp(dot(n, l), 0., 1.);
    float d = RayMarch(p+n*SURFACE_DIST*2., l);
    if (d<length(lightPos-p)) dif *= .1;
    return dif;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    vec3 col = vec3(0);

    vec3 ro = vec3(0,1,0);
    vec3 rd = normalize(vec3(uv.x, uv.y, 1));

    float d = RayMarch(ro, rd);

    vec3 p = ro + rd*d;

    float dif = GetLight(p);
    col = vec3(dif);

    fragColor = vec4(col,1.0);
}

const int MAX_ITER = 128;
const int LIM = MAX_ITER / 2;

const float PARAM = 0.387;
const float COEF = 0.80;

const float ROT = 5.0;

const float PRE = 15.0;
const float DUR = 60.0;

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    float t = iTime - PRE;
    if (t > 0.0) {
        t = mod(t, DUR);
    	if (t >= DUR / 2.0) { t = DUR - t; }
    }
    float zoom = pow(COEF, t + PRE);

    float mxy = min(iResolution.x, iResolution.y);
    vec2 cxy = iResolution.xy / mxy;
    vec2 uv = (fragCoord / mxy - 0.5 * cxy) * 75.0 * zoom;

    // Rotate : x' = x*cos(a) - y*sin(a), y' = x*sin(a) + y*cos(a)

    float sa = sin(iTime/ROT), ca = cos(iTime/ROT);
    uv *= mat2(ca, -sa, sa, ca);

    // Julia : x = x^2 - y^2 + x, y = 2 * x * y + y

    vec2 xy = uv;
    int iter = 0;
    for (int i = 0; i < MAX_ITER; ++i) {
        vec2 xy2 = xy * xy;
        if (xy2.x + xy2.y > 4.0) { break; }
        xy = vec2(xy2.x - xy2.y + (PARAM + zoom), 2.0 * xy.x * xy.y + (PARAM + zoom));
        ++iter;
    }

    if (iter < LIM) {
        fragColor = vec4(0.0, 1.0 - float(iter)/float(LIM), float(iter)/float(LIM), 1.0);
    } else if (iter == MAX_ITER) {
        fragColor = vec4(vec3(0.0), 1.0);
    } else {
        fragColor = vec4(1.0 - float(iter-LIM)/float(LIM), 0.0, 1.0, 1.0);
    }
}

float map( vec2 z, vec2 c )
{
    float i = 0.;

    for( ; i < 150.; ++i ) {
        z = vec2(
            z.r*z.r - z.g*z.g + c.r,
            2.*z.r*z.g + c.g
        );  
        if (length(z) > 40.) break;
    }

    if (i >= 150.) return 0.;

    i = i - log(length(z)) / log(40.);

    return( i / 35. );
}

float sampJulia( vec2 uv, vec2 muv, vec2 offset )
{
    return map( uv + offset / iResolution.xy, muv );
}

float sampMandelbrot( vec2 uv, vec2 offset )
{
    return map( vec2( 0 ), uv + offset / iResolution.xy );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv  = 2.*( fragCoord/iResolution.yy - vec2(.5 * iResolution.x/iResolution.y, .5) ) - vec2(.5, 0);
    vec2 muv;

    if( iMouse.z > 0.0 ) {
        muv = 2.*( iMouse.xy/iResolution.yy - vec2(.5 * iResolution.x/iResolution.y, .5) ) - vec2(.5, 0);
    } else {
        float t = .1*iTime;
        muv = vec2(.9*sin( t ) - .4, .8*cos( t ));
    }

    float lm = length(uv - muv) + .01;
    float c = clamp(.0002 / lm /lm, 0., 1.);

    float ja = sampJulia(uv, muv, vec2(-.75, .25));
    float jb = sampJulia(uv, muv, vec2(-.25,-.75));
    float jc = sampJulia(uv, muv, vec2( .25, .75));
    float jd = sampJulia(uv, muv, vec2( .75,-.25));
    float j = 0.25*(ja + jb + jc + jd);

    float ma = sampMandelbrot(uv, vec2(-.75, .25));
    float mb = sampMandelbrot(uv, vec2(-.25,-.75));
    float mc = sampMandelbrot(uv, vec2( .25, .75));
    float md = sampMandelbrot(uv, vec2( .75,-.25));
    float m = 0.25*(ma + mb + mc + md);

    fragColor = vec4(c, j, m, 1);
}

#define NUMBEADS 10
#define PI2 6.283
vec3 path(float t) {
    // I wanted a spiral of beads that would chase each other around
    // (i.e. by doing something like opTwist) but I couldn't get it
    // looking right, settled for this spiral-y effect where the beads
    // end up intersecting, so I just used soft min to have them glob
    // together without discontinuities which ended up looking interesting
    float k = .2;
    vec3 p = vec3(cos(t) * (1. - abs(sin(t*k))), sin(t*k) * 1.2, sin(t) * (1. - abs(sin(t*k))));
    return p;
}

float sdSphere(vec3 p, float t) {
    return length(p - path(t)) - .1;
}

float sdGround(vec3 p) {
    return p.y + 1.5;
}

//https://www.iquilezles.org/www/articles/smin/smin.htm
float smin( float a, float b, float k )
{
    float h = clamp( 0.5+0.5*(b-a)/k, 0.0, 1.0 );
    return mix( b, a, h ) - k*h*(1.0-h);
}

float map(vec3 p ) {
    // this seems like it could be sped up, NUMBEADS impacts performance more than I expected
    float dist = sdGround(p);
    for(int i = 0; i < NUMBEADS; i++) {
        float slice = float(i)/float(NUMBEADS);
        dist = smin(dist, sdSphere(p, iTime + slice * PI2), .5);
    }
	return dist;
}

vec3 grad(vec3 p) {
    vec2 eps = vec2(.001,0.);
    return normalize(map(p) - vec3(map(p - eps.xyy), map(p - eps.yxy), map(p-eps.yyx)));
}

vec3 march(vec3 ro, vec3 rd) {
    float t = 0.;
    vec3 p;
    for (int i = 0; i < 100; i++) {
        p = ro + rd * t;
        float d = map(p);
        if (d < 0.001 || t > 100.) break;
        t += d;
    }
    return p;
}

// https://www.iquilezles.org/www/articles/rmshadows/rmshadows.htm
float shadow(vec3 ro, vec3 rd, float k) {
    // start the ray a little bit away from
    // the surface to avoid self intersection
    float t = .1;
    float result = 1.;
    for (int i = 0; i < 10; i++) {
        vec3 p = ro + rd * t;
        float d = map(p);
        result = min(result, k * d / t);
        if (result < 0.001) {
            return 0.0;
        }
        t += d;
        if(t>10.){break;}
    }
    return result;
}

vec3 light(vec3 p, vec3 c, vec3 ld) {
    vec3 n = grad(p);
    float incid = dot(n, ld);
    vec3 diffuse = c * incid;
    // this shadow calculation also slows things down a lot (it's doing 
    // the ray march a second time from the initial contact point), 
    // wonder if there's a way to speed that up as well
    diffuse *= shadow(p, ld, 4.);
    return diffuse;
}

// basic y rotation used for moving the lights around
mat3 roty(float a) {
    return mat3(
        cos(a), 0., -sin(a),
        0., 1., 0.,
        sin(a), 0., cos(a));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (2. * fragCoord - iResolution.xy) / iResolution.y;

    vec3 ro = vec3(0., 0., 2.);
    vec3 rd = normalize(vec3(uv, -1.));

    // scene has a red and green light that rotate around the ornament
    vec3 ld2 = normalize(vec3(-.45, .2, .45));
    vec3 ld3 = normalize(vec3(.0, .2, -.45));
    ld2 *= roty(iTime * .21);
    ld3 *= roty(iTime * .21);

    // march and do lighting calcs
    vec3 p = march(ro, rd);
    vec3 col = light(p, vec3(16, 233, 25)/255., ld2);
    col += light(p, vec3(227, 21, 25)/255., ld3);

    // iq's gamma
    col = pow(col, vec3(.4545));

    fragColor = vec4(col, 1);
}

#define STEREO 1

#define PI 3.14159
#define STEPS 100.
#define EPS 0.00001
#define EPSN 0.001
#define EPSOUT 0.0015

mat2 rot(float a){
    float c = cos(a);
    float s = sin(a);
	return mat2(c, -s, s, c);
}

float smoothmin(float a, float b, float k){
	float f = clamp(0.5 + 0.5 * (a - b) / k, 0., 1.);
    return mix(a, b, f) - k * f * (1. - f);
}

vec2 repeat(vec2 pos, float t){
	t = 2. * PI / t;
    float angle = mod(atan(pos.y, pos.x) , t) - 0.5 * t;
    float r = length(pos);
    return r * vec2(cos(angle), sin(angle));
}

float distEllipsoid(vec3 p, vec3 r){
    float lg = length(p /(r * r));
	return (length(p / r) - 1.) * (length(p / r)) / lg;
}

float distRing(vec3 p, vec2 r){
  vec2 t = vec2(length(p.xz)-r.x,p.y);
  return length(t)-r.y;
}

float distBox(vec3 p, vec3 r){
  vec3 t = abs(p) - r;
  return length(max(t,0.0)) + min(max(t.x,max(t.y,t.z)),0.0);
}

float distScene(in vec3 pos, out int object){

    pos.yz = rot(0.2 + 0.25 * (0.5 + 0.5 * sin(0.25 * iTime - 0.5 * PI))) * pos.yz;
    pos.xz = rot(0.25 * iTime) * pos.xz;
    pos.y += 0.1 + 0.0125 * sin(iTime);

    pos.xy = rot(-0.4) * pos.xy;
    pos.xz = rot(0.6) * pos.xz;
    pos.yz = rot(0.4) * pos.yz;

    //gem
    vec3 p = pos;
    p.y -= 0.155;
    float dist = length(p) - 0.05;
    object = 1;

    //gold ?
    p = pos;        
    p.y -= 0.11;
    float distGold = distEllipsoid(p, vec3(0.03, 0.01, 0.03));
    distGold = smoothmin(distGold, max(max(length(p.xz) - 0.0175, p.y), -p.y - 0.25), 0.035);
    distGold = smoothmin(distGold, distEllipsoid(p - vec3(0., -0.25, 0.), vec3(0.01, 0.01, 0.01)), 0.035);
	distGold = min(distGold, distEllipsoid(p, vec3(0.04, 0.01, 0.04)));
    distGold = min(distGold, distEllipsoid(p - vec3(0., -0.05, 0.), vec3(0.0225, 0.01, 0.0225)));
    distGold = min(distGold, distEllipsoid(p - vec3(0., -0.25, 0.), vec3(0.025, 0.01, 0.025)));
    distGold = min(distGold, distEllipsoid(p - vec3(0., -0.175, 0.), vec3(0.0225, 0.01, 0.0225)));

    p.y -= 0.115;
    distGold = smoothmin(distGold, distRing(p.xzy, vec2(0.12, 0.0075)), 0.02);

    p.y -= 0.015;
    p.xy = rot(3. * PI/2.) * p.xy;
    p.xy = repeat(p.xy, 5.);
    distGold = smoothmin(distGold, max(max(length(p.yz), length(p) - 0.15), -(length(p) - 0.08)), 0.03);

    float distStar = distEllipsoid(p, vec3(0.02, 0.02, 0.015));
    p.x -= 0.03;
    distStar = smoothmin(distStar, length(p), 0.03);
	distGold = min(distGold, distStar);

    p = pos;
    p.y -= 0.15;
    distGold = smoothmin(distGold, distRing(p.xzy, vec2(0.055, 0.006)), 0.00015);
    p.y += 0.24;
    p.x -= 0.02;
    distGold = min(distGold, distBox(p, vec3(0.02, 0.0075, 0.005)));
    p.y += 0.029;
    distGold = min(distGold, distBox(p, vec3(0.02, 0.01, 0.005)));
    p.y -= 0.01;
    distGold = min(distGold, distBox(p, vec3(0.01, 0.02, 0.005)));

    dist = min(dist, distGold);

    if(dist == distGold){
        object = 3;
    }

    if(dist == distStar){
        object = 4;
    }

    //wings
    p = pos;
    p.y -= 0.05 + 0.008 * sin(1.5 * iTime);
    p.x = abs(p.x) - 0.05;
    float distWing = length(p) - 0.015;
    vec3 pBranch = p;
    pBranch -= vec3(0.055, 0.04, 0.);
    float rt = 10. * pBranch.x;
    pBranch.xy = rot(rt * rt) * pBranch.xy;
    distWing = smoothmin(distWing, distEllipsoid(pBranch, vec3(0.08, 0.015, 0.02)), 0.01);
    pBranch = p;
    pBranch.xy = rot(-0.25) * pBranch.xy;
    pBranch -= vec3(0.052, 0.035, 0.);
    rt = 8. * pBranch.x;
    pBranch.xy = rot(rt * rt) * pBranch.xy;
	distWing = smoothmin(distWing, distEllipsoid(pBranch, vec3(0.07, 0.015, 0.0175)), 0.005);
    pBranch = p;
    pBranch.xy = rot(-0.5) * pBranch.xy;
    pBranch -= vec3(0.04, 0.025, 0.);
    rt = 8. * pBranch.x;
    pBranch.xy = rot(rt * rt) * pBranch.xy;
	distWing = smoothmin(distWing, distEllipsoid(pBranch, vec3(0.05, 0.0125, 0.015)), 0.0075);

    dist = min(dist, distWing);
    if(dist == distWing){
        object = 2;
    }
    return 0.5 * dist;

}


vec3 getNormal(vec3 p){
    int o;
	return normalize(vec3(distScene(p + vec3(EPSN, 0., 0.), o) - distScene(p - vec3(EPSN, 0., 0.), o),
    					  distScene(p + vec3(0., EPSN, 0.), o) - distScene(p - vec3(0., EPSN, 0.), o),
                          distScene(p + vec3(0., 0., EPSN), o) - distScene(p - vec3(0., 0., EPSN), o)));
}


vec3 render(vec2 uv, float eyeOffset){

    //background
    vec3 bgColor = vec3(0.);
    vec3 inkColor = bgColor;
    vec2 uvbg = 5. * uv - vec2(0.25 * eyeOffset, 0.);
    float time = 0.05 * iTime;
    float angle = atan(uvbg.y, uvbg.x) + 2.0 * sin(PI * time);
    float radius = length(uvbg);      
    float k = 10. / 7.;
    float offset = 0.1 + sin(2.6 * sin(1.9 * sin(PI * time)));

    float res = 10000.0;    
    for(float i = 0.0; i < 7.; i++){
    	angle += 2.0 * PI;
        res = min(res, abs(radius - (1. / (cos(k * angle) + offset))));
    }    
  	res = min(res, abs(radius - offset - 0.8));
    res = min(res, abs(radius - offset - 1.));

    float bg = smoothstep(0., 0.01, res);
    vec3 col = mix(vec3(0.1, 0.1, 0.1), bgColor,  bg);

    //raymarch
    vec3 eye = vec3(eyeOffset, 0., 2.);
    vec3 up = vec3(0., 1., 0.);
    vec3 forward = normalize(-eye);
    vec3 ray = normalize(1.5 * forward + normalize(cross(forward, up)) * uv.x + up * uv.y);
    int o;
    float dist, step, c, prevDist;
    bool hit = false;
    vec3 pos = eye;
    dist = distScene(pos, o);
    float outline = 1.;

    for(step = 0.; step < STEPS; step++){
        prevDist = dist;
    	dist = distScene(pos, o);
        if(dist > prevDist + EPS && dist < EPSOUT ){
        	outline = min(outline, dist);
        }
        if(abs(dist) < EPS){
        	hit = true;
            break;
        }
        if(length(pos) > 3.) break;
    	pos += dist * ray;
    }
    outline /= EPSOUT;

    vec3 normal = getNormal(pos);

    //shading
    if(hit){
    	vec3 light = vec3(10., 5., 12.);
        light.yz = rot(0.5) * light.yz;
        float shine = 30.;

        if(o == 1){
            col = vec3(0.65, 0.4, 0.9);
        }
        if(o == 2){
            col = vec3(0.75, 0.75, 0.75);
        	shine = 5.;
        }
		if(o == 3){
            col = vec3(0.95, 0.85, 0.4);
            shine = 20.;
        }
        if(o == 4){
        	col = vec3(0.75, 0.75, 0.75);
        }

        //diffuse
        vec3 l = normalize(light - pos);
        if(o == 1){
            float diff = dot(-normal, l);
            if(o != 0) col = mix(col, vec3(0., 0., 0.15), 0.45 * (1. - diff));
        }else{
        	float diff = dot(normal, l);
        	diff = smoothstep(0.3, 0.35, diff);
            if(o != 0) col = mix(col, vec3(0., 0., 0.5), 0.3 * (1. - diff));
        }

        //specular
        vec3 refl = reflect(-l, normal);
        float spec = pow(dot(normalize(eye - pos), refl), shine);
        spec = smoothstep(0.5, 0.55, spec);
        col += 0.02 * shine * spec;

        //outline
        outline = smoothstep(0.75, 0.95, outline);
        col = mix(inkColor, col, outline);
    }  
    return col;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.x;
    uv *= 0.8;
#if STEREO
    //using the Dubois anaglyph method for the colors
    vec3 colorLeft = render(uv, -0.05);
    mat3 transfoLeft = mat3(vec3(0.42, -0.05, -0.05),
    						vec3(0.47, -0.05, -0.06),
                           	vec3(0.17, -0.03, 0.01));
    vec3 colorRight = render(uv, 0.05);
    mat3 transfoRight = mat3(vec3(-0.01, 0.38, -0.07),
    						vec3(-0.04, 0.73, -0.13),
                           	vec3(-0.01, 0.01, 1.30));
    fragColor = vec4(pow(transfoLeft * colorLeft + transfoRight * colorRight, vec3(1. / 2.2)),1.0);
#else
    fragColor = vec4(pow(render(uv, 0.), vec3(1./2.2)), 1.);
#endif
}

